@-moz-document domain("worldwidetorrents.me") {
/*
Dark MonoChrome WWT v1.4
by superiorSilicon

Feel free to modify & share as you wish. Just credit me.
*/

/* Home Page */

html, .main { /* Main Background */
    background: #3a3a3a !important;
    overflow-x: hidden;
}

.main > center > table, .myTable {
    background: #3a3a3a !important;
}

.w3-card-2{ /* Round Cards on The Sidebar */
   	background: #4b4b4b !important;
    color: white !important;
}

.w3-card-2 a, .myFrame, .myFrame a { /*Notice, Notice Links & Siderbar Card Links*/
    color: white !important;
}

.w3-card-2 a:hover, .myFrame a:hover { /*Notice Links & Siderbar Card Links Hover Event*/
 	color: gray !important;   
}

.w3-card-2 a:visited, .myFrame a:visited { /*Notice Links & Siderbar Card Visited Links*/
    color: lightgray;
}

.w3-card-2 > p > font { /*Sidebar Reputation Color Fix*/
    color: white !important;
}

#textbox { /*"Friend Sites" Text*/
    font-family: system-ui, Arial, sans-serif;
    color: white !important;
}

.w3-allerta { /*Titles|Headers*/
    font-family: 'Raleway', sans-serif;
}

.w3-navbar { /*Navigation Bar*/
    background: #6a6a6a !important;
    max-height: none !important;
}

a[href="/home.php"] { /*WWT Navbar Title*/
    background: #7a7a7a !important;
    padding: 16px !important;
}

.fa { /*Profile Icons (Sidebar)*/
	color: white !important
}

.w3-allerta.w3-theme-l1 { /*Section Headings (e.g Movies, Music, Shoutbox)*/
    background: #7a7a7a !important;
}

.w3-btn.w3-teal { /*Buttons*/
    background: gray !important;
}

.shoutbox_body { /*Shoutbox*/
    color: white;
    font-family: 'Raleway', sans-serif;
}

.shoutbox_body a, .shoutbox_messageboxback a { /*Shoutbox Links*/
    color: white;
    text-decoration: none;
}

.shoutbox_body a:hover, .shoutbox_body a:focus { /*Shoutbox Links Hover Events*/
    color: gray;
    text-decoration: underline;
}

.shoutbox_body a:visited { /*Shoutbox Visited Links*/
    color: lightgray;
}

article.box { /*API Page*/
    color: white
}

article.box a { /*API Page Links*/
    color: white;
}

article.box a:hover { /*API Page Links*/
    color: gray;
}

font[color="#4c2dd2"] { /*WWT Elite Class Color Fix*/
 	color: #4c7ff2 !important;
}

img.w3-circle { /*Sidebar & Navbar Picture*/
    border-radius: 0 !important;
}

footer.w3-theme-d2 { /* Footer */
    background: #4b4b4b !important;
}

table.w3-table.w3-striped.w3-bordered.w3-card-4, .w3-jose, table.w3-table.w3-striped.w3-bordered.w3-card-4 td { /*Torrents Table*/
    background: #3a3a3a !important;
    color: white;
}

table.w3-table.w3-striped.w3-bordered.w3-card-4 a { /* Torrents Table Links */
    color: white;
}

table.w3-table.w3-striped.w3-bordered.w3-card-4 a:hover { /* Torrents Table Links Hover Event */
    color: gray;
}

a[href*="torrents.php?parent_cat="] { /*"View All" Links*/
    color: white;
}

.w3-dropdown-content.w3-white.w3-card-4.w3-small { /*Navbar Dropdown*/
    background: #6b6b6b !important;
}

.w3-dropdown-content.w3-white.w3-card-4.w3-small a { /*Navbar Dropdown*/
    color: white;
}

.w3-hover-white { /*Navbar Dropdown*/
    padding: 16px !important;
}

.w3-hover-white:hover { /*Navbar Dropdown Hover*/
    background: #7b7b7b !important;
    color: white !important;
}

a[href*="/account-details.php"] { /*Navbar Profile Link*/
    padding: 12px !important;
    margin: 0 !important;
}
    
/* Torrent Upload Page */

form[name="upload"] { /*Upload Form*/
    color: white;
}

/* PM Page */

#mpbox:hover {
    background: #2b2b2b !important;
}

/* Search Page */
a[href*="torrents.php?cat="] {
    color: white !important;
}

a[href*="torrents.php?cat="]:hover {
    color: gray !important;
}

a[href*="torrents-search.php?sort=id&order=desc&page="] {
    color: white !important;
}

a[href*="torrents-search.php?sort=id&order=desc&page="]:hover {
    color: gray !important;
}

/* Forums */

.f-cat td { /* Forum Categories */
    background: #2b2b2b !important;
    color: white !important;
}

.p-title th, .p-title tr, legend { /* Forum Post Headers */
    background: #2b2b2b !important;
    color: white !important;
}

.comment-details { /* Forum Left Sidebar User Details */
    background: #3e3e3e !important;
}

.bbcodecode td { /* Forum "Code" BBCode */
    color: white !important;
}

.bbcodecode tr { /* Forum "Code" BBCode */
    background: rgba(197,202,233,.8) !important;
    color: white !important;
}

td[style="border: 1px black dotted; max-width: 630px; word-wrap: break-word; word-break: break-all;"] { /* Forum Quotes */
    background: rgba(197,202,233,.8) !important;
    color: black !important;
    border: 0 !important;
}

table.main { /* Forum Quotes */
    background: rgba(197,202,233,.8) !important;
    border: 0;
    border-radius: 5px !important;
}


/* General Elements */

.bb_holder .fa { /*BB Icons*/
    color: black !important;
    cursor: pointer;
}

.bb_holder { /* ForumPost/TorrentDescription Box + BBIcons */
    border: 0;
}

.bb_holder tr { /* ForumPost/TorrentDescription Box + BBIcons */
 	background: #7a7a7a !important;
    border: 0;
}

.table_head { /* Table Headers */
    background: #2b2b2b !important;
}

.f-title th { /* Headers */
    background: #2b2b2b !important;
}

.w3-button.w3-teal.w3-margin-bottom { /* Profile Report Button */
    background: gray !important;
    border: 0;
    padding: 10px 10px 10px 10px;
}

* {
    outline: none;
}

#onlineUserSpan {
    background: #2a2a2a !important;
}

/* Watchlist Script Style */
#modal-container {
    background: #2a2a2a !important;
    color: white;
}
}

@-moz-document url-prefix("http://worldwidetorrents.me/api/") {
pre { 
    color: white
}
}
