css:
@-moz-document domain("filelist.ro") {
div#wrapper {
    display: flex;
    align-items: flex-start;
    height: 100% !important;
}

.mainheader {
    height: 100%;
    overflow: none;
    min-width: 200px;
    width: 200px;
    display: flex;
    background: #201F1B;
    /* flex: 1; */
}



.subheader {
    /* width: 100%; */
    height: 100%;
    margin: 0;
    display: flex;
    width: 200px;
    flex-direction: column;
}

div#navigation {
    width: 100%;
    position: initial;
    top: 0px;
    order: 2;
    height: auto;
    margin: 0px;
}

.statusbar {
    background: #1A1915;
    border: none;
    width: 180px;
    order: 3;
}

.logo {
    text-align: center;
    float: none;
    width: 200px;
    background-position: -40px -10px;
    height: 150px;
    /* transform: scale(0.7); */
}

.subheader > a {
    display: block;
    order: 1;
}

.statusbar > div >div {
    /* display: none; */
}

a {}

.statusbar > div >div a {
    /* display: block; */
}

.status_avatar {
    margin: 0px;
}

li.fleft {
    float: none;
}

div#nav {
    height: auto;
}

div#wrapper {
    flex-direction: row;
    height: initial !important;
}

div#container {
    flex: 1;
    width: auto;
    padding: 0;
    margin: 0;
}
div#maincolumn {
    width: 100%;
}

.status_avatar {
    float: none !important;
    height: auto;
}

.status_avatar img {
    width: 55px;
    height: auto !important;
}

.statusbar * {
    float: none !important;
}

.statusbar > div:nth-child(2) {
    display: inline-block;
}

.status_avatar {
    display: inline-block;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) > div:nth-child(5) > span:nth-child(2) {
    display: block;
}

.statusbar > div:nth-child(2) > div > div {
    display: block;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) > div:nth-child(3) {
    display: inline-block;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) > div:nth-child(3) > span {
    width: 49%;
    display: inline-block;
    margin: 0 !important;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) > div:nth-child(1) {
    position: relative;
    top: -25px;
    margin-left: 60px;
    line-height: 5px;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) > div:nth-child(2) {
    position: relative;
    top: -20px;
    width: 120px;
    margin-left: 60px;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) {
    position: relative;
    margin-top: -30px;
    line-height: 20px;
}

#wrapper > div.mainheader > div > div.statusbar > div:nth-child(2) > div:nth-child(4) > span {
    display: block;
    line-height: 20px;
}
#nav ul li.active, #nav ul li a:hover, #nav ul li.active a {
    color: white;
    text-shadow: none;
}

#nav ul li, #nav ul li a {
    color: #747474;
}

div#navigation {
    padding-top: 30px;
}


/*@import url('http://fonts.googleapis.com/css?family=Roboto');*/
body { font: Roboto;}
.cblock {
    width: auto;
}

.cblock-top {
    display: none;
}

body {
    background: white;
    background: #201F1B;
    height: auto;
}

.cblock-content {
    background: white;
    color: black;
    width: auto;
}

.cblock-innercontent {
    width: auto;
    padding-left: 40px;
}

div#container {
    padding: 0px;
    background: white;
    margin-top: 5px;
}

div#maincolumn {
    padding: 0px;
    /* padding: 0px 30px; */
}

#navigation {
    font-family: Roboto;
}

.statusbar {
    /* font-family: Roboto; */
    font-size: 12px; 
}

.colhead {
    background: #f7f7f7;
    color: #747474;
}

.pager a, .pager a:link {
    background: #eee;
    border: 1px solid #ddd;
    color: black;
    font-family: Roboto;
}

html,body {
    font-family: Roboto;
    font-size: 9pt;
}

.pager span.current {
    background: #ddd;
    border: 1px solid #ddd;
    color: black;
}

.pager {
    /* transform: scale(1.1); */
    /* position: relative; */
}

input, select, select:active, select:hover, select option {
    background: #f1f1f1;
    border: none;
    color: black;
    font-family: Roboto;
    font-size: 13px;
    line-height: 13px;
}

select option {
    border: none;
    color: black !important;
    background: white;
}

table td.noborder form {
    color: black;
}

input.btn {
    background: #201f1b;
    color: white !important;
    font-family: Roboto;
    font-size: 15px;
    line-height: 15px;
    height: 30px;
}

table {
    margin: 0;
}
input.btn:hover {background: #0b2a53;color: #e0e0e0 !important;font-family: Roboto;font-size: 15px;line-height: 15px;height: 30px;}

.torrentrow >span {
    /* transform: scale(1.2); */
    /* margin-left: 160px; */
    border-bottom: 1px solid #ececec;
    margin-bottom: 11px;
}

.torrentrow a {
    color: #201f1b;
    font-family: Roboto;
}

.visitedlinks .colhead {
    display: none;
}


.torrenttable {
    border-bottom: 1px solid #ececec;
    margin-bottom: 11px;
    }

div#copyright {
    bottom: -210px;
}

div#footer {
    bottom: -155px;
    background: #201f1b;
}

.cblock-bottom {
    display: none;
}

.torrenttable a {
    font-size: 14px;
}

.torrenttable > * {
    height: 70px !important;
}

.torrenttable img {
    filter: grayscale(40%);
}
#copyright {
    background: #201f1b;
}

#copyrightInner > font:nth-child(3) > b:after {
    content: "Theme by Farcas Sergiu";
    display: block;
}

td {
    color: #201f1b;
    font-size: 14px;
}

.quote {
    background: #f1f1f1;
}
}
