css:
@-moz-document domain("forums.nintendoprime.net") {
/* bar thingy */
#ipsLayout_header header {
    background: #333;
}
.ipsNavBar_primary > ul > li.ipsNavBar_active > a {
    background: #666;
	color: #fff;
}
.ipsNavBar_primary > ul > li > a {
    background: rgba(255, 255, 255, .1)
}
.ipsNavBar_secondary,
#ipsLayout_header nav {
    background: #666;
}
.ipsNavBar_secondary > li.ipsNavBar_active a {
    color: #55b7dd;
}
.ipsNavBar_secondary > li.ipsNavBar_active a:hover {
	color: #2aa5d5
}
.ipsNavBar_secondary > li > a {
    color: #bfbfbf;
}
.ipsNavBar_secondary > li > a:hover {
    color: #d9d9d9;
}
.ipsMenu_headerBar {
    background: #333;
    color: #fff;
    border-bottom: 1px solid #888;
}
.ipsMenu_footerBar {
    background: #333;
    color: #fff;
}                                         
                                            
/* down area? im amazing ig */
body {
    background: #404040;
    color: #e6e6e6;
}
.ipsBreadcrumb > ul:last-child > li:last-child {
	color: #6ac0e2;
	opacity: 1;
}
.ipsBreadcrumb.ipsBreadcrumb_top {
    opacity: 1;
}
.ipsBreadcrumb > ul > li {
    color: #cccccc;
}

.ipsType_pageTitle {
    font-weight: bold;
    color: #2694c0;
}
.ipsType_richText {
    color: #fff;
}
.ipsApp .ipsButton_link {
    color: rgb(150, 150, 180);
}
.ipsApp .ipsButton_important {
	background: #3385ff;
}
.ipsApp .ipsButton_important:hover {
	background: #0052cc;
}

.ipsButtonBar {
    line-height: 1;
    box-shadow: none;
    background: #333;
    border-bottom: 1px solid #555;
}
.ipsDataItem {
    background: #555;
    border-color: #888;
}
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated) {
    background-color: #555;
}
.ipsBox {
    border: 1px solid #333;
    border-radius: 0px;
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
	background: #333;
    color: #fff;
    border-radius: 0px;
}
a {
    color: #66a3ff;
}
a:hover {
    color: #99c2ff;
}
.ipsType_blendLinks a:hover {
    color: #99c2ff !important;
}
.ipsBreadcrumb [data-action="defaultStream"], .ipsBreadcrumb [data-action="markSiteRead"] {
	color: #fff;
}

.ipsApp .ipsButton_link.ipsFollow, .ipsApp .ipsButton_link.ipsPromote {
    background: #555;
    border: 1px solid #777;
    border-radius: 0px;
    color: #fff;
}
.ipsFollow .ipsCommentCount {
    background: #777;
    color: #e6e6e6
}
html[dir="ltr"] .ipsFollow .ipsCommentCount:after {
    border-color: transparent #777 transparent transparent;
}
.ipsButtonBar .ipsButtonRow li > a, .ipsButtonBar .ipsButtonRow li > button, .ipsButtonBar .ipsButtonRow li > span {
    background: #555;
    color: #fff;
}
ul:not( .ipsMenu_keyNav ) .ipsMenu_item:not( .ipsMenu_itemClicked ):not( .ipsMenu_itemDisabled ) a {
    background: #555;
    color: #fff;
    border-bottom: 1px solid #777;
}
ul:not( .ipsMenu_keyNav ) .ipsMenu_item:not( .ipsMenu_itemClicked ):not( .ipsMenu_itemDisabled ) a:hover {
    background: #444;
}

.ipsMenu {
    background: #555;
    color: #fff;
    border: 1px solid #777;
}
.ipsMenu_sep {
    border-top: none;
}
hr.ipsHr {
    border-color: #888;
}
.ipsMenu_title {
    background: #333;
    border-bottom: 1px solid #777;
}
.ipsMenu.ipsMenu_bottomRight:before,
.ipsMenu.ipsMenu_bottomRight:after {
    border-color: transparent transparent #777 transparent !important;
}

/* Post junk */
.ipsType_sectionHead {
	color: #66a3ff;
}
.ipsFaded.ipsFaded_more {
	opacity: 1;
}
.ipsApp .ipsButton_light {
    color: #fff;
	background: #555;
    border: 1px solid #888;
    border-radius: 0px;
}
.ipsPager .ipsPager_next a:after, .ipsPager .ipsPager_prev a:after {
	color: #66a3ff;
}
.ipsPager_type, #ipsLayout_footer a, #ipsLayout_footer p {
    color: #fff;
}
.ipsAreaBackground {
    background: #777;
    border: 1px solid #888;
}
html[dir="ltr"] .ipsComposeArea_withPhoto .ipsComposeArea_editor:before {
    border-color: transparent #666 transparent transparent;
}

/* Blog Sections... */
.ipsType_sectionTitle {
    background: #333;
    color: #fff;
}
/* Calendar Section */
.ipsApp .ipsButton_primary {
    background: #333;
    border-color: #888;
}
/* Pages */
.ipsTabs {
    background: #333;
}
.ipsTabs_activeItem,
.ipsTabs_panel {
    background: #666;
    color: #e6e6e6;
}
/* Staff List */
.ipsAreaBackground_light {
    background: #555;
    border-bottom: 1px solid #888;
}
/* Leaderboards */
.ipsButtonRow li > a, .ipsButtonRow li > button, .ipsButtonRow li > span, .ipsAreaBackground_light .ipsButtonRow li > a:not( .ipsButtonRow_active ), .ipsAreaBackground_light .ipsButtonRow li > button:not( .ipsButtonRow_active ), .ipsAreaBackground_light .ipsButtonRow li > span:not( .ipsButtonRow_active ) {
    background: #444;
    color: #fff;
    border: 1px solid #888;
}

/* Profile Junkie */
.ipsNavBar_primary:not( .ipsNavBar_noSubBars ) > ul:before {
    background: #666;
}
.ipsAreaBackground_reset {
    background: #333;
}
.ipsPageHead_special {
	background: #666;
}
.ipsStreamItem_snippet .ipsType_richText {
    color: #fff;
}
.cProfileRepScore_neutral {
    background: #666;
}
/* Other pop ups i forgot where it goes lol */
.cUserHovercard .ipsPageHead_special {
    background: #666;
}
.ipsHovercard {
    background: #333;
}

}
