css:
@-moz-document domain("mail.tutanota.com") {
button:hover {
	opacity: 1 !important;
			filter: invert(1);
	-webkit-filter: invert(1);
}

/*****  header  ***/
.header-nav {background: #2f4a60; box-shadow: none}
.nav-bar div:nth-last-child(2) {background: #7b7c8a !important}
.nav-bar a[style="color: rgb(132, 0, 16);"] {
	background: #000;
	padding-left: 11px;
	padding-right: 11px;
	margin-left: -5px;
	margin-right: -5px;
}

/*****  center  ***/
/***  folders  */
/* heads */
.mr-negative-s button small,
	.folder-row small.b {color: #000 !important; opacity: 1}
.folder-row .button-icon {background: #000 !important}
.mr-negative-s button .icon {fill: #000 !important}
.mr-negative-s button:hover small {color: #555 !important; opacity: 1}
.mr-negative-s button:hover .icon {fill: #555 !important; opacity: 1}
.folder-row button .icon span {background: #000 !important}
.folder-row button:hover .icon {background: #808080 !important;}
.folder-row button:hover .icon path {fill: #eee !important}
/* items */
.folder-column, .list-border-right {border-right: none}
.folder-column, .list-bg {background: #2f4a60}
.folders .row-selected {max-width: 277px}
.folders .row-selected a {background: #000}
.nav-button:hover /*miez?*/ {-webkit-filter: brightness(2) hue-rotate(9deg); filter: brightness(2) hue-rotate(9deg)}
/***  list  */
.list-container {color: #707070}
.list-row {background: #2f435a}
.odd-row {background: #315064}
.list-row.row-selected {background: #000}
.list-row .text-ellipsis {font-weight: 500}
.list-row .text-ellipsis.b {background: #730019; color: #707070; font-weight: 700; padding: 0 6px 13px 20px; margin: 0 -6px -13px -20px}
.list-row .flex-space-between small:first-child, .list-row .list-accent-fg {color: #6cc}
.list-row.row-selected * {color: #730019 !important}

/*****  content  ***/
body {background: #707070; color: #000}
.body a {
	background: #616e7f;
	color: #000 !important;
	padding: 0 2px;
	border-radius: 2px;
	text-decoration: none;
}
.content-message-bg {border: solid 4px #730019; background: #707070; color: #730019}
.hr {height: 2px; background: #780015}
span.button-icon svg {
			filter: invert(.923) sepia(1) hue-rotate(-64deg) saturate(15);
	-webkit-filter: invert(.923) sepia(1) hue-rotate(-64deg) saturate(15)
}
/* in mail body */
hr {height: 2px; background: #303030; border: none}

/*****  dialog  ***/
/** header **/
.dialog-header {background: #7b7c8a; border-bottom: none}
.flex-third-middle {color: #730019}
.button-content.primary .text-ellipsis {
	color: #7b7c8a !important;
	background: #730019;
	padding: 2px 7px;
}
.button-content.secondary .text-ellipsis {
	color: #000 !important;
}
/* attach files */
.mr-negative-s.flex-end >button:focus {opacity: 1}
.mr-negative-s.flex-end >button:focus span {background: #3f0 !important}
.mr-negative-s.flex-end >button:focus span path {fill: #730019 !important}
.mr-negative-s.flex-end >button:focus span.button-icon svg {filter: none; -webkit-filter: none}
/** content **/
.content-bg {background: #000; color: #6cc}
.items-end.flex-wrap {border-bottom: solid 2px #730019 !important}
.text-field .button-content .text-ellipsis, .text-field input {color: #fff !important}
.text-field .button-content:hover .text-ellipsis {color: #730019 !important; font-weight: 500 !important}
.small.noselect /*this msg is sent...*/ {color: #730019; font-weight: 800}
/** dd menu **/
.dropdown-panel {background: #730019}
.button-content .text-ellipsis {color: #707070 !important}

/***** logo ***/
.logo g, .logo path:first-of-type {fill: #7b7c8a}

/***** compose icon ***/
button.fixed-bottom-right {border-radius: 50%}
button.fixed-bottom-right, button.fixed-bottom-right span.button-icon {
	border: solid 4px #6cc;
	box-sizing: content-box;
	opacity: 1;
}

button.fixed-bottom-right span.button-icon {
	background: #730019 !important;
	fill: #6cc !important;
}
button.fixed-bottom-right span.button-icon svg,
button.fixed-bottom-right:hover {
	filter: none; -webkit-filter: none;
}

button.fixed-bottom-right:hover span.button-icon {
	background: #6cc !important;
	fill: #730019 !important;
}
button.fixed-bottom-right:hover, button.fixed-bottom-right:hover span.button-icon {
	border-color: #730019;
}
}
