css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dealabs.com") {
  /****** Fait par Raven450 en souvenir du vieux Dealabs.com *****/
  
  /* Deals */
  .js-threadList {
    padding-right: 7px;  
  }
  .js-threadList article {
    margin-bottom: 15px;
    border-radius: 8px;
    border: 1px solid #d1d5db;
  }
  img.thread-image{
    max-height: 140px;
    max-width: 140px;
  }
  
  
  /* Barre de menus */
  div.nav{
    background-color: #333333;
  }
  section.subNav {
    margin-bottom: 15px;
  }
  .subNavMenu--morph .subNavMenu-link.subNavMenu-link--selected::after {
    background-color: #f44343;
  }
  .subNavMenu-link.subNavMenu-link--selected {
    color: #f44343 !important;
  }
  .subNavMenu-link .subNavMenu-btn .space--l-2 {
    color: #8f949b !important;
  }
  
  /* Bouton poster nouveau deal */
  /*menu.tGrid-cell a.js-submit-btn-default {
    background-color: rgba(130, 118, 185, 1);
  }*/
  .nav .btn--mode-highlight.btn--mode-special.btn--toW5-circle {
    background-color: rgba(130, 118, 185, 1);
  }
  .nav .btn--mode-highlight.btn--mode-special.btn--toW5-circle:hover {
    background-color: rgba(130, 118, 185, 0.8);
  }
  
  /* Deals Hot */
  .cept-hottest-widget .threadWidget-header--type-card {
    background-color: #f44343;
    color: white !important;
  }
  .cept-hottest-widget .threadWidget-header--type-card .mute--text {
    color: white;
    text-transform: uppercase;
  }
  .cept-hottest-widget .threadWidget-header--type-card .text--color-red {
    color: white;
  }
  .vote-temp--burn options {
    color: white;
  }
  .ico--type-gear-red::before {
    background-position: -.00071em -181.37em;
    width: .85786em;
  }
  .cept-hottest-widget .thread-image.imgFrame-img {
    max-height: 100%;
    max-width: 100%;
  }
  
}
