css:
@-moz-document domain("proxer.me") {
@font-face {
    font-family: "Segeo UI Light";
    src: url("https://logosart.de/proxer2-0/segoe-ui-webfont.woff") format('woff');
    font-weight: Lighter;
}

body{
    font-size: 1.05em;
    color: #909090;
	font-family: "Segoe UI Light", sans-serif !important;
    font-weight: lighter !important;
	font-variant: normal;
	background: #232428 !important;

}

.calendarHeadNav table {
    background-color: #232428;
    border-radius: 0;
    border: 0;
}

h1{
	font-family: "Segoe UI Light", sans-serif;
    font-weight: lighter;
    color: #ef394a;
}

#headerInfo {
	border-radius: 0;
    border: 1px #232428 solid;
    background: transparent;
}

.headDate {
	border-color: #232428;
}

ul#simple-navi li, ul#simple-navi-wiki li {
	margin: 0;

}

.details td {
    background-color: #2d2f33;
}

#notificationBubble, #loginBubble, .customBubble, .pointerBubble {
	border-radius: 0;


}

hr{
	display: none;
}

a:hover{
	color: #ef394a;
    transition: all 0.7s;
}

h3{
	color: #fff;
	font-weight: lighter;
}

a{
	color: #bdbdbd;
    transition: all 0.7s;
}

ul#simple-navi li.active a{
	color: #ef394a;
    background: transparent;
    border-bottom: 1px #ef394a solid;
}

ul#simple-navi li a, ul#simple-navi-wiki li.active a, ul#simple-navi-wiki li a.active, ul#simple-navi-wiki li a:hover {
	background: transparent;
    color: #8f8f90;
    border-bottom: 1px #8f8f90 solid;
    font-weight: lighter; 
    transition: all 1s;
}

ul#simple-navi-wiki li a, ul#simple-navi li a.active, ul#simple-navi li a:hover{
    transition: all 1s;
	color: #fff;
    background: transparent;
    border-bottom: 1px #fff solid;
}

#nav{
    background: url("https://logosart.de/proxer2-0/shade.png");
    height: 80px;
    border-bottom: 0px;
}

#wrapper{
    background-position-x: 55%;
    background-size: 110%;
    max-width: none !important;
   	background-position-y: 0;
}
ul#simple-navi, ul#simple-navi-wiki{
	
    border-radius: 0;
    background-color: #2d2f33;
    border: 0;

    margin-left: 5px;
    margin-right: 20px;
}

#main{
	margin-top: 25%;
    margin-left: 0;
    margin-right: 0;
    padding-bottom: 5vh;
    border-radius: 0;
    background-color: #2d2f33;
    border: 0;
}

.topmenu ul{
	background: #2d2f33;
    opacity: 0.9;
    border: 0;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
}

.topmenu>ul>li:hover {
	background: #ea0b3f;
}

.colorbox{
	display: none;
}

a.menu {
    border: 0px solid #3e3e3e;
    background: #3e3e3e;
    border-radius: 0;
    color: #a0a0a0;
    transition: all ease 0.8s;
}

a.menu.active, a.menu:hover {
 	background: #ef394a !important;  
    color: #fafafa !important;
    transition: all ease 0.8s;
}

.infocell>div {
    float: left;
    width: 150px;
    border: 0;
    padding: 10px;
}

.infocell {
    cursor: pointer;
	border-color: #656565;
    transition: all 0.8s;
}
.infocell:hover {
	background: #505050;
    color: #fff;
}

.infocell:hover a{
    color: #fff;
}

.navlogo{
	background-image: url(https://logosart.de/proxer2-0/logo_proxer.png) !important;
}

*:focus {
    outline: none;
}

select {
    font-family: "Segoe UI Light", sans-serif;
    font-size: 0.8em !important;
    font-weight: lighter;
	background: #232428;
    color: #fff;
    border: 1px solid #3e3e3e;
    padding: 4px 7px;
}

input[type=text] {
    font-family: "Segoe UI Light", sans-serif;
    padding: 4px 7px;
    background: #232428;
    color: #fff;
    border: 1px solid #3e3e3e;
}

input[type=password] {
    font-family: "Segoe UI Light", sans-serif;
    padding: 4px 7px;
    background: #232428;
    color: #fff;
    border: 1px solid #3e3e3e;
}

input[type=submit] {
    font-family: "Segoe UI Light", sans-serif;
    cursor: pointer;
    padding: 4px 15px;
    background: #ef394a;
    color: #fff;
    border: 0;
    transition: all ease 0.5s;
}

input[type=button] {
    font-family: "Segoe UI Light", sans-serif;
    cursor: pointer;
    padding: 4px 15px;
    border: 0;
    background: #ef394a;
    border-radius: 0;
    color: #fff;
    transition: all ease 0.5s;
}

input[type=radio]::before {
  content: url('https://logosart.de/proxer2-0/proxer-radio.png');
  display: inline-block;
  width: 15px;
  height: 15px;
  margin-right: 5px;
    background: #2d2f33;
} 

input[type=radio]:checked::before{
    content: url('https://logosart.de/proxer2-0/proxer-radio-checked.png');
    margin-right: 5px;
}

input[type=radio]:hover::before{
    content: url('https://logosart.de/proxer2-0/proxer-radio-hover.png');
    margin-right: 5px;
}

input[type=radio]:hover:checked::before{
    content: url('https://logosart.de/proxer2-0/proxer-radio-hover-checked.png');
    margin-right: 5px;
}

input[type=checkbox]::before {
  	content: url('https://logosart.de/proxer2-0/proxer-checkbox.png');
  	display: inline-block;
  	width: 15px;
  	height: 15px;
  	margin-right: 5px;
	background: #2d2f33;
} 

input[type=checkbox]:checked::before{
    content: url('https://logosart.de/proxer2-0/proxer-checkbox-checked.png');
    margin-right: 5px;
}

input[type=checkbox]:hover::before{
    content: url('https://logosart.de/proxer2-0/proxer-checkbox-hover.png');
    margin-right: 5px;
}

input[type=checkbox]:hover:checked::before{
    content: url('https://logosart.de/proxer2-0/proxer-checkbox-hover-checked.png');
    margin-right: 5px;
}

input[type=submit]:hover {
	box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
}

input[type=button]:hover {
	box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
}

textarea{
	background: #2d2f33;
    border: 1px solid #3e3e3e;
    color: #fafafa;
}

.ui-tooltip {
	background: #2d2f33;
    border: 0;
    font-family: "Segoe UI Light", sans-serif !important;
    color: #fafafa;
    font-weight: lighter;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2);
	border-radius: 0;
}

.message {
    background: #2d2f33 !important;
    border: 0 !important;
    color: #fafafa !important;
    box-shadow: 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12), 0 2px 4px -1px rgba(0, 0, 0, .2) !important;
	border-radius: 0 !important;
}

::-webkit-input-placeholder { /* WebKit, Blink, Edge */
    color:    #999999;
}

:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
   color:    #999999;
   opacity:  1;
}

::-moz-placeholder { /* Mozilla Firefox 19+ */
   color:    #999999;
   opacity:  1;
}

:-ms-input-placeholder { /* Internet Explorer 10-11 */
