css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.dealabs.com"), domain("www.mydealz.de"), domain("www.hotukdeals.com") {

  
  
/* ---------- Gestion du prix -------- */

a.overflow--wrap-off.flex--auto-0 .thread-price{
color: #f44343;
font-weight: bold;
font-size: 14pt;
font-family: 'Roboto', sans-serif;
}
a.overflow--wrap-off.flex--auto-0 .thread-price.text--color-greyShade{
color: #8F949B;
font-weight: bold;
font-size: 14pt;
}     
  
  
  
  
/* ---------- Couleur d'arrière-plan -------- */
.bg--off {
background: #35373B;
}
/* ---------- Bordure entre image et description des deals en preview / et du deal sur sa page -------- */
.threadGrid-image, .threadItem-image {
border-right: 1px solid #D1D5DB;
margin-right: 12px;
} 
/* ---------- Bordure avatar (merci à Trideal)-------- */  
.avatar {
border-radius: 5px;
}
/* ---------- Bordure vote -------- */
.vote-box {
border: 1px solid #e7ecf3;
background: #e7ecf3;
} 
/* ---------- Couleur de la bulle de notification Hot -------- */
.subNavMenu-item--separator:nth-child(1) .subNavMenu-link-badge{
background: #F44343;
}  
/* ---------- Couleur du survol du bouton Hot -------- */
.subNavMenu-item--separator:nth-child(1) .subNavMenu-item.subNavMenu-link:hover{
color: #F44343;
}
/* ---------- Couleur du bouton Hot (merci à Rasmus) -------- */
.subNavMenu-item--separator:nth-child(1) .subNavMenu-link.subNavMenu-link--selected{
color: #F44343;
}
/* ---------- Couleur du soulignage du bouton Hot (merci à Rasmus) -------- */
.subNavMenu-item--separator:nth-child(1) .subNavMenu-link.subNavMenu-link--selected:after {
background-color : #F44343;
}
/* ---------- Couleur du survol du bouton Commentés -------- */
.subNavMenu-item--separator:nth-child(3) .subNavMenu-item.subNavMenu-link:hover{
color: #52B034;
}
/* ---------- Couleur du bouton Commentés (merci à Rasmus) -------- */
.subNavMenu-item--separator:nth-child(3) .subNavMenu-link.subNavMenu-link--selected{
color: #52B034;
}
/* ---------- Couleur du soulignage du bouton Commentés (merci à Rasmus) -------- */
.subNavMenu-item--separator:nth-child(3) .subNavMenu-link.subNavMenu-link--selected:after {
background-color : #52B034;
}
/* ---------- Couleur de fond du menu Les + hot -------- */
.cept-hottest-widget .threadWidget-header--type-card {
background-color: #FF4343;
}
/* ---------- Couleur de la police de la séléction de la liste déroulante -------- */
.plainSelect {
color: #FFFFFF;
}
/* ---------- Couleur de la police de la liste déroulante -------- */
.plainSelect-ctrl {
color: #F44343;
}
/* ---------- - déjà voté -------- */
.bubbleWrap--s.bg--grey {
background-color: #36B7CD;
}
/* ---------- + déjà voté -------- */
.cept-voted--up + .bubbleWrap--s {
background-color: #DF0033;
}
/* ---------- expiré déjà voté -------- */
.vote-box--muted .bubbleWrap--s {
background-color: #8F949B;
}
/* ---------- Bouton + -------- */
.vote-up .bubbleWrap--s {
background-color: #DF0033;
}
/* ---------- Bouton - -------- */
.vote-down .bubbleWrap--s {
background-color: #36B7CD;
}
/* ---------- Flèche bas vote négatif -------- */
.ico--type-minus-blueTint:before {
background-position: 0.05em -128.60em;
}
/* ---------- Flèche haut vote positif -------- */
.ico--type-plus-red:before {
background-position: 0.07em -128.40em;
transform: rotate(180deg);
}  
/* ---------- Flèche blanche du menu Les + hot -------- */
.ico--type-dropdown-red:before {
background-position: 0em -128.60em;
}
/* ---------- Roue blanche du menu Les + hot -------- */
.ico--type-gear-red:before {
background-position: -.00071em -181.37em;
}
/* ---------- Gestion du prix -------- */
.thread-price{
color: #FF0000;
font-weight: bold;
font-size: 14pt;
}
/* ---------- Gestion du prix barré et remise -------- */
.mute--text.size--all-s{
color: #000000;
font-weight: normal;
font-size: 9pt;
}
/* ---------- Gestion des frais de port -------- */
.cept-shipping-price{
color: #000000;
font-weight: bold;
font-size: 10pt;
}
/* ---------- Dissimulation de "Bons plans" -------- */
.cept-merchant-link-term{
color: #FFFFFF;
font-size: 1%;
}
/* ---------- Ajout du @ devant le marchand -------- */
.text--color-brandPrimary.cept-merchant-name:before {
font-family: Arial, sans-serif;
font-size: 14pt;
content: "@ ";
color: #03A5C1;
}
/* ---------- Ajout du @ expiré devant le marchand -------- */
.text--color-greyShade.cept-merchant-name:before {
font-family: Arial, sans-serif;
font-size: 14pt;
content: "@ ";
color: #8F949B;
}
/* ---------- Gestion du marchand -------- */
.cept-merchant-name{
font-weight: bold;
font-size: 14pt;
font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
font-variant: small-caps;
}
/* ---------- Gestion du titre du deal -------- */
.cept-tt{
color: #000000;
font-weight: bold;
font-size: 11pt;
}
/* ---------- Gestion de la description des deals en preview -------- */
.cept-description-container.size--all-s {
color: #777777;
font-weight: normal;
font-size: 10pt;
}
/* ---------- Gestion de la température du deal -------- */
.vote-temp{
font-weight: bold;
font-size: 20pt;
}
/* ---------- Gestion de la température du deal voté + -------- */
.cept-voted--up{
color: #DF0033;
font-weight: bold;
font-size: 20pt;
}
/* ---------- Gestion de la température du deal voté - -------- */
.cept-voted--down{
color: #36B7CD;
font-weight: bold;
font-size: 20pt;
}
/* ---------- Gestion de la température du deal épinglé -------- */
.vote-temp.text--color-charcoalTint{
color: #DF0033;
font-weight: bold;
font-size: 20pt;
}
/* ---------- Gestion du terme Epinglé -------- */
.cept-pinned-flag{
color: #8276b9;
font-weight: bold;
font-size: 20pt;
}
/* ---------- Gestion de la température des deals expirés -------- */
.vote-box.vote-box--muted{
font-weight: bold;
font-size: 20pt;
}  
/* ---------- Gestion des catégories du carroussel des + hot -------- */
.thread-group{
display: none;
}
/* ---------- Gestion du libellé Les + hot -------- */
.threadWidget-header--type-card .mute--text {
display: none;
}
.tGrid-cell.space--l-3.space--v-3:before {
content: "LES PLUS HOT";
color: #FFFFFF;
font-size: 10pt;
font-weight: bold;
}
.tGrid-cell.space--v-3.width--all-5.space--r-2 {
padding-left: 20px;
}
/* ---------- Gestion de la température du carroussel des + hot -------- */
.vote-temp.space--r-1.vote-temp--hot{
color: #FFFFFF;
background-color: #FF4343;
font-weight: bold;
font-size: 10pt;
}
.vote-temp.space--r-1.vote-temp--warm{
color: #FFFFFF;
background-color: #FF7900;
font-weight: bold;
font-size: 10pt;
}
.thread-title .linkPlain.space--v-1:before {
 content: "_";
color: #FFFFFF;
font-size: 6pt;
}
.thread-title .linkPlain.space--v-1, .thread-title.linkPlain.space--v-1  {
color: #777777;
}
/* ---------- Gestion du prix du carroussel des + hot -------- */
.thread-price.thread-price.text--b.vote-temp--burn {
color: #FF0000;
font-weight: bold;
font-size: 9pt;
}  
/* ---------- Gestion de la température des deals une fois voté -------- */
.cept-voted {
font-weight: bold;
font-size: 20pt;
}
/* ---------- Gestion de l'espace entre les commentaires (merci à Vinula) -------- */
.comments-item-inner, .comments-list article:target .page2-space--h-p {
border-top: 5px solid #35373B;
}
/* ---------- Gestion des boutons de pagination -------- */
.ico--type-left-blue:before, .ico--type-left-brandPrimary:before {
 background-position: 1em -1em; 
content: "\25C0";
font-size: 24pt;
height: 50px;
color: #03A5C1;
}
.ico--type-right-grey:before {
 background-position: 1em -1em; 
content: "\25B6";
font-size: 24pt;
height: 50px;
color: #C1C5CA;
}
.ico--type-right-blue:before, .ico--type-right-brandPrimary:before {
 background-position: 1em -1em; 
content: "\25B6";
font-size: 24pt;
height: 50px;
color: #03A5C1;
}
.ico--type-left-grey:before {
 background-position: 1em -1em; 
content: "\25C0";
font-size: 24pt;
height: 50px;
color: #C1C5CA;
}  
/* ---------- Gestion de la jauge -------- */    
.vote-box .vote-temp--burn, .vote-box .vote-temp--hot {
color: #DF0034;
width: 80px;
height: 37px;
text-align: center;
border-bottom: 5px solid #DF0034;
}
.vote-box .vote-temp--warm {
color: #FF7900;
width: 80px;
height: 37px;
text-align: center;
border-bottom: 5px solid #FF7900;
}
.vote-box .vote-temp--cold, .vote-box .vote-temp--colder {
color: #36B7CD;
width: 80px;
height: 37px;
text-align: center;
border-bottom: 5px solid #36B7CD;
}  
/* ---------- Gestion du footer -------- */    
.bg--main.boxSec-div.js-pagi-bottom {
height: 30px;
background-color: black;
}  
.pagi-toggleFooter .ico:before {
display: none;
}
.ico.ico--type-down-brandPrimary {
display: none;
} 
.ico.ico--type-up-brandPrimary {
position: relative;
left: 1000px;
} 
#pagination {
position: relative;
bottom: 30px;
}     

}
