css:
@-moz-document domain("www.dealabs.com") {
#main > div:nth-child(1) > header > div > div > menu > a.btn.btn--mode-highlight.btn--mode-special.btn--toW5-circle.space--ml-2.space--fromW2-ml-3.vAlign--all-m.js-submit-btn-default.hide--toW2 > span.ico.ico--type-plus-white.ico--reduce.size--all-xxxl.size--fromW4-xl, .subNavMenu-btn > span.space--l-2
{
    display: none;
}

.nav-content
{
    height: 70px;
    max-width: 1170px;
}

#main > div:nth-child(1) > section > div
{
    max-width: 1170px;
}

#main > div:nth-child(1)
{
    max-width: 100%;
}

.tGrid-row
{
    max-width: 1170px;
    display: block;
    margin: 0 auto;
}

.logo, .pepperLogo
{
    margin: 0 10px 0 0;
    float: left;
    padding: 0;
    height: 70px;
    width: 183px;
    background: url(https://static.dealabs.com/images/header/logo.svg) no-repeat center;
    background-size: contain;
}

.nav-link
{
    color: #bfc3c8;
    font-size: 1em;
    font-weight: bold;
    padding: 0px 7px;
    height: 70px;
}

.btn--mode-header-login
{
    background-color: #02a5c1;
    color: white;
    font-weight: bold;
    border-radius: 4px;
    border: 1px solid #02a5c1;
    padding: 11px 10px 11px 15px;
}

.btn--mode-highlight
{
    color: #bfc3c8;
    font-weight: bold;
    border: 1px solid #bfc3c8;
    border-radius: 4px;
    padding: 12px 10px 11px 10px;
    background: none;
}

.subNavMenu-item--separator:nth-child(1) .subNavMenu-link.subNavMenu-link--selected
{
    color: #f44343;
}

.subNavMenu-item--separator:nth-child(1) .subNavMenu-link.subNavMenu-link--selected:after
{
    background-color: #f44343;
}

.subNavMenu-item--separator:nth-child(3) .subNavMenu-link.subNavMenu-link--selected
{
    color: #52B034;
}

.subNavMenu-item--separator:nth-child(3) .subNavMenu-link.subNavMenu-link--selected:after
{
    background-color: #52B034;
}

.subNavMenu-item
{
    height: 40px;
    line-height: 40px;
}

#tour-expired
{
    display: inline-block;
}

.subNav-item
{
    height: 40px;
    border-right: none;
}

.subNavMenu-btn
{
    border: none;
    background-color: rgba(0, 0, 0, 0);
    color: #03a5c1;
    padding: 11px .57em 12px .57em;
}

.subNavMenu-link
{
    font-weight: 400!important;
}

img.thread-image.width--all-auto.height--all-auto.imgFrame-img.cept-thread-img
{
    max-width: 140px;
    margin-top: 0;
    max-height: 140px;
}

.threadGrid.space--t-2.space--b-2.space--h-2.space--fromW3-h-3.space--fromW3-t-3.space--fromW3-b-3
{
    position: relative;
    padding-right: 80px;
    margin-bottom: 15px;
    border: 1px solid #d1d5db;
    border-radius: 8px;
}

section.thread-list--type-list.width--all-12.thread-list--type-list--sideAds.cept-listing--list.tGrid.space--mb-4
{
    margin-top: 22px;
}

a.overflow--wrap-off.flex--auto-0
{
    position: absolute;
    left: 7.8em;
    transform: translateX(-50%);
    bottom: 25px;
}

.threadGrid-footerMeta span.hide--toW3
{
    display: none;
}

.threadGrid-title > div > div.flex.flex--align-c
{
    position: absolute;
    left: 7.8em;
    transform: translateX(-50%);
    bottom: 5px;
}

.vote-box
{
    position: absolute;
    right: 5px;
    bottom: 20px;
    width: 60px;
    height: 140px;
}

button.vote-up.vote-btn, span.ico.ico--reduce.ico--type-pin-grey.space--r-2.space--fromW3-r-1
{
    position: absolute;
    top: 0;
    right: 50%;
    z-index: 39;
    height: 33px;
    transform: translateX(50%);
}

span.vote-temp
{
    position: absolute;
    top: 50%;
    right: 50%;
    font-size: 20px;
    z-index: 39;
    transform: translateX(50%) translateY(-50%);
}

button.vote-down.vote-btn, button.space--l-2.border--l.border--color-borderGrey.clickable
{
    height: 33px;
    bottom: 0;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    border: none;
}

button.space--l-2.border--l.border--color-borderGrey.clickable
{
    padding: 0;
}

strong.thread-title.text--b.size--all-l.lineClamp--2
{
    margin-right: -80px;
}

.ico--type-plus-red:before, .ico--type-minus-blueTint:before, .ico--type-pin-grey:before
{
    transform: scale(2);
}

span.ico.ico--reduce.ico--type-pin-grey.space--r-2.space--fromW3-r-1
{
    top: 23px;
}

.threadGrid-headerMeta > div > div.vote-box.space--h-2.border.border--color-borderGrey.bRad--a > span.text--color-grey.size--all-s.space--r-2.cept-pinned-flag.hide--toW3
{
    visibility: hidden;
}

.threadGrid-title, .threadItem-title
{
    grid-column-start: 2;
    grid-column-end: 5;
    grid-row: 1;
    max-width: 100%;
    padding-top: 25px;
    padding-right: 90px;
}

.threadGrid-dealBtn, .threadItem-dealBtn
{
    grid-row: 1;
    position: absolute;
    right: 5px;
    top: -8px;
}

.threadGrid-body, .threadItem-body
{
    grid-column-start: 2;
    grid-column-end: 5;
    grid-row: 2;
    padding-top: 0.2em;
    font-size: 0.9em;
    color: #8899a6;
}

.cept-merchant-link-term
{
    width: auto;
    overflow: hidden;
    display: inline-block;
    position: relative;
    color: rgba(0, 0, 0, 0);
}

span.text--b.text--color-brandPrimary.cept-merchant-name
{
    display: block;
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
}

.thread-divider
{
    background: none;
    position: relative;
    padding-right: 8px;
}

.thread-divider:before
{
    width: 33px;
    height: 33px;
    content: "@";
    color: #05a5c0;
    font-weight: bolder;
}

.userHtml a.linkPlain, .userHtml a.thread-link
{
    color: #02a5c1;
}

.vote-temp.text--color-charcoal
{
    display: none;
}

.comments-item-inner div.space--t-3 > div
{
    float: right;
}

body > section > div > section > section > ul > li.tGrid-row
{
    display: table-row;
}

.threadGrid.threadItem
{
    position: relative;
}

.threadItem-footerMeta > div > a
{
    display: none;
}

.threadItem .vote-box
{
    bottom: 0;
}

.height--all-full
{
    height: auto;
}

#footer
{
    max-width: 100%;
}

.thread--expired span.text--color-grey.size--all-s.space--l-1.space--r-2.cept-show-expired-threads.hide--toW3, .thread--expired i.ico.ico--reduce.ico--type-clock-grey.size--all-l
{
    display: none;
}
}
