css:
@-moz-document domain("admin.staging.experticity.com"), domain("admin.experticity.com")
{
/*
default theme for code-mirror
http://ethanschoonover.com/default
*/

/*
default color palette
http://ethanschoonover.com/default/img/default-palette.png
*/

.default.base03 { color: #002b36 !important; }
.default.base02 { color: #073642 !important; }
.default.base01 { color: #586e75 !important; }
.default.base00 { color: #657b83 !important; }
.default.base0 { color: #839496 !important; }
.default.base1 { color: #93a1a1 !important; }
.default.base2 { color: #eee8d5 !important; }
.default.base3  { color: #fdf6e3 !important; }
.default.solar-yellow  { color: #b58900 !important; }
.default.solar-orange  { color: #cb4b16 !important; }
.default.solar-red { color: #dc322f !important; }
.default.solar-magenta { color: #d33682 !important; }
.default.solar-violet  { color: #6c71c4 !important; }
.default.solar-blue { color: #268bd2 !important; }
.default.solar-cyan { color: #2aa198 !important; }
.default.solar-green { color: #859900 !important; }

/* Color scheme for code-mirror */

.cm-s-default {
  line-height: 1.45em !important;
  color-profile: sRGB !important;
  rendering-intent: auto !important;
}
.cm-s-default {
  color: #839496 !important;
  background-color: #002b36 !important;
  text-shadow: #002b36 0 1px !important;
}
.cm-s-solarizexdXXXX.cm-s-light {
  background-color: #fdf6e3 !important;
  color: #657b83 !important;
  text-shadow: #eee8d5 0 1px !important;
}

.cm-s-default .CodeMirror-widget {
  text-shadow: none !important;
}

.cm-s-default .cm-header { color: #586e75 !important; }
.cm-s-default .cm-quote { color: #93a1a1 !important; }

.cm-s-default .cm-keyword { color: #cb4b16 !important; }
.cm-s-default .cm-atom { color: #d33682 !important; }
.cm-s-default .cm-number { color: #d33682 !important; }
.cm-s-default .cm-def { color: #2aa198 !important; }

.cm-s-default .cm-variable { color: #839496 !important; }
.cm-s-default .cm-variable-2 { color: #b58900 !important; }
.cm-s-default .cm-variable-3 { color: #6c71c4 !important; }

.cm-s-default .cm-property { color: #2aa198 !important; }
.cm-s-default .cm-operator { color: #6c71c4 !important; }

.cm-s-default .cm-comment {
    color: #586e75 !important;
    font-style: italic !important;
    text-align: right  !important;
    background-color: #073642 !important;
}

.cm-s-default .cm-string { color: #859900 !important; }
.cm-s-default .cm-string-2 { color: #b58900 !important; }

.cm-s-default .cm-meta { color: #859900 !important; }
.cm-s-default .cm-qualifier { color: #b58900 !important; }
.cm-s-default .cm-builtin { color: #d33682 !important; }
.cm-s-default .cm-bracket { color: #cb4b16 !important; }
.cm-s-default .CodeMirror-matchingbracket { color: #859900 !important; }
.cm-s-default .CodeMirror-nonmatchingbracket { color: #dc322f !important; }
.cm-s-default .cm-tag { color: #93a1a1 !important; }
.cm-s-default .cm-attribute { color: #2aa198 !important; }
.cm-s-default .cm-hr {
  color: transparent !important;
  border-top: 1px solid #586e75 !important;
  display: block !important;
}
.cm-s-default .cm-link { color: #93a1a1 !important; cursor: pointer !important; }
.cm-s-default .cm-special { color: #6c71c4 !important; }
.cm-s-default .cm-em {
  color: #999 !important;
  text-decoration: underline !important;
  text-decoration-style: dotted !important;
}
.cm-s-default .cm-strong { color: #eee !important; }
.cm-s-default .cm-error,
.cm-s-default .cm-invalidchar {
  color: #586e75 !important;
  border-bottom: 1px dotted #dc322f !important;
}

.cm-s-default div.CodeMirror-selected { background: #073642 !important; }
.cm-s-default.CodeMirror ::selection { background: rgba(7, 54, 66, 0.99) !important; }
.cm-s-default .CodeMirror-line::-moz-selection, .cm-s-dark .CodeMirror-line > span::-moz-selection, .cm-s-dark .CodeMirror-line > span > span::-moz-selection { background: rgba(7, 54, 66, 0.99) !important; }

.cm-s-solarizexdXXXX.cm-s-light div.CodeMirror-selected { background: #eee8d5 !important; }
.cm-s-solarizexdXXXX.cm-s-light .CodeMirror-line::selection, .cm-s-light .CodeMirror-line > span::selection, .cm-s-light .CodeMirror-line > span > span::selection { background: #eee8d5 !important; }
.cm-s-solarizexdXXXX.cm-s-light .CodeMirror-line::-moz-selection, .cm-s-ligh .CodeMirror-line > span::-moz-selection, .cm-s-ligh .CodeMirror-line > span > span::-moz-selection { background: #eee8d5 !important; }

/* Editor styling */



/* Little shadow on the view-port of the buffer view */
.cm-s-default.CodeMirror {
  -moz-box-shadow: inset 7px 0 12px -6px #000 !important;
  -webkit-box-shadow: inset 7px 0 12px -6px #000 !important;
  box-shadow: inset 7px 0 12px -6px #000 !important;
}

/* Remove gutter border */
.cm-s-default .CodeMirror-gutters {
  border-right: 0 !important;
}

/* Gutter colors and line number styling based of color scheme (dark / light) */

/* Dark */
.cm-s-default .CodeMirror-gutters {
  background-color: #073642 !important;
}

.cm-s-default .CodeMirror-linenumber {
  color: #586e75 !important;
  text-shadow: #021014 0 -1px !important;
}

/* Light */
.cm-s-solarizexdXXXX.cm-s-light .CodeMirror-gutters {
  background-color: #eee8d5 !important;
}

.cm-s-solarizexdXXXX.cm-s-light .CodeMirror-linenumber {
  color: #839496 !important;
}

/* Common */
.cm-s-default .CodeMirror-linenumber {
  padding: 0 5px !important;
}
.cm-s-default .CodeMirror-guttermarker-subtle { color: #586e75 !important; }
.cm-s-default .CodeMirror-guttermarker { color: #ddd !important; }
.cm-s-solarizexdXXXX.cm-s-light .CodeMirror-guttermarker { color: #cb4b16 !important; }

.cm-s-default .CodeMirror-gutter .CodeMirror-gutter-text {
  color: #586e75 !important;
}

.cm-s-default .CodeMirror-cursor {
    border-left: 2px solid #819090  !important;
}
/* Fat cursor */
.cm-s-solarizexdXXXX.cm-s-light.cm-fat-cursor .CodeMirror-cursor { background: #77ee77 !important; }
.cm-s-solarizexdXXXX.cm-s-light .cm-animate-fat-cursor { background-color: #77ee77 !important; }
.cm-s-default.cm-fat-cursor .CodeMirror-cursor { background: #586e75  !important; }
.cm-s-default .cm-animate-fat-cursor { background-color: #586e75  !important; }

/* Active line */
.cm-s-default .CodeMirror-activeline-background {
  background: rgba(255, 255, 255, 0.06) !important;
}
.cm-s-solarizexdXXXX.cm-s-light .CodeMirror-activeline-background {
  background: rgba(0, 0, 0, 0.06) !important;
}
}
