css:
@-moz-document domain("www.wanikani.com") {
:root {
  --dark: #424242;
  --background: #212121;
  --light: #616161;
  --lightest: #757575;
  --body: #FFFFFF;
  --enabled: #64FFDA;
  --disabled: #F44336;
  --radicals: #00A1F1;
  --kanji: #F100A1;
  --vocabulary: #A100F1; }

/** GLOBAL
*********************************** **/
.small-caps {
  color: inherit !important; }

.dashboard .lattice-single-character {
  background-image: none !important; }
  .dashboard .lattice-single-character a {
    box-shadow: none !important; }

.progress {
  background: var(--dark); }
  .progress .bar {
    background: var(--light) !important; }

[class*="-icon"] {
  box-shadow: none !important;
  border-radius: 2px; }
  [class*="-icon"].kanji-icon {
    background: var(--kanji); }
  [class*="-icon"].vocabulary-icon {
    background: var(--vocabulary); }
  [class*="-icon"].radical-icon {
    background: var(--radicals); }

/** BODY
*********************************** **/
body,
html {
  background: var(--background) !important;
  color: var(--body); }

h1,
h2,
h3,
h4,
h5,
h6 {
  color: inherit; }

* {
  text-shadow: none !important; }

/** FOOTER 
************************************ **/
footer #hotkeys {
  background: var(--light); }
  footer #hotkeys a {
    color: var(--body) !important; }
    footer #hotkeys a:hover {
      color: var(--body) !important; }

/** NAVBAR
************************************ **/
.navbar-inner {
  background: var(--dark) !important;
  box-shadow: none !important;
  border: none !important; }

.navbar-inner * {
  text-shadow: none !important; }

div.navbar-inner li:not(.title) a span,
div.navbar-inner li.title a span:first-child {
  border: none;
  border: 5px solid var(--light) !important;
  background-color: transparent !important;
  box-shadow: none !important;
  text-shadow: none !important; }

div.navbar:not(.navbar-fixed-top) div.navbar-inner li a span {
  width: 3.5em !important;
  height: 3.5em !important; }

div.navbar.navbar-fixed-top div.navbar-inner li.top {
  width: 40px !important; }

.nav li .dropdown-menu {
  background: var(--lightest) !important; }
  .nav li .dropdown-menu:before, .nav li .dropdown-menu:after {
    border-bottom-color: var(--lightest) !important; }
  .nav li .dropdown-menu a:hover {
    background: rgba(0, 0, 0, 0.1) !important; }

.nav li.dropdown.open.radicals .dropdown-toggle span {
  border-color: var(--radicals) !important; }

.nav li.dropdown.open.kanji .dropdown-toggle span {
  border-color: var(--kanji) !important; }

.nav li.dropdown.open.vocabulary .dropdown-toggle span {
  border-color: var(--vocabulary) !important; }

.nav li.dropdown.open .dropdown-toggle {
  box-shadow: none !important;
  background: var(--light) !important; }
  .nav li.dropdown.open .dropdown-toggle span {
    border-color: var(--lightest) !important; }

div.navbar-inner li:not(.radicals):not(.kanji):not(.vocabulary) a {
  color: var(--body) !important; }

div.navbar-inner li:not(.open):hover.radicals a {
  color: var(--radicals) !important; }

div.navbar-inner li:not(.open):hover.kanji a {
  color: var(--kanji) !important; }

div.navbar-inner li:not(.open):hover.vocabulary a {
  color: var(--vocabulary) !important; }

div.navbar-inner li:not(.title):not(.account) a span,
div.navbar-inner li:not(.title):not(.account) a span:hover {
  background-image: none !important; }

section.blog,
section.forum-topics-list {
  display: none !important; }

/** USER-INFO 
*********************************** **/
header div.user-info {
  background: var(--dark) !important;
  color: var(--body) !important;
  box-shadow: none !important; }
  header div.user-info div[class*=span] {
    color: #EEEEEE !important; }

.knowledge-distribution,
.wall-of-shame {
  background: var(--background) !important;
  color: var(--body); }
  .knowledge-distribution h3 span,
  .wall-of-shame h3 span {
    background: rgba(0, 0, 0, 0.1) !important;
    text-shadow: none !important;
    box-shadow: none !important; }
  .knowledge-distribution .progress,
  .wall-of-shame .progress {
    background: var(--dark) !important; }

/** REVIEW
*********************************** **/
#reviews-summary,
#lessons-summary {
  /** HEADERS **/
  /** LESSONS **/ }
  #reviews-summary header nav,
  #lessons-summary header nav {
    border: none !important; }
    #reviews-summary header nav #back-dashboard,
    #lessons-summary header nav #back-dashboard {
      border: none !important;
      box-shadow: none !important; }
    #reviews-summary header nav #review-queue-count,
    #lessons-summary header nav #review-queue-count {
      background: var(--dark) !important; }
  #reviews-summary #review-stats [id*="review-stats-"],
  #reviews-summary .pure-g-r div.pure-u-1,
  #lessons-summary #review-stats [id*="review-stats-"],
  #lessons-summary .pure-g-r div.pure-u-1 {
    box-shadow: 3px 3px 0 rgba(0, 0, 0, 0.1) !important;
    background: var(--dark) !important; }
  #reviews-summary #review-stats [id*="review-stats-"]:not(#review-stats-answered-correctly),
  #lessons-summary #review-stats [id*="review-stats-"]:not(#review-stats-answered-correctly) {
    background: var(--light) !important; }
  #reviews-summary #review-stats-answered-correctly:after,
  #lessons-summary #review-stats-answered-correctly:after {
    border-color: transparent transparent transparent var(--dark) !important; }
  #reviews-summary h3,
  #lessons-summary h3 {
    border-color: var(--light) !important; }
    #reviews-summary h3 span,
    #lessons-summary h3 span {
      background: var(--dark) !important;
      color: #FFFFFF; }
      #reviews-summary h3 span strong,
      #lessons-summary h3 span strong {
        background: var(--light) !important; }
  #reviews-summary #radicals > div > ul > li,
  #reviews-summary #kanji > div > ul > li,
  #reviews-summary #vocabulary > div > ul > li,
  #reviews-summary .burn > ul > li,
  #reviews-summary .enlighten > ul > li,
  #reviews-summary .master > ul > li,
  #reviews-summary .guru > ul > li,
  #reviews-summary .apprentice > ul > li,
  #reviews-summary .guru > ul > li,
  #lessons-summary #radicals > div > ul > li,
  #lessons-summary #kanji > div > ul > li,
  #lessons-summary #vocabulary > div > ul > li,
  #lessons-summary .burn > ul > li,
  #lessons-summary .enlighten > ul > li,
  #lessons-summary .master > ul > li,
  #lessons-summary .guru > ul > li,
  #lessons-summary .apprentice > ul > li,
  #lessons-summary .guru > ul > li {
    box-shadow: 2px 2px 0 rgba(0, 0, 0, 0.2) !important; }
  #reviews-summary #radicals h2,
  #reviews-summary #radicals > div > ul > li.vocabulary,
  #lessons-summary #radicals h2,
  #lessons-summary #radicals > div > ul > li.vocabulary {
    background: var(--radicals) !important; }
  #reviews-summary #vocabulary h2,
  #reviews-summary #vocabulary > div > ul > li.vocabulary,
  #lessons-summary #vocabulary h2,
  #lessons-summary #vocabulary > div > ul > li.vocabulary {
    background: var(--vocabulary) !important; }
  #reviews-summary #kanji h2,
  #reviews-summary #kanji > div > ul > li.kanji,
  #lessons-summary #kanji h2,
  #lessons-summary #kanji > div > ul > li.kanji {
    background: var(--kanji) !important; }

/** SESSION
*********************************** **/
#reviews #question #character,
#lessons #question #character {
  box-shadow: none !important; }
  #reviews #question #character.kanji,
  #lessons #question #character.kanji {
    background: var(--kanji) !important; }
  #reviews #question #character.vocabulary,
  #lessons #question #character.vocabulary {
    background: var(--vocabulary) !important; }
  #reviews #question #character.radical,
  #lessons #question #character.radical {
    background: var(--radical) !important; }

#reviews #question #question-type,
#lessons #question #question-type {
  box-shadow: none !important;
  background: var(--light) !important;
  border-color: var(--light) !important;
  color: var(--body) !important; }

#reviews #answer-form input[type=text] span,
#reviews #additional-content ul li span,
#lessons #answer-form input[type=text] span,
#lessons #additional-content ul li span {
  background: var(--light);
  color: var(--body) !important;
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.2); }
  #reviews #answer-form input[type=text] span:hover:before,
  #reviews #additional-content ul li span:hover:before,
  #lessons #answer-form input[type=text] span:hover:before,
  #lessons #additional-content ul li span:hover:before {
    background: var(--lightest) !important;
    color: var(--body) !important;
    box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.2) !important; }

#reviews #answer-form input[type=text].active:before,
#reviews #additional-content ul li.active:before,
#lessons #answer-form input[type=text].active:before,
#lessons #additional-content ul li.active:before {
  border-color: transparent transparent var(--lightest) transparent; }

#reviews #answer-form input[type=text].active span i,
#reviews #additional-content ul li.active span i,
#lessons #answer-form input[type=text].active span i,
#lessons #additional-content ul li.active span i {
  color: #FFFFFF !important; }

#reviews #answer-form button,
#lessons #answer-form button {
  background: var(--lightest);
  color: var(--body) !important; }

#reviews fieldset:not(.correct):not(.incorrect) input,
#lessons fieldset:not(.correct):not(.incorrect) input {
  background: var(--lightest) !important;
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.1);
  color: #FFFFFF !important; }

#reviews fieldset:not(.correct):not(.incorrect) button,
#lessons fieldset:not(.correct):not(.incorrect) button {
  background: rgba(0, 0, 0, 0.1) !important; }

#reviews fieldset.correct input,
#lessons fieldset.correct input {
  background: var(--enabled) !important;
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.1); }

#reviews fieldset.correct button,
#lessons fieldset.correct button {
  background: rgba(0, 0, 0, 0.2) !important; }

#reviews fieldset.incorrect input,
#lessons fieldset.incorrect input {
  background: var(--disabled) !important;
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.1); }

#reviews fieldset.incorrect button,
#lessons fieldset.incorrect button {
  background: rgba(0, 0, 0, 0.2) !important; }

#reviews #information,
#lessons #information {
  background: var(--lightest);
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.2) !important; }
  #reviews #information:before,
  #lessons #information:before {
    border-color: transparent transparent var(--light) transparent; }
  #reviews #information #item-info h2,
  #lessons #information #item-info h2 {
    color: #FFFFFF;
    border-bottom: 2px solid #FFFFFF;
    box-shadow: none !important; }
  #reviews #information #item-info #related-items ul li a,
  #lessons #information #item-info #related-items ul li a {
    color: #FFFFFF; }
  #reviews #information #item-info #related-items ul span,
  #lessons #information #item-info #related-items ul span {
    box-shadow: none !important; }
  #reviews #information #item-info #all-info,
  #lessons #information #item-info #all-info {
    background: var(--light);
    color: #FFFFFF; }
  #reviews #information .highlight-kanji,
  #lessons #information .highlight-kanji {
    background: var(--kanji); }
  #reviews #information .highlight-radical,
  #lessons #information .highlight-radical {
    background: var(--radicals); }
  #reviews #information .highlight-vocabulary,
  #lessons #information .highlight-vocabulary {
    background: var(--vocabulary); }

/** DASHBOARD
*********************************** **/
#main {
  /** CONTENT
    ************************************ **/
  /** STATS
    *********************************** **/
  /** SEARCH
    ************************************* **/
  /** PLUGINS
    ************************************ **/
  /** TIMELINE **/
  /** SELF-STUDY **/
  /** INFORMATION
    ************************************ **/
  /** LEVELS 
    *********************************** **/
  /**CHARACTERS 
    *********************************** **/
  /** PROGRESS
    *********************************** **/ }
  #main section#timeln,
  #main section.review-status,
  #main section.srs-progress,
  #main section.progression,
  #main section.dashboard-sub-section {
    padding: 1rem;
    margin: 1rem 0rem;
    border: none !important;
    border-radius: 0px;
    width: calc(100% - 2rem);
    background: var(--dark);
    color: var(--body) !important;
    box-shadow: 3px 3px 0 rgba(0, 0, 0, 0.1); }
    #main section#timeln *,
    #main section.review-status *,
    #main section.srs-progress *,
    #main section.progression *,
    #main section.dashboard-sub-section * {
      border: none; }
    #main section#timeln span,
    #main section.review-status span,
    #main section.srs-progress span,
    #main section.progression span,
    #main section.dashboard-sub-section span {
      color: inherit; }
    #main section#timeln #timeline,
    #main section.review-status #timeline,
    #main section.srs-progress #timeline,
    #main section.progression #timeline,
    #main section.dashboard-sub-section #timeline {
      width: 100%; }
  #main section.srs-progress ul * {
    background-image: none !important;
    border-radius: 0px;
    box-shadow: none; }
  #main #search #main-ico-search {
    color: #FFFFFF; }
  #main #search form .search-query {
    border-radius: 0px;
    background: var(--dark);
    border: none;
    box-shadow: 3px 3px 0 rgba(0, 0, 0, 0.1); }
  #main #timeln-graph .kan {
    fill: var(--kanji) !important; }
  #main #timeln-graph .voc {
    fill: var(--vocabulary) !important; }
  #main #timeln-graph .rad {
    fill: var(--radicals) !important; }
  #main #timeln .link,
  #main g.label-y text,
  #main g.label-x text {
    color: var(--body);
    filL: var(--body); }
  #main #timeline .grid .shadow {
    stroke: var(--body); }
  #main #timeline .arrows .cur {
    stroke: var(--body); }
  #main .selfstudy label {
    color: var(--body); }
  #main .selfstudy .btn-group .btn {
    border: none;
    box-shadow: none;
    color: var(--body);
    background: var(--dark);
    height: 30px; }
    #main .selfstudy .btn-group .btn.on {
      background: var(--enabled); }
  #main .selfstudy .btn-group select {
    border-radius: 0px;
    -webkit-appearance: none;
    outline: none; }
  #main .selfstudy .btn-group:last-child {
    margin: 0rem 1rem; }
  #main .dashboard-sub-section h3,
  #main .dashboard-sub-section div.see-more {
    background: var(--light);
    border: none;
    box-shadow: none;
    border-radius: 2px;
    margin: .2rem 0rem; }
  #main .none-available {
    background: var(--lightest); }
    #main .none-available td {
      padding: 2rem 0rem;
      color: var(--body); }
      #main .none-available td div {
        box-shadow: none; }
  #main .kotoba-table-list tr[id|=vocabulary] {
    background: var(--vocabulary); }
  #main .kotoba-table-list tr[id|=kanji] {
    background: var(--kanji); }
  #main .kotoba-table-list tr[id|=radical] {
    background: var(--radicals); }
  #main .additional-info,
  #main .page-list,
  #main section[id|=level] header {
    background: var(--dark) !important;
    color: var(--body) !important; }
    #main .additional-info *,
    #main .page-list *,
    #main section[id|=level] header * {
      border: none; }
    #main .additional-info ul li.page-list-header,
    #main .page-list ul li.page-list-header,
    #main section[id|=level] header ul li.page-list-header {
      display: none; }
    #main .additional-info ul li a,
    #main .page-list ul li a,
    #main section[id|=level] header ul li a {
      background: var(--light);
      color: var(--body); }
    #main .additional-info.additional-info.legend span,
    #main .page-list.additional-info.legend span,
    #main section[id|=level] header.additional-info.legend span {
      box-shadow: none; }
    #main .additional-info h2, #main .additional-info h3,
    #main .page-list h2,
    #main .page-list h3,
    #main section[id|=level] header h2,
    #main section[id|=level] header h3 {
      color: #FFFFFF; }
  #main ul.multi-character-grid li,
  #main ul.single-character-grid li {
    border: none; }
    #main ul.multi-character-grid li:hover,
    #main ul.single-character-grid li:hover {
      box-shadow: none; }
  #main ul.multi-character-grid li[id|=kanji],
  #main ul.single-character-grid li[id|=kanji] {
    background: var(--kanji); }
  #main ul.multi-character-grid li[id|=vocabulary],
  #main ul.single-character-grid li[id|=vocabulary] {
    background: var(--vocabulary); }
  #main ul.multi-character-grid li[id|=radical],
  #main ul.single-character-grid li[id|=radical] {
    background: var(--radicals); }
  #main section.progression {
    color: var(--body); }
    #main section.progression h3 {
      color: var(--body); }
    #main section.progression div .chart .progress {
      padding: .2rem;
      border-radius: 2px;
      background: var(--light) !important;
      box-shadow: none !important; }
      #main section.progression div .chart .progress .bar {
        border-radius: 2px;
        box-shadow: none !important;
        background: var(--lightest) !important; }
      #main section.progression div .chart .progress .threshold {
        color: var(--body); }
    #main section.progression .radicals-progress .chart .progress .bar {
      background: var(--radicals) !important; }
    #main section.progression .kanji-progress .chart .progress .bar {
      background: var(--kanji) !important; }
    #main section.progression .pct90 {
      background: var(--lightest); }

#loading,
#additional-content-load {
  background-color: var(--light) !important; }

#answer-exception span {
  background: var(--lightest);
  box-shadow: 3px 3px 0px rgba(0, 0, 0, 0.1); }
  #answer-exception span:before {
    border-color: transparent transparent var(--light) transparent; }

.popover {
  box-shadow: none !important;
  width: 300px; }

/** PLUGINS
******************************** **/
/** IGNORE ANSWER **/
#WKO_button {
  background: #F44336 !important; }

}
