css:
@-moz-document domain(www.citeclass.com)
{
  body
  {
    color: #6b6b6b;
    background: #0b141d;
  }

  html, body
  {
    color: #505050;
    background: #0b141d;
  }

  .block .content
  {
    font-family: "SassoonMontessori";
    background: #0b141d;
  }

  div.region-content .header, .header, div.region-content .header
  {
    background-color: #0b141d;
  }

  #page
  {
    background-color: #2b2b2b;
    border-radius: 10px;
  }

  #page-header
  {
    background: rgb(16, 29, 41);
    color: #003171;
  }

  #page-header a:link, #page-header a:visited
  {
    color: #003171;
  }

  .navbar
  {
    background: #2b2b2b;
    border: 1px solid #252525;
  }

  .navbar ul[role="navigation"] a
  {
    color: #505050;
  }

  #custommenu
  {
    background-color: rgb(16, 29, 41);
  }

  #custommenu .yui3-menu-horizontal .yui3-menu-content li a
  {
    color: #505050;
  }

  a:link, a:visited
  {
    color: #505050!important;
    border: none !important;
  }

  #region-post div.region-content div.current, #report-region-pre div.region-content div.current, #region-pre div.region-content div.current
  {
    background-color: #101d29;
  }

  div.region-content div.header-tab
  {
    background: none repeat scroll 0 0 #101d29;
    color: #505050;
    border-bottom: 1px solid #101d29;
  }

  .block .content a
  {
    color: #505050;
  }

  #report-region-pre div.region-content .header, #region-pre div.region-content .header, #region-post div.region-content .header
  {
    background-color: #0b141d;
  }

  element.style
  {
    background: #0b141d;
    border: 1px solid #0b141d;
  }

  #region-post div.region-content div.current h2, #report-region-pre div.region-content div.current h2, #region-pre div.region-content div.current h2
  {
    color: #505050;
  }

  #page-footer
  {
    background: #0b141d;
    color: #676767;
  }

  #page-footer a
  {
    color: #002350;
  }

  .course-content ul.gridicons li .image_holder
  {
    border-color: #2b2b2b!important;
    background-color: #2b2b2b!important;
  }

  .course-content ul.gridicons li p.icon_content
  {
    color: #505050!important;
  }

  input[type="text"]
  {
    background-color: #444444;
    border-color: #525252;
  }

  .course-content ul.gridicons li
  {
    border: 1px solid #505050!important;
  }

  input[type="submit"], button, .pretty-button, .delete-slide
  {
    color: #696969;
  }

  #region-main div.header-tab
  {
    background: none repeat scroll 0 0 #101d29;
    color: #505050;
    border-color: #101d29;
  }

  div#inst1829 p
  {
    color: #000000;
  }

  .coursebox
  {
    background-color: #0b141d;
  }

  div#region-main div.region-content div p
  {
    color: #505050!important;
  }

  .path-course-view .course-content .main
  {
    background: #2b2b2b;
  }

  #page-course-view-grid a:hover
  {
    color: #000000;
  }

  div#yui_3_9_1_2_1508778813606_347
  {
    background-color: #1b1b1b!important;
  }

  table.flexible .r0, table.generaltable .r0
  {
    background-color: #252525;
  }

  table.flexible .r1, table.generaltable .r1
  {
    background-color: #292929;
  }

  td.submissionnotgraded, div.submissionnotgraded
  {
    color: #505050;
    background-color: #292929;
  }

  td.submissionstatussubmitted, div.submissionstatussubmitted, a:link.submissionstatussubmitted
  {
    color: #505050;
    background-color: #2a402a8a;
  }

  span.instancename span
  {
    color: #100303!important;
  }

  div.region-content tr
  {
    background: #292929!important;
    color: #505050!important;
  }

  div.region-content h2
  {
    background: #2b2b2b!important;
    color: #505050!important;
  }

  div#tabs-pre.region-content h2
  {
    background: #101d29!important;
    color: #505050!important;
  }

  div#region-main div.region-content div div div.titgrade2
  {
    background: #0b131e!important;
    color: #505050!important;
  }

  div#region-main div.region-content div div div.titgrade2 a
  {
    color: #505050!important;
  }

  div#region-main div.region-content div div div.titgrade2 i a
  {
    color: #505050!important;
  }

  div#region-main div.region-content div div div
  {
    background: #1b1b1b!important;
    border: #333333!important;
  }

  div#region-main div.region-content div div div.titgrade2 strong span
  {
    color: #505050!important;
  }

  div#region-main div.region-content div div div hr
  {
    border-width: 1px;
    border-color: #353535;
  }

  div#region-main div.region-content div div div#gridmiddle-column
  {
    background: none!important;
    border: 20px auto!important;
  }

  div#region-main div.region-content div div div div#gridiconcontainer
  {
    background: none!important;
    border: 20px auto!important;
  }

  div#region-main div.region-content div div div#gridmiddle-column ul li div
  {
    background: none!important;
    border: 20px auto!important;
  }

  div#region-main div.region-content div div div.loginpanel
  {
    background: none!important;
  }

  div#region-main div.region-content div div div.loginpanel div
  {
    background: none!important;
  }

  div.region-content div.course_title h2
  {
    background: none!important;
  }

  div#region-main div.region-content div div.block_course_overview.block div
  {
    background: #0b141d!important;
  }

  div#region-main div.region-content div div.header-tab div.title
  {
    background: #101d29!important;
  }

  div#region-main div.region-content div div.header-tab div.title h2
  {
    background: none!important;
  }

  div.region-content div.header-tab.current div.title h2
  {
    background: none!important;
  }

  div.region-content div.header-tab div.title h2
  {
    background: none!important;
  }

  div.region-content div div table tbody tr
  {
    background: #0b141d!important;
  }

  div.region-content div div.course-content div#gridmiddle-column div.content table tbody tr td
  {
    background: #252525!important;
    border: 1px solid #0b131e!important;
  }

  div#region-main div.region-content div div div div.clearer
  {
    background: transparent!important;
  }

  div#region-main div.region-content div div div span
  {
    color: #6f6f6f!important;
  }

  div#region-main div.region-content div div div
  {
    color: #6f6f6f!important;
    background: none repeat scroll 0% 0% #2b2b2b!important;
    border: none!important;
  }

  div#region-main div.region-content div div ul li strong a
  {
    color: #505050!important;
  }

  div#region-main div.region-content div div.course-content {
    color: #505050!important;
    background: none!important;
  }

  div#region-main div.region-content div table tbody td tr div {
    color: #505050!important;
    background: none repeat scroll 0% 0% #292929!important;
  }
  .loginbox  {
    border: 1px solid #252525;
  }
  div.header-tab.current h2 {
    border-radius: inherit;
  }
  div.header-tab div.title {
    border-radius: inherit;
  }
  .navbutton input[type="submit"] {
    background-color: #404040;
  }
  div.logininfo {
    color: #676767;
  }
}
