css:
@-moz-document domain("web.wechat.com"), domain("wx.qq.com") {
body {
	background: url(https://picsum.photos/1440/900/?blur&random) no-repeat 50%;
	background-size: cover;
}
.contact_list .contact_title {
	border-right: 1px solid #292c33;
	border-bottom: 1px solid #292c33;
}
.contact_list .contact_title,
.chat_item.top,
.panel,
.box {
	background-color: rgba(0, 0, 0, 0.6);
}
.contact_list .contact_item,
.chat_item {
	border-right: 1px solid #292c33;
	background-color: rgba(0, 0, 0, 0.2);
}
.contact_list .active,
.contact_list .active .contact_item,
.chat_item.active {
	background-color: transparent;
	border-right: 0;
}
.search_bar .frm_search {
	background-color: rgba(99, 99, 99, 0.4);
}
.chat .box_ft .content .flex {
	background-color: rgba(99, 99, 99, 0.4);
	padding: 5px 15px;
	margin-left: 19px;
	border-radius: 5px;
	color: #fff;
}
.message .nickname {
	color: #fff;
}
.web_wechat_pic,
.web_wechat_profile_loc,
.web_wechat_screencut,
.web_wechat_face {
	filter: invert(100%);
}
.chat .box_ft {
	border-top: 0;
}
.box_hd .title .title_name {
	color: #fff;
}
.box_hd .title_wrap {
	background-color: transparent;
	margin: 0;
	border-bottom: 0;
	color: #fff;
}
.mmpop {
	background-color: transparent;
}
.members {
	background-color: rgba(99, 99, 99, 0.8);
	border-bottom: 0;
}
.member .nickname {
	color: #fff;
}
.members_wrp {
	box-shadow: none;
	-webkit-box-shadow: none;
}
.profile_mini_bd {
	background-color: #ffffff;
}
.profile .nickname {
	color: #ffffff;
}
.profile .meta_item .label,
.profile .meta_item .value,
.profile .signature {
	color: #bbb;
}
}
