css:
@-moz-document url-prefix("https://profitmaximiser.co.uk/mastermind/bookieblowout/") {
/* Alternative Scroll Bar Slider Color */
::-webkit-scrollbar-thumb
{
  background: #d0c058 !important;
}

/* Main Content */
#main_con_txt_c {    
    background-color: #191919 !important;
}

/* Site Menu */
#bottom_site_menu_wrap {
    background: none !important;
    background-color: #191919 !important;
}


/* Calculator Inputs */
#scontentr_inner table.calculator_table tbody tr td.input_area input.bet_amount {
    background-color: #272727 !important;
    color: #fff !important;
}
#scontentr_inner input {
    background: none repeat scroll 0 0 hsl(0, 0%, 15%);
    background-color: #272727 !important;
    color: #fff !important;

}

/* Calculator On Open Bet Page */
body > table {
    background-color: #191919 !important;
}
/* Calculator On Open Bet Page > Inputs */
#lodds {
    background-color: #272727 !important;
    color: white !important;
}
#bodds {
    background-color: #272727 !important;
    color: white !important;
}
#amount {
    background-color: #272727 !important;
    color: white !important;
}



/* Mail Page */

/* Header */
#scontentr_inner h1 {
    color: #ffffff !important;
}

/* Table Header */ 
table.listing th {
    background: #191919 !important;
    color: #fff !important;
}

/* Table Stripe */ 
table.listing .bg td {
    background: #272727 !important;
    color: #fff !important; 
}
table.listing td {
    background: #191919 !important;
    color: #fff !important;
}

/* Hide Facebook Icon */ 
a#social_but_f:link, a#social_but_f:visited, a#social_but_f:active, a#social_but_f:hover {
    display: none;
}

/* Copyright Text Color */
.copy {
    color: white;
}



}
