css:
@-moz-document url-prefix("https://profitmaximiser.co.uk/mastermind/eachwaysniper") {
/* Main Menu Hover Color */
.menu ul li:hover, .menu ul li.active {
    background: #ff9517 !important;
}
/* Sub Menu Hover Color */
.menu ul li:hover .sub_menu li:hover a {
     background: #ff9517 !important; 
}
/* Main Menu Borders */
.menu ul li {
    border-left: none !important;
    border-right: none !important;
}

.breadcrumb_menu a.active, .breadcrumb_menu a:hover {
    background-color: #ff9517 !important;
    color: #272727 !important ;
}

/* Alternative Scroll Bar Slider Color */
::-webkit-scrollbar-thumb
{
  background: #ff9517 !important;
}

/* Main Text Coloring */
body { 
    color: #fff ;
}
.title h1 {
    color: #ff940e !important;
}

/* Golden Parachute Header */
#header {
    background-color: #ff940e !important;
}

/* Software Page Selection Coloring */ 
.pagination ul > li > a, .pagination ul > li > span {
    background-color: #272727!important;
}

/* Active page number color on software page selection */
.pagination ul > .active > a, .pagination ul > .active > span {
    color: #ff9517 !important;
}


/* Clear Filter Button Background Coloring */
.top_action button.win-command {
    background: #ff9517 !important;
}

/* Clear filter buttons text coloring */
#clearBasicFilter > span.win-label {
    color: #ffffff!important;
}

#clearAllFilters > span.win-label {
    color: #ffffff!important;
}

/* Todays Extra Places Message Color */ 
.today_message {
    color: #ffffff !important;
}

/* Todays Extra Places Message Content Color */ 
#message {
    color: #ff9517 !important;
}

/* Filter Buttons Hover Coloring */
button.win-command:hover {
    color: #424242 !important;
}
/* Filter Buttons Extra Places Selected Coloring */
button.win-command {
    background: #ff9517 !important;
}

/* Software Table Head Coloring */
.table-bordered th {
    background: #ff9517 !important;
    color: #fff !important;
}

/* Software Rating Badge Coloring */
.badge.badge-important {
    background: #ff9517!important;
    color: #fff !important;
}


/* Pop up calc Background Coloring */
#modals .modal {
    background: #191919 !important;
}

/* Software Bookie Select Drop Down List Coloring */
select {
    color: #ffffff !important;
    background-color: #272727 !important;
}

/* Pop up calc use free bet button */
#modals .right_right button.btn {
    background: #ff9517 !important;
    color: #fff !important;
}

/* Pop Up Border Coloring */
.modal-right .right_center {
    border-left: 1px solid #ff9517 !important;
    border-right: 1px solid #ff9517 !important;
}

/* Pop Up Calc Slider Coloring */
.dynamic-slider-control {
    color: #ffffff !important;
    background-color: #272727 !important;
}


/* Pop Up Calc Market Details Heading Color */
#modals > div h3#myModalLabel {
    color: #ffffff !important;
}

/* Pop Up Calc Main Text Coloring */
.tableUI td {
    color: #ffffff !important;
}


/* Table Stripe Effect Coloring */ 
tbody tr:nth-child(even) {
    background: #191919 !important;
}
tbody tr:nth-child(odd) {
    background: #272727 !important;
}


/* Pop up Calc Input Coloring */ 
.modal-right .right_center > input {
    margin: 5px 0;
    background-color: #272727;
}
input[type="text"] {
    background-color: #272727!important;
}

/* Filter Pop Up Coloring */
#basicModal, #additionalModal {
    background: #191919 !important;
}

/* Filter Pop Up Header Coloring */
#basicModal h3 {
    color: #fff !important;
}
/* Filter Pop Up Checkbox Text Color */
label.checkbox span {
    color: #fff !important;
}
/* Filter Pop Up Smaller Checkbox Text Coloring */
label.radio span {
    color: #fff !important;
}

/* Filter Pop Up Input Background Color */
input[type="number"] {
    background-color: #272727 !important;
}

/* Filter Pop Up Input Slider Left/Right Background Color */
#basicModal #slider {
    background: #272727 !important;
}
#basicModal #slider .ui-slider .ui-slider-range {
    background: #272727 !important;
}

/* Filter Pop Up Input Slider Middle Selection Background Color */
.ui-slider .ui-slider-range {
    background: #fc9818 !important;
}

/* Slider buttons coloring */
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
    background: #191919 !important;
}
/* Small dashes under slider color */
.ui-slider-pips .ui-slider-line {
    background: #fc9818 !important;
}
/* Slider time text coloring */
.ui-slider-pips .ui-slider-pip {
    color: #ffffff !important;
}

/* input box coloring */
input[type="text"] {
    background-color: #1b1b1b!important;
}

/* user notes box coloring */ 
textarea {
    background-color: #272727;
}

/* Dutching software pop-ups */
#dialog {
    background: #272727;
}

#dialog3 {
    background: #272727;
}

/* Divider Color */
.divider_grey {
    background: #ff9517;
}

/* FAQ Page FAQ Title Color */
.faq_title {
    color: #ff9517;
}

/* Products Page Title Colors */
.product_title {
   color: #ff9517;
}

/* Heading 2 color (Effects Non Runner Page ) */
h2 {
    color: #ff9517!important;
}

/* Remove Divider Line Non Runner Page & Color Location Header Tag */
.runners h1 {
    background: none !important;
    color: #ff9517;
}

button.dt-button, div.dt-button, a.dt-button {
    background-image: linear-gradient(to bottom, #232322 0%, #232322 100%)!important;
}
}

@-moz-document url("https://profitmaximiser.co.uk/mastermind/eachwaysniper/user/ep_dutching2"), url("https://profitmaximiser.co.uk/mastermind/eachwaysniper/user/dutching_bounty"), url-prefix("https://profitmaximiser.co.uk/mastermind/eachwaysniper/user/golden_parachute_calc?event=") {
/* DUTCHING SOFTWARE */


#header {
    background-color: #272727 !important;
}

.btn {
    background: #191919;
    color: #ff9517;
}

.input-small {
    background-color: #191919 !important;
}

#open_select  {
    background-color: #191919 !important ;
}

#type_select {
    background-color: #191919 !important ;
}

.dd .ddTitle {
    color: #000;
    background: none !important;
}


                                         
}

@-moz-document url("https://profitmaximiser.co.uk/mastermind/eachwaysniper/user/software_log") {
button.dt-button, div.dt-button, a.dt-button {
     background-image: linear-gradient(to bottom, #222 0%, #282828 100%);
}
}
