css:
/* i really want this to be global */

* {
    image-rendering: auto;
	}

#ads_left {
    display: none;
	}

.left_menu_nav_wrap {
    background: rgba(255,255,255,.7);
    border-radius: 2px;
	box-shadow: 0 1px 0 0 #88a, 0 0 0 1px #bbb, 0 15px 10px -10px rgba(0, 0, 0, 0.5), 0 1px 4px rgba(0, 0, 0, 0.3);
    padding: 8px 15px;
	}

.im-page--title-main-inner {
    color: #eee
	}

.im-chat-input, .im-page--title-wrapper, .im-page--dialogs-footer {
    background: #333;
	}

.left_menu_nav {
	color: #333
	}

#page_header_cont .back {
    background: #000;
	-webkit-box-shadow: 0px 0px 10px rgba(0,0,0,.8);
	-moz-box-shadow: 0px 0px 10px rgba(0,0,0,.8);
	box-shadow: 0px 0px 10px rgba(0,0,0,.8);
	}

body {
	background: url('http://gabclan.ga/img/1.png');
    background-size: 300px;
    image-rendering: pixelated;
	}

#side_bar ol {
    background: #fff;
    border-radius: 2px;
	box-shadow: 0 1px 0 0 #d7d8db, 0 0 0 1px #e3e4e8;
    padding: 8px 15px;
	}

.page_block, #side_bar ol, #hat_onl_wrap {
    box-shadow: 0 15px 10px -10px rgba(0, 0, 0, 0.5), 0 1px 4px rgba(0, 0, 0, 0.3);
	}
