css:
@-moz-document domain("courses.softlab.ntua.gr")
{
/* Main body text characteristics */

BODY {
	font-family: Georgia, "Times New Roman", Times, serif;
	background-color: Black;
	color: Grey;
}


/* Links */

A:link, A:visited {
	color: #046f06;
	text-decoration: underline;
}

A.external, A.external:link, A.external:visited {
	color: #046f06;
}

A.internal, A.internal:link, A.internal:visited {
	color: #046f06;
}

A.toplink, A.toplink:link, A.toplink:visited {
	color: #046f06;
}

A.addresslink, A.addresslink:link, A.addresslink:visited {
	color: #046f06;
}

A.inlineLink, A.inlineLink:link, A.inlineLink:visited {
	color: #046f06;
	font-family: Courier New, Courier, mono;
	font-style: normal;
}

NOSCRIPT.inlineLink {
	color: #046f06;
	font-family: Courier New, Courier, mono;
	font-style: normal;
}


/* Headers */

H1 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	margin-bottom: auto;
	font-size: large;
	color: Grey;
	font-weight: bold;
	text-align: center;
}

H1.courseTitle {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	margin-bottom: auto;
	font-size: x-large;
	color: Grey;
	font-weight: bold;
	text-align: center;
}

H2 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: medium;
	color: Grey;
	font-weight: bold;
}

H3, H4, H5, H6 {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: medium;
	color: Grey;
	font-weight: normal;
}

H3.lecture {
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-size: medium;
	color: Grey;
	font-weight: normal;
}


/* Institution, school, department, course title, etc. */

P.courseInstitution {
	color: Grey;
	font-family: Verdana, Arial, Helvetica, sans-serif;
}

P.courseTitle {
	color: Grey;
	font-family: Verdana, Arial, Helvetica, sans-serif;
	font-weight: bold;
	margin-bottom: 0;
}

P.courseSeparator {
	background-image: url(https://courses.softlab.ntua.gr/pl2/2017b/Images/Dividers/blue-simple.gif);
	background-position: center;
	background-repeat: repeat-x;
	margin-bottom: 0;
}

P.simpleSeparator {
	background-image: url(https://courses.softlab.ntua.gr/pl2/2017b/Images/Dividers/blue-simple.gif);
	background-position: center;
	background-repeat: repeat-x;
	margin-bottom: 0;
}



/* Other tags */

UL LI {
	list-style-type: circle;
}

P {		/* necessary for Netscape */
	font-family: Georgia, "Times New Roman", Times, serif;
	color: Grey;
}

TD {		/* necessary for Netscape */
	font-family: Georgia, "Times New Roman", Times, serif;
	color: Grey;
}

TH {
	font-family: Georgia, "Times New Roman", Times, serif;		/* necessary for Netscape */
	color: Grey;		/* necessary for Netscape */
	font-weight: bold;
	text-align: left;
}

TH.header {
	font-family: Georgia, "Times New Roman", Times, serif;
	color: Grey;
	font-weight: bold;
	text-align: left;
	background-color: #333399;
}

ADDRESS {	/* necessary for Netscape */
	font-family: Georgia, "Times New Roman", Times, serif;
	color: Grey;
}

tr { background-color:transparent;}
}
