@-moz-document domain(okoun.cz) {
    /* */
      /* Puvodne skin pro novy Okoun po vzoru stareho stylu Standard */
    /* */
    /* */
    /* */
    /* Primarne pro Firefox, nejak funguje i v Chrome, ale neni to uplne idealni. Chrome nepouzivam = neresim, Lucifer */
    /* */
    /* */
    /* */
    /* 0.1 - prvni stazitelna verze
    /* 0.2 - doplnena zmena barvy moderatorskych odkazu v hlavicce a barvy oznameni o novem vzkazu */
    /* 0.3 - podpora site rules pro Firefox */
    /* 0.4 - Upraveno odsazeni prvku na strance tak, jak bylo na starem Okounu */
    /* 0.5 - Uprava sirky prispevku (Kdokoliv) */
    /* 0.6 - Vylepseni upravy sirky prispevku a drobne zmeny zahlavi (Kdokoliv) */
    /* 0.7 - Zlepseni chovani infoboxu a okna Prispet (Trofo) */
    /* 0.8 - Zmena barev odpovidacich odkazu pri hover nad prispevkem (Trofo) */
    /* 0.9 - Doplnena moznost trvale zobrazeneho okna pro psani prispevku - pro aktivovani smazte dvojita lomitka na zacatku poslednich tri radku (Trofo) */
    /* 0.91 - Hrabe se v tom Lucifer, jen jemne ladi nektere drobnosti */
    /* 0.92 - Hrabe se v tom furt Lucifer, uz to neni zlute */
    /* 0.93 - a uzto neni ani  svetle - inverzni design menu a pozadi */
    /* 0.94 - hotfixy pro Chromeho, mensi upravy. Hotfixy pro Chrome pripadne dle radku 442 a 503 */

    
    html, body, div#hasad-bs {
        background-color: #898989 !important;
    }
    
    #body {
       margin: 0 !important;
       padding: 0 !important;
    }
    /* Zuzeni a zmenseni infoboxu, roztazeni panelu prispevku, osetreni sirky prispevku ve Vzkazniku a zuzeni okna Prispet */
    
    .main .yui-g .yui-u.yui-ge.first.main {
        float: none ! important;
        width: 100% ! important;
    }
    .main .yui-g .yui-u.yui-ge.first.main .yui-u.first,
    .main .yui-g .yui-u.yui-ge.first.main .welcome.exp,
    .main .yui-g .yui-u.yui-ge.first.main .col,
    .main .yui-g .yui-u.yui-ge.first.main .listing {
        float: none ! important;
        width: 84% ! important;
    }
    .main .yui-g .yui-u.yui-ge.first.main .listing {
        width: 100% ! important;
        clear: right ! important;
        position: relative !important;
        z-index: 1 !important;
    }
    .main .ctx,
    .yui-u .ctx {
        width: 13% !important;
        font-size: 0.7em !important;
        margin-top: 5em !important;
        position: relative !important;
        z-index: 2 !important;
    }
    .exp .content.post {
        width: 86% !important;
    }
    /* Barva a styl moderatorskych odkazu v zahlavi */
    
    .settings,
    .settings a,
    .settings a:visited {
        color: #0000aa !important;
        background-color: #eeeeee !important;
        font-weight: bold !important;
        font-size: 8pt !important;
        text-align: left !important;
        float: none !important;
        padding: 3px !important;
        border: 1px solid grey !important;
        width: 150px !important;
    }
    /* Oprava pozice tlacitka pro mazani prispevku a odesilani zmen v moderatorskem panelu */
    
    .delete,
    .yui-button.top {
        top: 0em !important;
        margin-top: 0em !important;
    }
    /* Vetsi odsazeni prvku na strance */
    
    body,
    dl,
    dt,
    dd,
    ul,
    ol,
    li,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    pre,
    code,
    form,
    fieldset,
    legend,
    input,
    textarea,
    p,
    blockquote,
    th,
    td {
        margin-top: 0.10em !important;
    }
    /* Barva beznych odkazu */
    
    .head .menu a,
    .footer a,
    .item.hover .actions span.prev a.prev .head .menu a:visited {
        color: #0000aa !important;
    }
    .head .user a,
    .head .user a:visited {
        color: #0000aa !important;
    }
    /* Barva odpovidaciho boxu pri hover nad prispevkem a nad konkretnim odkazem */
    
    .actions .prev a,
    .actions .prev,
    .actions .reply {
        color: #777777 !important;
    }
    .actions a.prev:hover,
    .actions a.reply:hover,
    .actions .prev a:hover {
        color: #000000 !important;
    }
    /* Barva pro hover nad odkazy v zahlavi */
    
    .head h1 a:hover,
    .head .menu a:hover,
    .head .user a:hover {
        background-color: #ffffff !important;
    }
    a:hover {
        background-color: #ffffff !important;
    }
    /* Logo Okouna - umisteni a styl */
    
    .head h1 a {
        padding-left: 0em !important;
        margin-left: 1.5em !important;
        font-size: 1.1em !important;
    }
    /* Barva pozadi pro hover na logu Okouna */
    /* Zmena barev prispivaciho a nahledoveho okna */
    
    .item .actions.replyForm a.reply,
    .expbutton .post.content,
    .actions.replyForm .post.content {
        border-top-color: #cccccc !important;
        border-bottom-color: #cccccc !important;
        border-left-color: #cccccc !important;
        border-right-color: #cccccc !important;
        background-color: #cccccc !important;
    }
    .preview .item {
        background-color: #fafadf !important;
    }
    
    
    /* Nove prispevky */
    
    .item {
        background-image: url(http://www.okoun.peklo.biz/bg20.png) !important;
        background-size: 100% 100% !important;
        color: #000000 !important;
    }
    

    .room .item.new.o-first {
        margin-top: 6px !important;
    
    }
    /* Zmena barev tlacitka Prispet */
    
    .expbutton .hd a {
        border: 1px solid #999999 !important;
        background-color: #aaaaaa !important;
        background-image: url(http://www.okoun.peklo.biz/bg23.png) !important;
        background-size: 100% 100% !important;
        background-repeat: repeat-x !important;
        padding-bottom: 2px !important;
        margin-bottom: 0px !important;
        font-family: verdana !important;
    }
    .expbutton .hd {
        background-color: #cccccc !important;
        border-top-color: #cccccc !important;
        border-bottom-color: #cccccc !important;
        border-left-color: #cccccc !important;
        border-right-color: #cccccc !important;
    }
    /* Ikona - zvetseni ikony vpravo nahore, ramecek a vycentrovani ikony v ikonboxu */
    /* Nefunguje vertical-align, proto ma samotna ikona nastaveny margin 6px */
    
    .user img {
        max-height: 56px !important;
        max-width: 40px !important;
        margin-top: 2px !important;
    }
    .user.ico,
    .user .ico,
    .item .replyForm .post.content .user.ico,
    .item .replyForm .post.content .user .ico {
        width: 52px !important;
        height: 62px !important;
    }
    .user.ico,
    .user .ico {
        border-top-style: solid !important;
        border-bottom-style: solid !important;
        border-left-style: solid !important;
        border-right-style: solid !important;
        border-top-color: #000000 !important;
        border-bottom-color: #000000 !important;
        border-left-color: #000000 !important;
        border-right-color: #000000 !important;
        background-color: #ffffff !important;
    }
    /* Pozice ikonboxu na radku */
    
    .item .ico {
        margin-left: 6px !important;
        margin-top: 6px !important;
    }
    /* Radkovani */
    
    body,
    .reset-f {
        line-height: 1.14 !important;
    }
    /* Barvy a odsazeni systemovych hlasek (login, logoff) */
    
    #sysmsgBox.messages,
