css:
@-moz-document domain("rapetontv.com") {
#wrapperMain {
display: flex;
min-height: 100vh;
flex-direction: column;
background-color: black;
}
#main {
background: rgb(250, 250, 250);
background: rgba(250, 250, 250, 1);
background-color: black;
color: white;
}
#primary .widget-container {
    margin: 0 0 10px 0;
    background: rgb(245, 245, 245);
    background: rgba(245, 245, 245, 1);
    border: 0px solid #d7d7d7;
    padding: 6px;
    display: block;
    background-color: black; 
}
.colrH {
    font-family: 'Lato', sans-serif;
    color: #9a9a9a !important;
}
.colr {
    color: #cccccc !important;
}
h2.video_header, .video_wrapper h2 {
 box-shadow: inset 0 0 20px 20px black;
}
.downarrowdiv {
box-shadow: inset 0px 1px 20px 20px #000000;
}
#mainmenu {
background: #f5f5f5 -webkit-linear-gradient(top, #af5100 0%,#000000 100%) !important;
}
#primary .sidebar-wrap h3.widget-title {
background: #000000 -webkit-linear-gradient(top, #251000 0%,#3e1d00 100%) !important;
}
.hentry {
margin: 0px 0 10px 0;
background-color: black;
}      
.getintouch tr td {
    text-align: left;
    padding: 6px 10px;
    background-color: black;
    color: white;
}
.contact .getintouch input.bar {
   
    color: #ffb302;
    background-color: #0e0701;
}
#header .widget_search input[type="text"] {
color: #fff;
background: #863e00;
border: 1px solid #4f2500;
}
.side_cat_border {
    border-bottom: #8f4200 dotted 1px;
}
.thumb_img_custom {
    border: #863e00 solid 2px !important;
    
}div.downarrowdiv {
    border-color: #863e00;
}
input.vidsearch {
    border-color: #863e00;
}
#mainmenu {
    border-color: #863e00;
}
h2.video_header {
    border-color: #863e00;
}

.footer-nav {
    
    background: linear-gradient(to bottom, #210f00 5%, #000000 100%);

}

#navigation li a {
   background: #1c0d00;
    color: #c57529 !important;
    }
div.getintouch h2 {
    border-color: #863e00;
}
.contact .getintouch input.bar {
border: #863e00 solid 1px;
}
.videotabs .tabcont {
background: #000000 -webkit-linear-gradient(top, #000000 0%,#000000 100%) !important;
}
.videotabs .tabcont input.chain {
    border: #863e00 solid 1px;
}
.comments textarea {
    background: #020202 !important;
    
    border: #863e00 solid 1px;
    
    color: #fffbfb;
}
.myButton {
        box-shadow: inset 0px 1px 0px 0px #5d2c01;
    background: linear-gradient(to bottom, #6c3200 5%, #200e00 100%);
    border: 1px solid #c57429;
    color: #9e9e9e;
}
ul.jca {
    border: #863e01 solid 1px !important;
}
tbody tr td {
    background-color: #251000;
}
.comments ul.commentslist li.level1 {
    background: #000000 !important;
    border: #863e01 solid 1px;
    box-shadow: 0 0 9px #863e01;
}
p.sel_error {
    color: #dbdbdb;
}
#content span.entry-date, #content ul li.entry-date {
   color: white;
   background: #3e1d00;
}
.getintouch table {
border: 1px solid #863e01;
}
body, input, textarea {
color: #ffb300;
background-color: #0d0600;
}
tr td input {
border-color: #863e01;
}
div.vidsearch_result {
background-color: #200e00 !important;
}  
a img {
backface-visibility: hidden;
background: #863e00;
box-shadow: inset -20px 20px 0px 11px black;
} 
}
