*	@version: 1.0.1
*/
body {
    color: darkgrey;
    background-color: black;
}
#wdp-menu-container,
.wdp-header {
    background-color: rgb(135, 0, 0);
}
.wdp-nav-workspace.wdp-active {
    background-color: rgb(50, 0, 0);
}
.wdp-active .wdp-menu-button,
.wdp-menu li:hover,
.signin-signincommand:hover,
.wdp-nav-workspace:hover,
.wdp-header button:hover {
    background-color: rgb(65, 0, 0);
}
#wdp-nav,
#wdp-powermenu-container,
.wdp-menu ul {
    background-color: rgb(100, 0, 0);
}
wdp-content-wrapper {
    background-color: black;
    font-family: ubuntu sans-serif;
}
.dock-panel {
    background-color: rgb(20, 20, 20);
}
.dock-divider {
    border: 2px solid rgb(0, 0, 0);
}
}
