css:
@-moz-document regexp("https://forum.yggtorrent\\.com/.*")
{
 #content
  {
    padding: 8px;
    line-height: 120%;
    background: #5a5a5a;
    -webkit-border-radius: 4px;
    border-radius: 4px;
  }
    .content-box, .content-box-large {
	margin-bottom: 30px;
	background: #555555;
	border-radius: 10px;
	color: black;
	padding: 10px;
}

  .border, .statistics, .post_block, .ipsComment, .popupInner, .no_messages, .general_box, .ipsSideBlock, .poll_question ol, .ipsBox_container, .ipsFloatingAction, .column_view .post_body
  {
    background: #2e2e2e;
  }

  body
  {
    color: #fff;
    font: normal 11px tahoma,helvetica,arial,sans-serif;
    position: relative;
  }

  body
  {
    color: #fff;
    font: normal 11px tahoma,helvetica,arial,sans-serif;
  }

  ul.post_controls
  {
    margin: 0;
    -webkit-box-shadow: inset rgba(0,0,0,0.1) 0px 0px 0px 1px;
    box-shadow: inset rgba(0,0,0,0.1) 0px 0px 0px 1px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    margin-top: 10px;
    clear: both;
    padding: 1px;
  }
 div.bbc_spoiler_content {
	background: #424242;
	border: 1px dashed #e3e3e3;
	padding: 5px;
	margin-top: 5px;
}

  #shoutbox-shouts-table .row2 td
  {
    border-bottom: 1px solid #585858;
    vertical-align: middle;
    background: #424242;
  }

  .ipsBox
  {
    background: #424242;
  }

  body
  {
    font-family: 'Helvetica Neue',Arial,Verdana,sans-serif;
    font-size: 14px;
    color: #fff;
    background-color: #2e2e2e;
  }

  .desc, .desc.blend_links a, p.posted_info
  {
    font-size: 11px;
    color: #fff;
  }
  .ipsLikeBar_info
  {
    line-height: 26px;
    background: #585858;
  }

  #recentajaxcontent li, #idm_categories a, #idm_categories li.with_sub.open li, #index_stats .status_list li, #panel_files .file_listing li, #panel_screenshots #ss_linked li, .file_listing, #cart_totals td, div#member_filters li, #files li, .ipsType_sectiontitle, #order_review td, #package_details .package_info, .block_list li, .package_view_top, .member_entry, #help_topics li, .ipsBox_container .ipsType_pagetitle, .userpopup dl, #announcements td, .sideVerticalList li, fieldset.with_subhead ul, .ipsList_data li, .ipsList_withminiphoto li, .ipsList_withmediumphoto li, table.ipb_table td, .store_categories li, #mini_cart li, #index_stats div[id*="statusReply"], #ipg_category .ipg_category_row, .block_inner .ipb_table td, .gallery_pane h2, .status_feedback li[id*="statusReply"], .ipsSideMenu ul li, #usercp_content .ipsType_subtitle, .ipbfs_login_row, .articles .block-1, .articles .type-1x2x2 .article_row, #idm_category .idm_category_row, #category_list li a, .ipsComment, #forum_table.ipb_table tr:last-of-type td
  {
    border-bottom: 1px solid #5a5a5a;
  }

  #search_options_menucontent label
  {
    cursor: pointer;
    display: block;
    padding: 0 6px;
    color: #5a5a5a;
  }

  blockquote.ipsBlockquote
  {
    font-size: 12px;
    padding: 10px;
    border: 1px solid #e3e3e3;
    border-top: 1px solid #e8e8e8;
    background: #5a5a5a;
    color: #9f9f9f;
    margin-bottom: 5px;
    margin: 0;
    overflow-x: auto;
  }
ul.post_controls {
	margin: 0;
	background: #6E6E6E;
}
    ul.post_controls a, ul.post_controls a.ipsButton_secondary {
	color: #fff;
	text-shadow: rgba(255,255,255,0.5) 0px 1px 0px;
	border: 0;
	height: 34px;
	line-height: 34px;
	padding: 0 10px;
	text-decoration: none;
	display: block;
}
  .row1, .post_block.row1 {
	background-color: #525252;
}
  .row2, .post_block.row2 {
	background-color: #1b1b1b;
}
    .poll_question h4 {
	background: #1b1b1b;
	padding: 5px;
	-webkit-border-radius: 0px;
	-moz-border-radius: 0px;
	border-radius: 0px;
}
  .ipsHeaderMenu {
	background: #1c1c1c;
	padding: 10px;
	border-radius: 0 0 6px 6px;
	overflow: hidden;
	width: 340px;
}
  #search_options_menucontent li {
	white-space: nowrap;
	background-color: #1c1c1c;
}
  .ipbfs_titlebox {
	background: #2e2e2e url('https://yggtorrent.com/forum/public/style_images/yggv2/highlight_strong.png') repeat-x 0 -1px;
	border: 1px solid #2e2e2e;
	color: #1c1c1c;
	text-shadow: #2a2a2a 0px 1px 0px;
	box-shadow: rgba(0,0,0,0.15) 0px 1px 3px;
	padding: 13px;
	overflow: hidden;
}
  .ipsType_pagetitle, .ipsType_subtitle {
	font: 300 22px/1.4 Helvetica,Arial,sans-serif;
	color: #585858;
}
  #shouts-global-resizer, #shouts-resizer {
	background-color: #424242;
	cursor: n-resize;
	height: 6px;
}
    .ipbmenu_content a {
	text-decoration: none;
	text-align: left;
	display: block;
	padding: 6px 10px;
	background-color: #424242;
}
  #folder_list .total {
	background: #424242 url('https://yggtorrent.com/forum/public/style_images/yggv2/highlight.png') repeat-x 0 1px;
	font-size: 0.75em;
	font-weight: bold;
	padding: 3px 6px;
	right: 0;
	top: 1px;
	position: absolute;
}
  div.bbc_spoiler_content {
	background: #424242;
	border: 1px dashed #e3e3e3;
	padding: 5px;
	margin-top: 5px;
}
  
.ipbmenu_content, .ipb_autocomplete {
	background: #424242;
	border: 1px solid #c6c6c6;
  }
  .ipb_autocomplete li.active {
	background: #585858;
}
  .popupInner h3 {
	border-bottom: 1px solid #d8d8d8;
	text-shadow: rgba(255,255,255,0.8) 0px 1px 0px;
	background: #1b1b1b url('https://yggtorrent.com/forum/public/style_images/yggv2/highlight.png') repeat-x 0 0;
	padding: 8px 10px 9px;
	font-size: 16px;
	font-weight: 300;
}
    .ipsTable tr:nth-child(2n+1) td, .ipsTable tbody:nth-child(2n+1) td, table.alternate_rows tr:nth-child(2n+1) td, ul.alternate_rows li:nth-child(2n+1) .ipsTable tr td, ul.alternate_rows li:nth-child(2n+1), .row2 {
	background: #585858;
}
    .textinput, .multitext, .input_text, .ipsTagBox_wrapper {
	padding: 4px;
	border-width: 1px;
	border-style: solid;
	border-top-color: #848484;
	border-left-color: #c1c1c1;
	border-right-color: #c1c1c1;
	border-bottom-color: #e1e1e1;
	background: #1b1b1b;
	-moz-border-radius: 2px;
	border-radius: 2px;
	width: 350px;
}
    .ipsTable tr:nth-child(2n) td, .ipsTable tbody:nth-child(2n) td, table.alternate_rows tr:nth-child(2n) td, ul.alternate_rows li:nth-child(2n) .ipsTable tr td, ul.alternate_rows li:nth-child(2n), .row1 {
	background: #424242;
}
    .header, .ipsTable th, .form_table th {
	background: #585858;
	color: #246594;
	text-shadow: 1px 1px 0 #1b1b1b;
	padding: 10px;
	font-size: 12px;
	font-weight: bold;
}
    .ipsSteps_wrap {
	background: #1e1e1e;
	padding: 10px;
}
    .ipsExpandable .item {
	padding: 10px;
	border-bottom: 1px solid #e2e8ee;
	background: #424242;
}
    .ipsExpandable .root_item {
	background: #1b1b1b;
	line-height: 22px;
}
.message, .message a, .message h3 {
    background-color: #424242;
    text-shadow:none;
}
th {
    background-color: #424242;
}
}
