css:
@-moz-document url-prefix(https://jira.platform.mnscorp.net/browse/) {
    #header,
    #announcement-banner,
    #ghx-operations,
    .complete-sprint-container,
    #ghx-view-pluggable,
    #ghx-view-presentation,
    #ghx-view-tools,
    #ghx-view-modes,
    #ghx-board-name,
    .aui-page-header-image,
    .aui-nav-breadcrumbs,
    .command-bar,
    .issue-body-content .module>.mod-header,
    #viewissue-devstatus-panel,
    #greenhopper-agile-issue-web-panel,
    .people-details .assign-to-me-link,
    .item-details,
    .property-list.two-cols>.item-right,
    #customfieldmodule,
    #attachmentmodule,
    #activitymodule,
    #addcomment {
        display: none !important;
    }

    #peopledetails,
    #datesmodule .item-details {
        display: block !important;
    }

    .people-details dl {
        padding-bottom: 1em !important;
    }

    .issue-header .issue-header-content>.aui-page-header {
        padding-bottom: 30px !important;
    }

    #summary-val {
        font-size: 2.5em !important;
    }

    .aui-group.issue-body {
        font-size: 1.5em !important;
    }

    .property-list img {
        width: 24px !important;
        height: 24px !important;
    }

    .property-list.two-cols>.item {
        width: 100% !important;
    }

    .property-list .wrap {
        padding-left: 250px !important;
    }

    .property-list .item {
        margin-bottom: 10px !important;
    }

    .property-list .wrap .name {
        margin-left: -250px !important;
    }

    .property-list .item .name {
        width: 250px !important;
    }

    ul.labels li .lozenge {
        font-size: 18px !important;
        padding: 5px !important;
    }
}

@-moz-document url-prefix(https://jira.platform.mnscorp.net/secure/) {
    #header,
    #announcement-banner,
    #ghx-operations,
    .complete-sprint-container,
    #ghx-view-pluggable,
    #ghx-view-presentation,
    #ghx-view-tools,
    #ghx-view-modes,
    #ghx-board-name,
    .SpartezAC-print,
    .ghx-swimlane-header .ghx-expander,
    .ghx-days,
    .ghx-move-count,
    .ghx-grabber,
    .ghx-end {
        display: none !important;
    }

    .ghx-column-headers {
        display: flex !important;
    }

    .ghx-column-headers .ghx-column {
        border-bottom: 0 !important;
        padding: 10px !important;
        width: 29% !important;
    }

    .ghx-column-headers .ghx-column:nth-child(4) {
        width: 13% !important;
    }

    .ghx-issue .ghx-highlighted-fields {
        padding-right: 0 !important;
        margin: 0 !important;
        overflow: visible !important;
        font-size: inherit !important;
        position: absolute !important;
        width: calc(100% - 2.5em) !important;
        left: 1em !important;
        bottom: 0.25em !important;
        opacity: 0.5 !important;
    }
    .ghx-issue .ghx-highlighted-fields .ghx-highlighted-field {
        max-width: none !important;
    }
    .ghx-issue .ghx-highlighted-fields .ghx-highlighted-field .aui-label {
        font-size: 16px !important;
    }

    .ghx-sprint-meta {
        margin-right: 0 !important;
    }

    #ghx-header h1 {
        font-size: 32px !important;
    }

    .ghx-issue,
    .aui-label,
    .ghx-sprint-meta .time,
    .ghx-column-headers h2,
    .ghx-swimlane-header {
        font-size: 18px !important;
    }

    .ghx-summary {
        font-size: 18px !important;
    }

    .ghx-sprint-meta .time .aui-icon-small {
        width: 22px !important;
        height: 22px !important;
    }

    .ghx-sprint-meta .time .aui-iconfont-time:before {
        font-size: 20px !important;
        margin-top: -10px !important;
    }

    .ghx-swimlane-header {
        padding-left: 0 !important;
    }

    .ghx-swimlane-header .ghx-description {
        font-size: 16px !important;
        color: #999 !important;
    }

    .ghx-swimlane-header .ghx-info {
        margin: 0 15px !important;
    }

    .ghx-swimlane .ghx-columns {
        display: flex !important;
    }

    .ghx-swimlane .ghx-columns .ghx-column,
    .ghx-swimlane .ghx-columns .ghx-parent-group {
        display: flex !important;
        flex-flow: wrap !important;
        width: 29% !important;
        border-right: 10px solid #fff !important;
        align-content: flex-start !important;
        background-color: #ddd !important;
        padding: 0.25em !important;
    }

    .ghx-columns .ghx-column .ghx-issue,
    .ghx-swimlane .ghx-columns .ghx-parent-group {
        margin: 0.25em !important;
        align-self: flex-start !important;
    }

    .ghx-swimlane .ghx-columns .ghx-parent-group > .ghx-issue {
        border: 0 !important;
        margin: 0 !important;
        width: 100% !important;
    }

    .ghx-parent-group .ghx-subtask-group {
        width: calc(100% - 2px - 0.5em) !important;
    }

    .ghx-columns .ghx-parent-group .ghx-subtask-group .ghx-issue {
        width: calc(100% - 2px - 0.25em) !important;
    }

    .ghx-swimlane .ghx-columns .ghx-parent-group {
        border: 1px solid #ccc !important;
        width: calc(50% - 2px - 1em) !important;
        margin: 0.5em !important;
        background-color: rgba(255,255,255,0.6) !important;
        padding: 0 !important;
    }

    .ghx-columns .ghx-column .ghx-issue {
        width: calc(50% - 2px - 0.5em) !important;
    }

    .ghx-columns .ghx-parent-group .ghx-subtask-group .ghx-issue {
        margin-right: 0 !important;
    }

    .ghx-columns .ghx-parent-group .ghx-subtask-group .ghx-issue-content {
        padding-bottom: 1em !important;
    }
    .ghx-issue.ghx-selected,
    .ghx-issue.ghx-selected .ghx-end {
        background-color: #fff !important;
    }

    .ghx-issue .ghx-issue-content {
        padding: 2.25em 0.75em 2em !important;
        position: relative !important;
        min-height: 70px !important;
    }

    .ghx-issue .ghx-avatar {
        top: 0.75em !important;
        right: auto !important;
        left: 0.75em !important;
    }

    .ghx-avatar-img {
        height: 18px !important;
        width: 18px !important;
        line-height: 18px !important;
        vertical-align: top !important;
    }

    .ghx-issue .ghx-flags {
        left: auto !important;
        top: 0.75em !important;
        right: 0.75em !important;
    }

    .ghx-issue .ghx-type,
    .ghx-issue .ghx-type img,
    .ghx-issue .ghx-flags,
    .ghx-issue .ghx-priority,
    .ghx-issue .ghx-priority img {
        width: 18px !important;
        height: 18px !important;
        vertical-align: top !important;
    }

    .ghx-issue .ghx-key {
        top: 0.75em !important;
        position: absolute !important;
        right: 1.75em !important;
    }

    .ghx-issue .ghx-type {
        left: 2em !important;
        top: 0.75em !important;
    }

    .ghx-issue.ghx-has-avatar .ghx-issue-fields,
    .ghx-issue.ghx-has-corner .ghx-issue-fields {
        padding-right: 0 !important;
    }

    .ghx-column:nth-child(4) .ghx-issue {
        opacity: 0.5 !important;
        width: calc(100% - 2px - 0.5em) !important;
    }
    .ghx-column:nth-child(4) .ghx-issue .ghx-avatar,
    .ghx-column:nth-child(4) .ghx-issue .ghx-type,
    .ghx-column:nth-child(4) .ghx-issue .ghx-flags {
        display: none !important;
    }

    .ghx-column:nth-child(4) .ghx-issue .ghx-key {
        right: 1em !important;
    }

    .ghx-swimlane .ghx-columns .ghx-column:nth-child(4) .ghx-parent-group {
        width: calc(100% - 2px - 0.5em) !important;
    }

    .ghx-swimlane .ghx-columns .ghx-column:nth-child(4) {
        width: 13% !important;
        display: block !important;
    }

    .ghx-swimlane .ghx-columns .ghx-column:nth-child(4),
    .ghx-swimlane .ghx-columns .ghx-column:nth-child(4) .ghx-parent-group {
        border-right: 0 !important;
    }

    .ghx-parent-stub .ghx-summary {
        font-size: 14px !important;
    }

    #ghx-pool {
        padding-left: 0 !important;
        padding-right: 0 !important;
    }
}
