@-moz-document domain("pcsx2.net") {
body.bd #ja-wrapper {
    min-width: 100%;
    background-color: #141414;
    box-shadow: 0 0 0px 6px #141414;
}
.blog-post {
  
    background-color: #27282e;
}
div.blog-post p {
    color: #bb7440;
}
div.ja-moduletable.moduletable.clearfix {
    background-color: #27282e;
    color: #00d1b5;
}
.column div.ja-moduletable, .column div.moduletable, .column div.ja-module, .column div.module {
    background: #27282e;
   
}
div.ja-moduletable.moduletable.clearfix h3 span {
    color: #00A3CC;
}
div.ja-moduletable.moduletable_menu.clearfix h3 span {
    color: #00A3CC;

}
div.blog-post ul li {
    color: #00A3CC;
}
.single-article, .search, .item-pagecontact, .itpdonate_campaign_, #xmap, .tag-category, .jd-item-page {
    background-color: #27282e;
}
div.item-page.clearfix h1 {
    color: #00A3CC;
}

LI{
    color: #ffffff;
}
div.item-page.clearfix h3 {
     color: #00A3CC;
}
div.ja-moduletable h3  {
    color: #00A3CC;
}
h1.contentheading, h2.contentheading, .contentheading {
    border-bottom: 1px solid #27282e;    
}
ol li a {
    color: #1ec9a7;
}

div.item-page.clearfix p a {
    color: #1ec9a7;
}
div.childcontent.cols2 {
    background-color: #1f121f;
}
table.jd_cat_subheader {
    background-color: #27282e;
    color: #ffff00;

}
tr td p {
   
    color: #ababab;
}
tr td b {
    color: #00A3CC;
}
.ja-megamenu li.mega .childcontent {
    border-radius: 10px;
    border: 1px solid #3a3a33;
    background: #27282e;
    display: block;
    height: auto;
    position: absolute;
    box-shadow: 0 0 19px black;
    
}
.ja-megamenu ul.level1 li.mega a.mega span.menu-title {
     color: #00A3CC;
}
.ja-megamenu ul.level1 li.mega a.mega {
    color: #d4cfcc;
    border-bottom: 1px solid #02d6ff;
}
.jd-item-page tr, .jd-item-page td {
    
    color:#ababab;
}


#ja-navhelper .main {
    padding: 5px 0;
    text-transform: uppercase;
    background-color:#27282e;
    color: #00A3CC;
}
   td b a {
    color: #00A3CC;
}
div.item-page.clearfix div {
    color: #ffffff;

}
.mytable td {
    background: none repeat scroll 0 0 #054d5d;
    border-top: 1px solid #0087a5;
    color: #d4d4d4;
    padding: 8px;
}
#rounded-corner th {
    background: none repeat scroll 0 0 #074b79;
    color: #ffffff;
}
div.custom p a {
    color: #ffffff;
}
div.jd-item-page div {
    color: #00A3CC;
    background-color: #27282e;
    box-shadow: inset 0 0 0px 20px #27282e;
}
#mod-search-searchword {
    background-color: #00a3cc;
    color: black;
}
.ja-box-usertools {
    background: #27282e;
}
#ja-cpanel-main ul.ja-usertools-font {
    background-color: #27282e;
   
    }
.ja-cpanel-action {
    background: #00A3CC;
}
#ja-cpanel-main {
  
    border: 1px solid #27282e;
}
#ja-cpanel-main h3 {
    background: none repeat scroll 0 0 #00A3CC;
    border-bottom: 1px solid #00A3CC;
}
a#ja-cpanel-toggle {
    background: none repeat scroll 0 0 #27282e;
    color: #ffffff;
}
div.itp-sbb-bothl {
   
    border-style: none;
}
.itp-sb-left {
    padding-top: 10px;
    text-align: center;
}
div.item-page.clearfix div a {
    color: #0096cc;
}
tr.more td a {
    color: #9ACD32;
}
tr.more td a {
    color:#9ACD32;
}
#perpage {
    background-color: #00A3CC;
    color: #ffffff;
    border-color:#022d49;
}
#youtube li a {
    color: #e61e1e;
}

#forum li a {
    color: #386beb;
}

#git li a {
    color: #17d168;
}

a {
    color: #efbc40;
}
div.custom h3 a {
    color: #00A3CC;
}
p.readmore a {
    background: none repeat scroll 0 0 #264db6;
    box-shadow: 0 2px 2px rgba(0,0,0,0.3);
    color: #fff;
    padding: 6px;
    text-transform: none;
}
.ja-megamenu {
    margin: 0;
    background: linear-gradient(#252525,#2d2d2d);
}
select.inputbox {
    background-color: #00A3CC;
    color: #ffffff;
    border-color: #022d49;
}
tr th strong {
    color: #ffffff;
    border-color: #ffffff;
}
thead tr th {
    color: #ffffff;
       
  box-shadow: inset 0 0 20px 20px #00A3CC;
    
  
}




body.mediawiki.ltr.sitedir-ltr.mw-hide-empty-elt.ns-0.ns-subject.page-Main_Page.rootpage-Main_Page.skin-vector.action-view {
    color: #d62bd6;
    background-color: #27282e;
}

#mw-head {
    background-color: #27282e;
}

div.mw-body {
    background-color: #27282e;
}

div.mw-content-ltr center {
    color: #c7c7c7;
}

div.mw-content-ltr p small {
    color: #c7c3c7;
}

li.selected span a {
    
    background-color: #27282e;
    
        box-shadow: inset 0 0 4px 2px #14b7fb;

}

#ca-talk span a {
    background-color: #140014;
}

tr td div {
    background-color: #27282e;
}

div.mw-body-content {
    background-color: #27282e;
}
div.vectorTabs li.selected a,  div.vectorTabs li.selected a:visited {
    color: #fff;
}
#ca-talk span a {
    background-color: #27282e;
        box-shadow: 0 0 5px 2px black;
    color: #ffecec;
}
p {
   color: #a7a7a7
}
tbody tr td {
    color:#ffffff;
        background-color:#27282e;
    
}
.menu-title,.menu-desc, td,  .mw-headline {
    color: #eee !important;
    
}




.navbox th, .navbox-title {
    background: #27282e;
}

div.catlinks {
    font-size: 9pt;
    background-color: #27282e;
    border: 1px solid #D3F4FE;
}
.mw-normal-catlinks {
    background-color: #27282e;
}
.ja-cpanel-action, .ja-cpanel-tools h3, .vectorTabs ul li, .mw-content-ltr  div, input {
    background:  #27282e !important;
}
div#mw-panel {
   
    background-color: #27282e;
       box-shadow: 0px 20px 0 14px #27282e;
}

#p-logo a {
    display: block;
    width: 10em;
    height: 160px;
    background-repeat: repeat;
    background-position: center center;
    text-decoration: none;
       box-shadow: 1px 0px 0 12px #27282e;
}
.mw-body {
   
    border: 1px solid #2c2c2c;
   
   
}div#footer {
    background-color: #27292e;
}div#footer #footer-info li {
    color: #aaaaaa;
}html {
    font-size: 100%;
    background-color: #27292e;
}
.mytable tbody tr:hover td {
    background: none repeat scroll 0 0 #00a3cc;
}dl.article-info {
    color: #c2a021;
    border-top: 1px solid #00a3cc;
    border-bottom: 1px solid #27292e;
}
}

