css:
@-moz-document regexp("^https?:\\/\\/wiki\\.unrealengine\\.com\\/.*") {
/*================================  Wiki start  ======================================*/ 
    
    html,
    body {
        background-color: #333 !important;
        color: #ccc;
    }

	.mw-body, .parsoid-body {
		background-color: #454545;
		color: #ccc;
	}

	.mw-body {
		border: 1px solid #505050;
	}

	.catlinks {
		background-color: #393939;
		border: 1px solid #505050;
	}

	.mw-highlight {
		background-color: #454545;
	}

	/*code start*/

	.mediawiki pre {
		border-color: #505050 !important;
		color: #ccc;
	}

	.mw-highlight .cp {
		color: #818F72;
	}

	.mw-highlight .cpf {
		color: #EC7600
	}

	.mw-highlight .c1,
	.mw-highlight .cm {
		color: #818F72;
	}

	.mw-highlight .kt {
		color: #8ED54D;
	}

	.mw-highlight .k {
		color: #8ED54D;
	}

	.mw-highlight .nc {
		color: #FFCD22;
	}

	.mw-highlight .s {
		color: #EC7600;
	}

	.mw-highlight .o {
		color: #FFF;
	}

	.mw-highlight .nb {
		color: #8ED54D;
	}

	.mediawiki .p {
		color: #FFF;
	}

	.mw-highlight .n {
		color: #FFCD22;
	}

	.mw-highlight .nl {
		color: #FFCD22;
	}

	/*code end*/

	/*tabs*/

	div.vectorTabs {
		background: none;
	}

	div.vectorTabs span {
		display: inline-block;
		background: none;
		background-color: #505050;
		box-shadow: 1px -4px 5px -2px #0005 inset;
		height: 100%;
	}

	div.vectorTabs span:hover {
		background-color: #666;
		box-shadow: none;
	}

	div.vectorTabs span:hover a {
		text-decoration: none;
	}

	div.vectorTabs .selected span {
		display: inline-block;
		background: none;
		background-color: #454545;
		box-shadow: none;
	}

	div.vectorTabs li.selected a,
	div.vectorTabs li.selected a:visited {
		color: #fff;
	}

	/*tabs end*/
    
    input {
        color: #ccc;
        background-color: #333;
        border: 1px solid #fff3;
    }
    
    textarea {
        color: #ccc;
        background-color: #333;
        border: 1px solid #454545;
    }
    
    select {
        color: #ccc;
        background-color: #333;
        border: 1px solid #fff3;
    }    
    
    input:hover,
    select:hover,
    input:focus,
    select:focus {
        border-color: #f90;
    }
    
    fieldset {
        border-color: #666;
    }
	
	a,
    a:link {
        color: #c3aa88;
        text-decoration: none;
    }
    
    a:hover {
        text-decoration: underline;
    }
    
    a:visited {
        color: #876;
    }
    
    h1, h2, h3, h4 {
        color: #ccc;
    }

	ul {
		list-style-image: none;
	}
    
    #firstHeading {
        font-size: 28px;
    }
    
    #mw-content-text h2,
    #mw-content-text h3,
    #mw-content-text h4 {
        font-size: 22px;
    }
    
    #mw-content-text h2:before,
    #mw-content-text h3:before,
    #mw-content-text h4:before {
        margin-right: 5px;
        color: #666;
        font-family: monospace;
    }
    
    #mw-content-text > h2:before {
        content: "❱";
    }
    
    #mw-content-text > h3:before {
        content: "❱❱";
    }
    
    #mw-content-text > h4:before {
        content: "❱❱❱";
    }

    
    
    
    #maincol {
        background-color: #393939;
        border: none;
    }
    

    .mw-body a.external {
        color: #1bf;
    }
    
    .mw-body a.external:visited {
        color: #08c;
    }


    .gallery {
        background: #333;
        border: 1px solid #454545;
    }
    
    
    
	/*contents*/

	.tocnumber {
		color: #ccc;
	}

	.toc {
		background-color: #393939 !important;
		border-color: #505050 !important;       
		border-radius: 4px;
	}

	/*contents end*/
    
    
    
    .searchwrapper {
        background-color: #454545;
    }
    
    .searchbox {
        background-color: #333;
        border-color: #666;
    }
    
    .searchbox:focus {
        border-color: #1bf;
    }
    
    .searchbutton {
        background-color: #666;
        border-color: #666;
    }
    
    
    
    .quicksearch .normaltitle,
    .quicksearch .largetitle,
    .quicksearch .smalltitle {
        color: #ccc;
    }
    
    
    
     
    a.homelink {
        color: #c3aa88 !important;
    }
    
    
    .action .heading {
        background-color: #666;
    }
    
    
    
    
    div.vectorTabs ul li {
        background-color: #393939;
        border: none;
    }
    
    div.vectorTabs ul li:hover a {
        color: #fff;
    }
    
    div.vectorTabs li a {
        color: #c3aa88;
        
    }
    
    div.vectorTabs li.new a,
    div.vectorTabs li.new a:visited {
        color: #c3aa88;
    }
    
    
    div.vectorTabs li.selected {
        background-color: #1bf;
    }
    
    
    #jumplink {
        background-color: #666;
        color: #c3aa88;
    }
    
    #jumplink:hover {
        color: #fff;
    }
    
    
    
    #sidebar {
        background-color: #454545;
        border-color: #454545;
        
    }
    
    #sidebar .arrowlist li {
        background-color: #393939;
        border: none;
    }
    
    #mw-panel.collapsible-nav .portal h3 {
        border: none;
    }
    
    div#mw-panel div.portal div.body ul li a {
        color: #c3aa88 !important;
    }
    
    div#mw-panel div.portal div.body ul li a:hover {
        color: #c3aa88 !important;
    }
    
    #mw-panel.collapsible-nav .portal h3 a,
    #mw-panel.collapsible-nav .portal.collapsed h3 a {
        color: #ccc !important;
    }
    
    #mw-panel.collapsible-nav .portal h3:hover,
    #mw-panel.collapsible-nav .portal .body ul li:hover {
        background-color: #666;
    }
    
    
    
    
    
    div.thumbinner {
        background-color: #454545;
        border: none;
    }
    
    html .thumbimage {
        border-color: #666;
    }
    
    
    
    
    #pagehistory li {
        border: none;
    }
    
    #pagehistory li.selected {
        background-color: #666;
        border: none;
    }
    
    .mw-plusminus-neg {
        color: #c33;
    }
    
    .mw-plusminus-pos {
        color: #9c0;
    }
    
    
    
    
    .searchterm_button {
        background-color: transparent;
        border-color: #1bf;
        border-radius: 10px;
        color: #ccc;
        padding: 2px 5px;
    }
    
    
    
    .topiclink_container {
        background: #454545;
    }
    
    .topiclink_container:hover {
        box-shadow: 0 0 0 1px #1bf;
    }
        
    .topiclink_title .topiclink_title_text a {
        color: #ccc;
    }
    
    
    
    .aux {
        background-color: #333;
        border-color: #666;  
    }
    
    
    
    table.wikitable {
        background-color: #333;
        color: #ccc;
    }
    
    td {
        background-color: transparent;              
    }
    
    th {
        color: #ccc;
    }
    
    table.wikitable > * > tr > td {
        border-color: #454545; 
    }
    
    table.wikitable > * > tr > th {
        background-color: #454545;
        border-color: #454545; 
    }
    
    
    
    
    #footer #legal {
        color: #ccc;
    }
    
    #footer #copyright a:link,
    #footer #copyright a:active,
    #footer #copyright a:visited,
    #footer #terms a:link,
    #footer #terms a:active,
    #footer #terms a:visited {
        color: #fff;
    }
    
    
    /*CODE*/
    
     pre {
         color: #ccc;
         background-color: #333 !important;
         border-radius: 4px;
         border-color: #666 !important;
         max-height: none !important;
         white-space: pre-wrap;
         word-break: break-all;
    }
    
    .mw-code {
        background-color: transparent;
        border: none
    }
    
     /*----------------*/
    
    
    .bash.source-bash .kw2 {
        color: #8ED54D;
    }
    
    .bash.source-bash .re5 {
        color: #b393e9;
    }
    
    .bash.source-bash .sy0 {
        color: #FFF;
    }
    
    .bash.source-bash .de1,
    .bash.source-bash .de2 {
        color: #ccc;
    }
    
    .bash.source-bash .nu0 {
        color: #999;
    }
    
    .bash.source-bash .st0 {
        color: #EC7600;
    }
    
    .cpp.source-cpp .me2 {
        
    }

    /*----------------*/
    
    .cpp.source-cpp .coMULTI,
    .cpp.source-cpp .co1 {
        color: #818F72;
    }
    
    .cpp.source-cpp .co2 {
       color: #b393e9; 
    }
    
    .cpp.source-cpp .de1,
    .cpp.source-cpp .de2 {
        color: #FFCD22;
    }
    
    .cpp.source-cpp .st0 {
        color: #EC7600;
    }    
        
    .cpp.source-cpp .kw1,
    .cpp.source-cpp .kw2,
    .cpp.source-cpp .kw3,
    .cpp.source-cpp .kw4 {
        color: #8ED54D;
    }
    
    .cpp.source-cpp .sy1,
    .cpp.source-cpp .sy2,
    .cpp.source-cpp .sy3,
    .cpp.source-cpp .sy4 {
       color: #FFF; 
    }
    
    .cpp.source-cpp .br0 {
        color: #FFF;
    }
    
    .cpp.source-cpp .nu0 {
        color: #999;
    }
    
    .cpp.source-cpp .me1,
    .cpp.source-cpp .me2{
        color: #FFCD22;
    }
    
     /*----------------*/
    
    .csharp.source-csharp .de1,
    .csharp.source-csharp .de2 {
        color: #FFCD22;
    }
    
    .csharp.source-csharp .me1 {
        
    }
    
    .csharp.source-csharp .kw1 {
        color: #8ED54D;
    }
    
    .csharp.source-csharp .sy0 {
        color: #FFF;
    }
    
    .csharp.source-csharp .br0 {
        color: #FFF;
    }
    
    .csharp.source-csharp .st0 {
        color: #EC7600;
    }
    
    .csharp.source-csharp .nu0 {
        color: #999;
    }
    
    .csharp.source-csharp .kw4 {
        
    }
    
    .csharp.source-csharp .co3 {
    }
    
    .csharp.source-csharp .coMULTI {
        color: #818F72;
    }
    
     /*----------------*/
    
    .ini.source-ini .de1 {
        
    }
    
    .ini.source-ini .re0 {
        color: #999;
    }
    
    .ini.source-ini .re1 {
        color: #FFCD22;
    }
    
    .ini.source-ini .re2 {
        color: #8ED54D;
    }
    
    .ini.source-ini .sy0 {
        color: #FFF;
    }
    
    
    /*CODE end*/
    
    
    /*popups*/
    .qtip-default {
        background-color: #454545;
        border-color: #666;
    }
    /*popups end*/
    
   
    
/*=================================  Wiki end  =======================================*/
}
