css:
@-moz-document regexp("^https?:\/\/www\.unrealengine\.com\/(en-US|ja|ko)\/blog.*") {
    
/*===============================  blog start  ===================================*/
    body {
        background-color: #333;
    } 
    
    
    
    
    .ue-react hr{
        border-color: #666; 
    }
    
    .blog-container .blog-article .blog-content .blog-header-info hr {
        border-color: #666; 
    }
    
    
   
    
    
    .blog-view-container .headline-banner .banner-author-date {
       text-shadow: 0 0 2px #000, 0 -0 4px #000;
    }
    

    
    
    .blog-container .blog-article h1 {
        color: #ccc;
    }
    
    .blog-container .blog-article .blog-content{
        color: #ccc; 
    }
    
    .blog-container .blog-article .blog-content .blog-header-info .category-section .category {
        color: #ccc;        
    }
    
    .blog-container .blog-article .blog-content .blog-header-info .category-section a:not(:last-child) .category {
       border-color: #666;  
    }
    
    .blog-container .blog-article .blog-content .cms > p,
    .blog-container .blog-article .blog-content .cms > p * {
        color: #ccc;    
    }
    
    
    .activityGridRow .activity-item .headline a {
        color: #ccc;
    }
    
    
    
    .blog-container .comment-rating-box h1.title {
        border-color: #666; 
    }
    
    
    /*comments*/
    
    .blog-container .comment-rating-box .sort-box {
        color: #ccc;
    }
    
    .blog-container .comment-rating-box .sort-box a {
        color: #999;
    }
    
    .blog-container .comment-rating-box .sort-box a:hover {
        color: #fff;
    }
    
    
    
    .blog-container .comment-item .comment-body .textile-container-view {
        color: #ccc;
    }
    
    .blog-container .comment-item .comment-body .comment-links .comment-date {
        color: #999;
    }

	.blog-container .comment-item .comment-body .comment-links > a,
	.blog-container .comment-item .comment-body .comment-links > span {
    	color: #808080;
	}    
    
    .blog-container .comment-item .comment-vote .comment-count {
        color: #999;
    }
    
    
    .blog-wrapper .btn-primary.disabled,
    .blog-wrapper .btn.disabled,
    .ReactModalPortal .btn-primary.disabled,
    .ReactModalPortal .btn.disabled {
        background-color: #454545;
        border-color: #666;
    }
    
    
    
    .ReactModalPortal .ReactModal__Content {
        background-color: #333 !important;
    }
    
    .ReactModalPortal .report-modal .cw-title {
        background-color: #666;
    }
    
    .ReactModalPortal .report-modal .cw-close {
        background-color: #333;
    }
    
    .ReactModalPortal .report-modal .cw-close:hover {
        background-color: #454545;
    }
    
    .ReactModalPortal .report-modal textarea {
        background-color: #333;
        color: #ccc;
        border-color: #666;
    }


    
    /*comments end*/
    
    
    /*comment box*/
    .blog-container .add-comment .textile-body .textile-edit textarea,
    .blog-container .add-comment .textile-body .textile-help textarea,
    .blog-container .add-comment .textile-body .textile-preview textarea {
        color: #ccc !important;   
    }

    .blog-container .comment-rating-box {
        color: #ccc;
    }
    
    .blog-wrapper .comment-edit-box .textile-container .textile-head li:hover,
    .blog-wrapper .comment-rating-box .textile-container .textile-head li:hover,
    .ReactModalPortal .comment-edit-box .textile-container .textile-head li:hover,
    .ReactModalPortal .comment-rating-box .textile-container .textile-head li:hover {
        background-color: #1bf3;
    }
    
    .ReactModalPortal .comment-chars-max.comment-edit-invalid,
    .ReactModalPortal .comment-chars-min.comment-edit-invalid,
    .blog-container .comment-chars-max.comment-edit-invalid,
    .blog-container .comment-chars-min.comment-edit-invalid {
        color: #f66;;
    }
    
    .blog-container .add-comment .textile-body .textile-edit textarea:focus,
    .blog-container .add-comment .textile-body .textile-help textarea:focus,
    .blog-container .add-comment .textile-body .textile-preview textarea:focus {
        box-shadow: none;
        border-color: #ccc !important;
    }
    
    .comment-add-reply .textile-head li,
    .comment-edit-box .textile-head li {
        background-color: transparent;
    }

	.blog-container .comment-item .comment-add-reply .textile-body .textile-edit textarea,
	.blog-container .comment-item .comment-add-reply .textile-body .textile-help textarea,
	.blog-container .comment-item .comment-add-reply .textile-body .textile-preview,
	.blog-container .comment-item .comment-add-reply .textile-body .textile-preview textarea {
		color: #ccc;
	}

	.comment-add-reply .textile-body .textile-edit textarea:focus,
	.comment-add-reply .textile-body .textile-help textarea:focus,
	.comment-add-reply .textile-body .textile-preview textarea:focus,
	.comment-edit-box .textile-body .textile-edit textarea:focus,
	.comment-edit-box .textile-body .textile-help textarea:focus,
	.comment-edit-box .textile-body .textile-preview textarea:focus {
    	box-shadow: none;
    	border-color: #ccc !important;
	}
    
    /*comment box end*/
    
    
    /*categories*/
    
    .blog-wrapper .blog-view-container .blog-view .light-bg,
    .ReactModalPortal .blog-view-container .blog-view .light-bg {
        background-color: transparent;
    }
    
    .blog-view {
        color: #999;
    }
    
    .blog-wrapper .blog-view-container .blog-view .byline,
    .blog-wrapper .blog-view-container .blog-view .short,
    .ReactModalPortal .blog-view-container .blog-view .byline,
    .ReactModalPortal .blog-view-container .blog-view .short {
        color: #ccc;
    }
    
    .activityGridRow::after {
        border-color: #666; 
    }
    
    
    .category-filter {
        background-color: #454545;
    }
    
    .category-filter .filter-content,    
    .category-filter .filter-content ul li {
        color: #ccc;
    }
    
    
    .category-filter .head .filter-button {
        color: #ccc;
    }
    
    .category-filter .head .filter-button i {
        color: #ccc;
    }
    
    
    
    /*categories end*/
    
    
    
/*================================  glog  end  ====================================*/
    
}
