css:
@-moz-document domain("www.aimp.ru") {
#page {
   
    background: #454545;
    color: #ffffff;
}
.page_news_topic, .page_news_topic_first {
 
    background-color: #454545;
  
    border: 1px #ff9828 solid;
}
.card {

    background-color: #454545;
    border: 1px #ff9828 solid;
   
}
.edit {

    background: #ff9828;
}
.button {

    color: #d0ffff;
    
    border: 1px solid #ff9828;
}
.aimp_content {
    padding: 20px;
    background: #454545;
}
#header div.frame {

    background-color: #454545;
    color: white;
}
.post_wrapper {
    
    background-color: #454545;
}
p#whoisviewing {
    
    background-color: #454545;
}
.flow_hidden {
   
    border-bottom: 1px solid #747474;
    background: #454545;
}

.windowbg span.botslice {
  
    background-color: #454545;
}

.description, .description_board, .plainbox {
   
    background: #454545;
}
body.custom-background {
    background-color: #454545;
}
.widget-area .widget a {
    color: #ffffff;
}
footer[role="contentinfo"] a {
    color: #f4f4f4;
}
.widget-area .widget h3 {

    color: #e17604;
}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote, code.bbc_code {
    border-top: 2px solid #e16400;
    border-bottom: 2px solid #e16400;
    background-color: #b4b4b4;
    color: #454545;
}
.comments-link a, .entry-meta a {
    color: #f7f1c1;
}
.topic_table td.lastpost, table.table_list tbody.content td.lastpost {

    background-color: #454545;
    color: #f0f0f0;
}
table.table_grid td, table.table_list td {
  
    background-color: #454545;
    color: white;
}
.roundframe {
    padding: 8px;
    background: #454545;
}
div.title_barIC {
    background: #e16405;
}
ul.reset, ul.reset li, .aimp_page_header ul.reset, .aimp_page_header ul.reset li {

    color: #ffffff;
}
.windowbg2 {
    background-color: #454545;
}
.table_list tbody.content td.children {
    color: #ffffff;
    
}
.content {
  
    background-color: #454545;
    color: white;
}
.counter {
  
    color: #ffffff;
}
.windowbg span.topslice {
  
    background-color: #454545;
}
.catalog_news_image {
    border: 1px solid #ff9828;
}
.buttonlist ul li a {
   
    color: white !important;
}
footer.entry-meta {
   
    color: #c4f3d0;
}.news_topic {
 
    background-color: #464646;
   
    border: 1px #ff9924 solid;
}.catalog_news {
    border: 1px solid #ff9920;
 
}
}
