/* ::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::: 
 * :::                Created by Hasan YÄ±lmaz a.k.a. SuPeRHeRo          :::
::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::::: 
 */

                    /* Navigationbar Settings */   

#nav-bar {
     border: none!important;
     padding: 5px 3px 1px 3px!important; 
     background: #dfeaf8 !important;}

#identity-box  {
 width: 25px !important;
 height: 25px !important;}

#identity-box > hbox {
  background-image: none !important;
  background-color:  transparent !important;
  margin: 3px -2px 1px -1px !important;}

#identity-icon-label {
 display: none !important;
}

.verifiedIdentity {
  background-color: #FFCCFF !important; -moz-border-radius: 5pt !important;
margin: 2px 0px 2px -2px !important;}

.verifiedDomain {
  background-color: #33FF33  !important;  -moz-border-radius: 5pt !important;
margin: 2px 0px 2px -2px !important;}

#urlbar  {
margin-left: 8px !important;
background-color: #dfeaf8 !important; 
border: none  !important; }

#urlbar .progress-bar { 
  -moz-border-radius: 4pt !important; 
  margin: 2px 3px 3px -1px!important;   }

#urlbar .textbox-input-box {
font-family: "arial" !important;
font-size: 17px !important;
}

#urlbar dropmarker {
background-image: none !important;
border: none !important;
margin-left: -4px !important;
}

/* combined Stop/Reload button - must reverse order on toolbar first */
#stop-button[disabled="true"] { display: none !important; } 
#stop-button:not([disabled]) + #reload-button { display: none !important; }

/* better auto-complete */
#urlbar[pageproxystate="valid"] > #urlbar-icons > #go-button {  
     visibility: visible !important; }
.ac-comment, .ac-url-text {
     font-size: 100% ! important; }
.ac-url-text {
     font-size: 100% ! important;
     color: green !important; }

#searchbar * 
{background-color: #dfeaf8 !important; 
border: none !important; -moz-border-radius: 6pt !important; }

#searchbar .searchbar-engine-button {
background: transparent !important;  }

#searchbar .textbox-input {
font-family: "Segoe ui" !important;
font-size: 17px !important;
}

#searchbar .textbox-input {
font-family: "arial" !important;
font-size: 17px !important;
}



                      /* General tab settings */
tab {
 font-family: arial !important;
 font-size: 13px !important;
 margin-top: 0px !important; /* Space over tabs and under URL-tab */
 margin-bottom: 0px !important;
 border-bottom: 0px !important;
 margin-left: 0px !important; }

/* Remove dotted border around text on selected tab */
@-moz-document url(chrome://browser/content/browser.xul) {
 .tab-text {
  border: none	!important; 
  margin-left: 7px  !important;}  }

tab .tab-progress{
   -moz-appearance: none !important;
   background-color: transparent !important; }

tab .progress-bar{
   margin-top: 2px !important; 
   margin-bottom: 3px !important;   -moz-border-radius: 2.5pt !important; }



                    /* Statusbar Settings */   

statusbar .statusbarpanel-text {
font-family: "arial" !important;
font-size: 16px !important;  }

#status-bar {
  border: none !important;
  margin: 0px !important;  }

menuitem hbox[anonid="accel"]{display:none!important}
statusbar {
background-image: none !important;
background-color: #dfeaf8 !important;
border-top: none !important;   }

/* restore buttons on Quick Find bar */
#FindToolbar > * { display: -moz-box !important; }
.findbar-container > * { display: -moz-box !important; }

#aios-toggle-toolbox{ margin-left: -4px !important; }
