css:
@-moz-document url-prefix(https://web.cs.dal.ca/~mhe/) {

* {
    box-sizing: border-box
}
html {
    font-size: 16px;
    font-family: "Trebuchet MS", "Lucida Grande", "Lucida Sans Unicode", "Lucida Sans", Tahoma, sans-serif;
}
body ,.container{
    display: block;
    margin: auto;
    width: 1024px;
}
body> ul,
body> p {
    width: 98%;
}
h1,
h2,
h3,
h4,
h5 {
    color: #0D47A1;
    line-height: 150%;
    padding: 6px;
    margin: 0 12px;
}
p {
    display: block;
    color: #212121;
    line-height: 200%;
    padding: 0.3em;
    margin: 0 0.8em;
}
pre {
    padding: 0.3em;
    margin: 0 0.8em;
}
strong {
    color: #2979FF;
    line-height: 250%;
    padding: 0.4em 0.4em 0.4em 0;
    margin: 0.8em 0.8em 0.8em 0;
}
ul {
    list-style-type: none;
    padding: 0.5em;
    margin: 1em;
}

li{
	margin: 0.3em 0;
}

li> p {
	margin: 0;
    padding: 0;
    line-height: 135%;
}

ol {
    border: 1px solid grey;
    margin: 2em;
    padding: 0.5em 2em;
}
a {
    text-decoration: none;
    color: #2196F3;
}
a:visited {
    color: #FA6900;
}
table {
    padding: 0.3em;
    margin: 0 0.4em;
}

td {
    background: #0277BD;
    border-radius: 3px;
   padding: 0.3em 1.2em;
    color: #BDBDBD;
    transition: all 400ms ease;
}
td:hover {
 background : #90CAF9;
transform: translate(0,3px); 
    color: #fff
}

td:hover a{
	color: #fff !important;
}

td> a {
    color: #fff;
            
}
td > a:visited {
    color: #fff;
}

s> a {
    color: white
}

.banner{
    margin:  10px;
}

.navbar{
    
	padding: 0.5em;
    line-height: 200%;
	height: auto;
}

.twocolumn{
	width: 100%;
    height: 300px;
}

.leftcolumn{
    height: 100%;
    width: 40%;
    margin: 0;
    padding: 0;
}

.leftcolumn > img{
    padding: 1em;
	height: 100%;
    width: auto;
}

.rightcolumn{
    width: 60%;
    margin: 0;
    padding: 0;
}

.rightcolumn > p {
	width: 100%;
}

.navbar > ul > li {
	font-size : 1em !important;
}

.banner ,.navbar,.body,.bottombar{
	width: 100%;
}

.body{
padding: 0 !important;
}

pre{
	background : lightgray;
}	

@media screen and (max-width:1024px) {
    body ,.container{
        width: 100%
    }

}
}
