css:
@-moz-document url-prefix(https://sweepsdb.com/)
{

body {
    background: #101010 !important; }


/* ------------ Header, Footer ------------ */

.blue.lighten-5, .blue.darken-1, .page-footer, #NtpMzjqWoQkH {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background: #202020 !important; }
nav ul a, .navbar-text {
    color: #CCC !important; }
nav ul a:hover, .navbar-text:hover {
    color: white !important;
    background: #303030 !important; }
.dropdown-content li .height-25 {
    color: #CCC !important; }
.dropdown-content li {
    border-color: #050505 !important;
    background: #303030 !important; }
.dropdown-content li > a {
    border-top: 1px solid #050505 !important;
    background: #303030 !important;
    color: #CCC !important; }
.dropdown-content li > a:hover, .dropdown-content li:hover {
    color: white !important;
    background: #505050 !important; }

.search input:not(.searchmob) {
    border-color: #050505 !important;
    box-shadow: none !important;
    color: white !important;
    background: #404040 !important; }


/* ------------ Contest List ------------ */

thead {
    color: #CCC !important; }

.contestlist, .card.white {
    border: 1px solid #050505 !important;
    background: #303030 !important; }
.card.xsmall {
    color: white !important;
    border: 1px solid #050505 !important;
    background: #404040 !important; }
.card.xsmall .card-content {
    color: #CCC !important; }
.card .card-action a:not(.btn):not(.btn-large):not(.btn-large):not(.btn-floating) {
    color: #58ACFA !important; }
.card .card-action a:not(.btn):not(.btn-large):not(.btn-large):not(.btn-floating):hover {
    color: #9ccefc !important; }

table.sortable th:hover:not(.no-sort) {
    color: white !important;
    background: #404040 !important; }

.categorycolumn a {
    color: #58ACFA !important; }

tr.sponsored {
    border-color: #050505 !important;
    background: #404040 !important; }
tr.sponsored a.contestlink {
    color: #dedede !important; }
tr.sponsored:hover, tr.sponsored:hover a.contestlink, table.sortable tbody.tabody, .sponsored {
    color: white !important;
    background: #505050 !important; }

.contestlink {
    color: white !important; }
.contestlink:hover {
    text-decoration: underline !important; }
.contestlink:visited {
    color: #b300b3 !important; }

.field-tip .tip-content {
    border: 1px solid #050505 !important;
    box-shadow: none !important; }

tbody.tabody tr:nth-child(even) {
    border-color: #050505 !important;
    background-color: #404040 !important; }

tbody.tabody tr:nth-child(odd) {
    border-color: #050505 !important;
    background-color: #303030 !important; }


/* ------------ Specific Giveaway ------------ */

table tbody td {
    color: #CCC !important; }

.select-wrapper input.select-dropdown {
    background-color: #404040 !important; }
.select-dropdown li.disabled, .select-dropdown li.disabled > span, .select-dropdown li.optgroup, .select-dropdown li.optgroup > span {
    color: #CCC !important;
    background: #505050 !important; }

.dropdown-content li > span {
    background: #202020 !important; }

.card-panel {
    background: #202020 !important; }

.btn {
    border: 1px solid #050505 !important;
    color: #CCC !important;
    background: #505050 !important; }
.btn:hover {
    background: #303030 !important; }


/* ------------ Settings Page ------------ */

.collection.s12 .active {
    color: #CCC !important; }

.collection.s12 #uid.black-text {
    color: white !important; }

#username.validate.s6.valid, #email.validate {
    background: #505050 !important; }

.collection.s12 .secondary-content {
    color: #CCC !important; }

.collection.s12 .dropdown-content li > span:hover {
    background: #404040 !important; }
}
