css:
@-moz-document domain("pcmag.com") {
/* Created By Marc Stocker */

:root {
    --bg-color-background	: #2e3031;		/* Main Background */
    --bg-color-first		: #1f1f20;		/* */
    --bg-color-second		: #0e0e0e;		/* */
    --bg-color-light-grey	: #6b6b6b;		/* Light Grey Login Header*/
    --bg-color-hover		: #3b3b4b;		/* */

    --border-color-top		: #2c2c36;		/* */
    --border-color-right	: #2c2c36;		/* */
    --border-color-bottom	: #2c2c36;		/* */
    --border-color-left		: #2c2c36;		/* */

    --text-color-first		: #eeeeee;		/* */
    --text-color-second		: #cccccc;		/* */
    --text-color-third		: #9a9a9a;		/* */
    --text-color-forth		: #5c5c5c;		/* */
    --text-color-green		: #257727;		/* */
    
    --text-color-links		: #33b1d7;      /* Standard link color 	- Default(Light Blue/Cyan) */
    --text-color-visited	: #9a6eff; 		/* Visted link color 	- Default(Purple) */
    --text-color-hover		: #10c4fb;		/* Link hover color		- Default(slightly ligher than link color) */
    
	--text-color-links-w	: #fafafa;      
    --text-color-hover-w	: #cacaca;		
    --text-color-visited-w	: #dadada; 		
    
}


/* Backgrounds */
/* Main Background */
body, header#main-top-nav, header#main-top-nav nav .main-nav
{
 	background: var(--bg-color-background);   
}
/* Background #2 */
#widgetbox .affiliate-text, #zergnet-widget-41514, #zergnet-widget-41521, header#main-top-nav nav .social, .story-navigation .prev-story, .story-navigation .next-story, header#main-top-nav nav .main-nav>li.closed.open .sub-nav-wrapper, .author-container
{
 	background: var(--bg-color-first);   
}
/* Background #3 */
#news-stack ul, footer, #news-stack .more
{
 	background: var(--bg-color-second);   
}
/* Background #4 */
.bottom-line-circ, .author-container .author-stories ul li, .reviews-div:not(.optimizely) .review-blender .pcmag-buyitnow table.pcmag-buyitnow-table tr td
{
    background: #151617;   
}
/* Background - Black Transparent */
.bxc.bx-campaign-486735.bx-active-step-1 .bx-shroud
{
 	background-color: rgba(0, 0, 0, 0.94);   
}
/* No background - Transparent */
.article-section .post-body a.zdcse-buybtn, .review-body a.zdcse-buybtn, .article-section .post-body a.zdcse-buybtn:hover, .review-body a.zdcse-buybtn:hover
{
    background: none;   
}
/* --------------------- Text ----------------------- */
/* Text 1 */
select, span, code, textarea, pre, label, div, dd, option, .article-header h1, .review-head-title h1, .content .article-header h1, .article-section .article-body h2, .review-body h2, #roundup-container tr td a, .spotlight-module ul li .spotlight-deck a, header#main-top-nav nav .main-nav>li>a, #news-stack h2, #reviews-stack h2, .spotlight-module h2, .headline2 a, .headline3 a, .story-navigation .prev-story, .story-navigation .next-story, .author-container .author-stories ul li .author-content a, .heading h2 a, .dark .comment__footer .vote-down, .dark .comment__footer a, .dark .comment__header .vote-down, .dark .comment__header a, .publisher-anchor-color a, a[data-action="profile"], h1, h2, h3, h4, h5, h6, .reviewProducts ul li h3 a, .reviewProducts .products li h3 a
{
 	color: var(--text-color-first) !important;   
}
/* Text 2 */
.article-header .deck, .review-head-title .deck, .content .article-header .deck, #share_container .share_menu li span, #widgetbox .slideshow-stack h2, #widgetbox .slideshow-stack h3, #widgetbox .articles-stack h2, #widgetbox .articles-stack h3, .affiliate-text, #widgetbox .slideshow-stack .more a, #widgetbox .articles-stack .more a, .article-section .article-body p, .review-body p, .spotlight-module ul li .spotlight-deck p, #news-stack ul li a, #reviews-stack ul li .review-container .review-div a.review-title span, abbr, acronym, .author-container .author-stories ul li .author-content p, .author-container .author-bio .author-deck, .inside-module ul li a, header#main-top-nav nav .main-nav>li.open.closed .sub-nav-wrapper .sub-nav>li a, .pros-cons-bl ul li, .newsletter-widget p:not(.privacy-text), .story-navigation>div p a
{
 	color: var(--text-color-second) !important;   
}
/* Text 3 */
.article-header .byline ul li, .article-header .byline ul.authors li, .review-head-title .byline ul li, .review-head-title .byline ul.authors li, .content .article-header .byline ul li, .content .article-header .byline ul.authors li, #zergnet-widget-41514 .zerghead1, #reviews-stack .more a, #news-stack .more a, .author-container .author-bio .author-info span:not([itemprop="name"]), .newsletter-widget p.privacy-text
{
 	color: var(--text-color-third);   
}

/* Text Black */
#toclink div
{
 	color: black !important;   
}

/*----- Blue links ------*/

#roundup-container .roundup-chart tr.readreview td a {
	color: var(--text-color-links) !important; }
#roundup-container .roundup-chart tr.readreview td a:hover {
 	color: var(--text-color-hover) !important; }
#roundup-container .roundup-chart tr.readreview td a:visited {
	color: var(--text-color-visited) !important }

/*----- White Links ------ */
.article-section .post-body a.zdcse-buybtn b, .review-body a.zdcse-buybtn b, .article-section .post-body a.zdcse-buybtn, .review-body a.zdcse-buybtn {
	color: var(--text-color-links-w) !important; }
.article-section .post-body a.zdcse-buybtn b:hover, .review-body a.zdcse-buybtn b:hover, .article-section .post-body a.zdcse-buybtn:hover, .review-body a.zdcse-buybtn:hover {
 	color: var(--text-color-hover-w) !important; }
.article-section .post-body a.zdcse-buybtn b:visited, .review-body a.zdcse-buybtn b:visited {
	color: var(--text-color-visited-w) !important }
/* ---------------- Tables ------------------------ */
/* Header */
#roundup-container .roundup-chart tr:nth-child(1) td:not(:first-child), #roundup-container .roundup-chart tr:nth-child(2) td:not(:first-child), #roundup-container .roundup-chart tr:nth-child(3) td:not(:first-child), #roundup-container .roundup-chart tr:nth-child(4) td:not(:first-child)
{
 	background-color: #494949;   
}
/* Even Rows */
#roundup-container .roundup-chart tr:nth-child(even) td
{
 	background-color: #5f5f5f;   
}
/* Odd Rows */
#roundup-container .roundup-chart tr:nth-child(odd) td
{
 	background-color: #7f7f7f;   
}
/* --- Columns --- */
/* Evens */
#roundup-container .roundup-chart tr:nth-child(even) td:first-child
{
 	background-color: #555;
}
/* Odds */
#roundup-container .roundup-chart tr:nth-child(odd) td:first-child
{
 	background-color: #777;   
}
/* ---------------- Buttons ----------------------- */
/* Media Buttons */
/*Normal*/
#share_container .share_menu li
{
 	background-color: #888a8c;   
}
#share_container .share_menu.vertical li 
{
    background-color: #636363;
    opacity: .8;
}
#share_container .share_menu.vertical
{
 	background-color: #46464678;   
}

/* --------------------- Borders --------------------- */

/* --------------------- Filters --------------------- */
#zergnet-widget-41514 .zergheader::before
{
 	mix-blend-mode: color   
}








span > img
{
    mix-blend-mode: overlay;
}
}
