css:
@-moz-document url-prefix(http://capa.phys.dal.ca/) {
* {
    box-sizing: border-box
}
html {
    font-size: 16px;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
}
body {
    display: block;
    margin: auto;
    width: 1024px;
}
form > table,
menu {
    width: 40%;
    margin-left: 30%;
    margin-right: 30%;
}
table[width="500"],
table[cellpadding="5"] {
    width: 97%;
    margin-left: 1.5%;
    margin-right: 1.5%;
}
h1,
h2,
h3,
h4,
h5 {
    color: #0D47A1 !important;
    line-height: 150%;
    padding: 0.3em;
    margin: 0 0.3em;
}
i,
font,
p>b {
    color: #1565C0;
    font-style: normal;
}
var {
    font-style:normal;
    color: #FF5722;
    font-size: 1.2em;
}
sub {
    color: #0277BD;
    font-size: 1em;
}
sub > font {
    color: #0277BD;
    font-size: 0.8em;
}
a {
    text-decoration: none;
    color: #2196F3 !important;
    transition: all 400ms ease; 
}
a:visited {
    color: #FA6900 !important;
}
a:hover{
	background-color: rgba(25,118,210,0.8);
    box-shadow: 0 0 0 3px rgba(25,118,210,0.8);
    color: white !important;
}


p {
    display: block;
    color: #212121;
    line-height: 200%;
    padding: 0.3em;
    margin: 0 0.8em;
    transition: all 500ms ease; 
}
li,td{
 transition: all 500ms ease; 
}
li:hover,td:hover{
    background-color: rgba(88,88,88,0.1);
    box-shadow: 0 0 0 3px rgba(88,88,88,0.1);
}


ol {
    padding: 0.3em;
    margin: 0 0.8em;
}
span {
    font-size: 1.5em;
    margin: 0 0.2em 0 0;
}
pre {
    padding: 0.3em;
    margin: 0 0.8em;
}
strong {
    color: #2979FF;
    line-height: 250%;
    padding: 0.4em 0;
    margin: 0.8em 0;
}
img {
    margin: 0 0.3em;
}
h1 > i,
font> i {
    font-style: normal;
    font-family: Copperplate, "Copperplate Gothic Light", fantasy;
}
td {
    padding: 0.2em !important;
}
table {
    margin: 0 0.8em;
}

p> tt {
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    background-color: #F44336;
    color: #fff;
    padding: 0.7em 3em 0.7em 1em;
}
td > a {
    position: -webkit-sticky;
    position: sticky;
    border: 1px solid grey;
    padding: 0 0.18em;
}
a>b {
    margin: 0;
}
select {
    border-radius: 0.3em;
    padding: 0.7em;
    font-size: 1em;
    text-decoration: none;
    margin: 0.3em;
    position: relative;
    display: inline-block;
    box-shadow: 0 0.1em 0 0 lightgrey;
}
select:active {
    transform: translate(0, 0.3em);
    box-shadow: 0 0.1em 0 0;
}
input {
    border-radius: 0.3em;
    padding: 0.7em;
    font-size: 1em;
    text-decoration: none;
    margin: 0.3em;
    position: relative;
    display: inline-block;
    box-shadow: 0 0.1em 0 0 lightgrey;
    outline: none;
}
input[type="submit"] {
    background-color: #55acee;
    color: #fff;
    border: none;
}
input[type="reset"] {
    background-color: #FA6900;
    color: #fff;
    border: none;
}
input[type="button"] {
    background-color: #F44336;
    color: #fff;
    border: none;
}
input:active {
    transform: translate(0, 0.3em);
}
ul {
    padding: 1em 1.8em;
    border: 1px solid lightgrey;
}
li>form{
	display: inline-block;
}
menu{
	width: 60%;
}

@media screen and (max-width:1024px) {
    body {
        width: 100%
    }
}
}
