css:
@-moz-document url-prefix("https://your.domain.here/otrs") {
/*Imports*/

/*Version*/

.ARIARoleContentinfo:after {
    content: " SasnycoN Beta 1.2 ";
}
    .LayoutPopup > .Content {
    padding: 20px;
    background-color: #fff;
    background-image: url("https://cdn-images-1.medium.com/max/2000/1*17E24_QYryTNUigzDMtQrg.jpeg");
}
    .Field {
        color: white;
    }
div {
        color: black;
    }
    .TableSmall td a {
    color: #800000;
}
.OverviewBox > h1 {
    margin: 10px 0px;
    padding: 8px 11px 7px;
    position: relative;
    color: #fff;
}
#ToolBar a i {
    text-shadow: none;
    color: #800000;
}
#Header {
    height: 40px;
    background: #000000;
    border-bottom: 38px solid #000000;
    margin-bottom: 7px;
}

#Navigation > li > a {
    color: #fff;
    padding: 12px 12px 2px 12px;
    height: 24px;
}
#Navigation > li.Selected > a, #Navigation > li:hover > a {
    color: #fff;
}
.MainBox {
    position: relative;
    padding: 4px 16px 8px 8px;
    color: #000;
    background-color: #333333;
    background-image: url("https://cdn-images-1.medium.com/max/2000/1*17E24_QYryTNUigzDMtQrg.jpeg");
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center; 
    background-size: auto;
}
    
.MainBox > h1 {
    margin: 0;
    padding: 10px 8px 15px 8px;
    position: relative;
    color: #800000;
}
.CanDrag .Header h2 {
    cursor: move;
    color: #fff;
    Background: #000;
}
 
div.Headline .Flag + h1 {
    margin-left: 30px;
    color: #fff;
}
fieldset.TableLike > label, fieldset.TableLike > .Row > label {
    display: block;
    float: left;
    line-height: 1.9em;
    width: 30%;
    text-align: right;
    color: #929292 !important;
    min-height: 17px;
    padding-top: 0.3em;
    padding-bottom: 1px;
    text-shadow: none;
}
.MainBox {
        color: #929292 !important;
    }
    body {
    font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 12px;
    text-align: left;
    color: #929292 !important;
}
a {
    color: #800000;
    text-decoration: none;
    font-weight: bold;
}
#ToolBar a span.Counter {
    bottom: -14px;
    color: #fff;
    display: block;
    font-size: 11px;
    left: 50%;
    margin-left: -12px;
    position: absolute;
    text-align: center;
    text-indent: 0 !important;
    width: 24px;
}
}
