css:
@-moz-document domain("lowcygier.pl") {
/* glowna stona forum*/
body {
    background-color: #272c35;
}

#wrap {
    background: #272c35;
}

#inner-wrap {
    background: #1f232a;
}

.forumlist_grid_block_stats, .login_form_forgot_link, .profile_widget_list, .sidebar_content {

    border-top-color: #3a4049;
    background: #1f232a;

}

.fancy_panel, body.content_block_header_stripe li.header {

    background-color: #1f232a;

}

a {
    color: #b3bbca;
}

body, html {
    color: #b3bbca;
}

.forabg, .forumbg, .viewtopic_wrapper {
    background-color: #272c35;
}

li.row {
    border-bottom-color: #3a4049;
    background-color: #1f232a;
}
li.row strong {

    color: #e1e4ea;

}

/* podstrony */

.bg1, .bg2, table.zebra-list tr:nth-child(2n) td, table.zebra-list tr:nth-child(2n+1) td, ul.zebra-list li:nth-child(2n), ul.zebra-list li:nth-child(2n+1) {

    background-color: #1f232a;
}

.post {

    border-color: #3a4049;
}

.content, .postbody {

    color: #b3bbca;

}

a:hover, .navbar_footer a, .copyright_bar a, .social_links_footer a:hover span, .icon.fa-file.icon-red, a:hover .icon.fa-file.icon-red, .navigation .active-subsection a, .navigation .active-subsection a:hover, .navigation a:hover, .tabs .tab > a:hover, .tabs .activetab > a, .tabs .activetab > a:hover, a.postlink, a.postlink:visited, .navbar_in_header .badge, .button:focus .icon, .button:hover .icon, .dark_base .social_links_footer a span, .dark_base h2, .dark_base h2 a, .dark_base h1, .dark_base h1 a, .dark_base a:link, .dark_base a:visited, .button-secondary:focus, .button-secondary:hover, .notification_unread {

    color: #e1e4ea;
}

.postprofile {

    color: #666;
    border-color: #3a4049;

}

.postprofile strong {

    color: #e1e4ea;
}

.panel {

    background-color: #272c35;
    color:#e1e4ea;

}

h1, h2 {

    color: #e1e4ea;

}

.panel .inputbox {
    background-color:  #09081154;
}

.message-box textarea {
    color: #b3bbca;
}

.inputbox {
    border-color:#3a4049;
    color: #b3bbca;
}

.content, .postbody {

    color: #b3bbca;

}

.post:target .content, .post:target h3 a {

    color: #b3bbca;

}

blockquote {
    background-color: #272c35;

}

blockquote blockquote {
    background-color: #1f232a;

}

blockquote blockquote blockquote {
    background-color: #272c35;

}
dl.faq dt {
    color: #e1e4ea;
}
.codebox code {
    background-color: #272c35;
}
}

@-moz-document domain("bazar.lowcygier.pl") {
body {
    color: #ccc;
}
.navbar-inverse {
    background-color: #1f232a;
    border: none;
}
.navbar-inverse .navbar-nav > .active > a, .navbar-inverse .navbar-nav > .active > a:hover, .navbar-inverse .navbar-nav > .active > a:focus {
    color: #fff;
    background-color: #363d49;
}
.navbar-inverse .navbar-nav > .open > a, .navbar-inverse .navbar-nav > .open > a:hover, .navbar-inverse .navbar-nav > .open > a:focus {
background-color: #363d49;
}
.precontent {
    background: #363d49;
}
.information {
    background: #1f232a;
    border-radius: 5px;
}
.media-heading > a {
    color: #ddd;
}
.time,
.platform {
    color: #888;
}
.R_1 {
    background: #5b0000;
}
.R_2 {
    background: #c50000;
}
.R_3 {
    background: #ae6200;
}
.R_4 {
    background: #c9b400;
}
.R_5 {
    background: #509b1c;
}
.R_6 {
    background: #22420c;
}
.R_7 {
    background: #007ccf;
}
.R_8 {
    background: #005fcf;
}
.R_9 {
    background: #4d00cf;
}
.R_10 {
    background: #70025d;
}
.badge-new {
    color: #f08700;
    background-color: rgba(0, 0, 0, 0.5);
}
.gradient {
    background: #1f232a;
}
#exTab1 .nav-pills > li > a {
    border-top: 3px solid #1f232a;
    border-right: 1px solid #1f232a;
    border-left: 1px solid #1f232a;
    background-color: #363d49;
    color: #ccc;
    font-size: 16px;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:focus {
    border-top: 3px solid #1f232a;
    border-right: 1px solid #1f232a;
    border-left: 1px solid #1f232a;
    background-color: #23232d !important;
    box-shadow: 0 0 0px 0 rgba(0, 0, 0, 0.2);
}
.comment-striped {
    background-color: #1f232a!important;
}
#exTab1,
#exTab1 .tab-content {
    background-color: #23232d !important;
}
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:focus,
.nav-tabs > li.active > a:hover {
    background-color: #1f232a;
    color: #ccc;
    border: 1px solid #1f232a;
    border-bottom: none;
}
.nav > li > a:hover,
.nav > li > a:focus {
    text-decoration: underline;
    background-color: #eee;
}
.dropdown-menu,
.dropdown-menu > li > a {
    background-color: #363d49;
    color: #b3bbca;
}
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus {
    background-color: #1f232a;
    color: #ddd;
}
.dropdown-menu img.icon {
    background-color: #ccc;
    border: #ccc 5px solid;
    border-radius: 5px;
}
.btn-buy:hover {
    background-color: #f08700;
    border-color: #111111;
    color: #ffffff;
}
.btn-buy {
    background-color: #353c3b;
    border-color: #111;
}
a:hover,
.forum-content-area a:hover {
    color: #f08700;
}
a:visited {
    color: #777;
}
.tip-header {
    background-color: #363d49;
}
.tip-content {
    background-color: #1f232a;
}
.tooltip.top .tooltip-inner {
    border: #1f232a 1px solid;
    border-radius: 5px;
}
.input-lg.select2-container--krajee .select2-selection--single,
.input-group-lg .select2-container--krajee {
    background-color: #5e5e73;
}
.input-lg {
    background-color: #5e5e73 !important;
}
.pc {
    background-color: transparent !important;
}
.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: #1f232a;
}
.has-success .form-control,
.has-success .select2-container--krajee.select2-container--focus .select2-selection,
.has-success .select2-container--open .select2-selection,
.has-success .select2-container--krajee .select2-selection,
.has-success.select2-container--krajee .select2-dropdown,
.has-success .form-control:focus {
    border-color: #f08700;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 6px #f08700;
}
.form-control {
    background-color: #5e5e73;
    color: #111;
    border-color: #1f232a;
}
.easy-autocomplete-container > ul,
.select2-container--bootstrap .select2-selection--single .select2-selection__rendered,
.select2-container--bootstrap .select2-dropdown,
.select2-container--bootstrap .select2-selection,
.select2-container--bootstrap.select2-container--open .select2-selection {
    background-color: #1f232a;
    color: #ccc;
    font-size: medium;
    font-family: inherit;
}
.select2-container--bootstrap .select2-search--dropdown .select2-search__field {
    font-family: inherit;
}
.easy-autocomplete-container ul li.selected,
.select2-container--bootstrap .select2-results__option--highlighted[aria-selected],
.select2-container--bootstrap .select2-results__option[aria-selected=true] {
    background: none repeat scroll 0 0 #444;
}
.has-success .help-block,
.has-success .control-label,
.has-success .radio,
.has-success .checkbox,
.has-success .radio-inline,
.has-success .checkbox-inline,
.has-success.radio label,
.has-success.checkbox label,
.has-success.radio-inline label,
.has-success.checkbox-inline label {
    color: #f08700;
}
.messages {
    background-color: #1f232a;
    border-radius: 5px;
}
.easy-autocomplete-container > ul {
    border-color: rgb(221, 221, 221);
    border: 1px solid;
}
.easy-autocomplete input {
    border-color: #1f232a;
    color: #ccc;
}
.has-error .help-block,
.has-error .control-label,
.has-error .radio,
.has-error .checkbox,
.has-error .radio-inline,
.has-error .checkbox-inline,
.has-error.radio label,
.has-error.checkbox label,
.has-error.radio-inline label,
.has-error.checkbox-inline label {
    color: #e10400;
}
.alert-danger,
.error-summary {
    color: #e10400;
    background-color: #111;
    border: 1px solid #e10400;
}
.tooltip.top .tooltip-inner {
    border: 1px solid #23232d;
    background-color: #1f232a;
}
.statement {
    background-color: #ccc;
    border-color: #555;
}
.modal-body,
.modal-footer {
    background-color: #161616;
}
.message-positive .well {
    background-color: #90c03c;
    box-shadow: 0 0 20px 0 rgb(144, 192, 60);
}
.feed {
    color: #b3bbca;
}
.trader {
    color: #e1e4ea;
    font-weight: bold;
}
.navbar-nav > li.hover:hover {
    background-color: #363d49;
}  
.this {
    background-color: #1f232a !important;
}
.list-group-item {
    background-color: #363d49;
    border-color: #1f232a;
}
.well {
    border: 1px solid #1f232a;
    background-color: #363d49;
}
.numbers-stats {
    color: #fff;
}
.dropdown-list-dark {
    background-color: #1f232a;
}
hr {
    border-color: #1f232a;
}
.row.vertical-divider > div[class^="col-"] {
    border-left: 1px solid #1f232a;
}
    .row.vertical-divider div[class^="col-"]:first-child {
    border-left: 1px solid #1f232a;
}
    .row.vertical-divider div[class^="col-"]:last-child {
    border-right: 1px solid #1f232a;
}
.myactive > a {
    color: white !important;
}
#magic-line {
        border-top: solid 3px #1f232a;
}
.table-bordered > thead > tr > th, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > tbody > tr > td, .table-bordered > tfoot > tr > td {
    border: 2px solid #1f232a;
}
.table > tbody > tr > td, .table > tbody > tr > th, .table > tfoot > tr > td, .table > tfoot > tr > th, .table > thead > tr > td, .table > thead > tr > th {
    border-bottom: 1px solid #1f232a;
}
.nav-tabs {
    border-bottom: 1px solid #1f232a;
}
.dropdown-menu .divider {
    background-color: #272c35;
}

.message-positive .well {
    background-color: #567322;
    box-shadow: 0 0 10px 0 #567322;
    border: none;
}
.footer {
    background-color: #1f232a;
    color:#8b8b8b;
    border-top: none;
}
}
