css:
@-moz-document regexp("^https?:\/\/www\.unrealengine\.com\/marketplace.*") {
    
/*===============================  global start  ===================================*/
   
html body {
	background-color: #333;
	color: #fff;
}

input {
	background-color: #333;
}

hr {
	border-color: #808080;
}
    
/*================================  global end  ====================================*/
    
/*navigation bar*/ 

.asset-search .input-search {
	background-color: transparent;
	border-color: #666;
}

   .asset-search .input-search:focus-within {
	border-color: #1bf;
}

.asset-search .input-search input {
	background-color: transparent;
}

.asset-search .input-search input:focus {
	background-color: transparent !important;
	color: #ccc !important;
}

.asset-search .input-search span {
	color: #ccc;
}



.sub-nav-container .page-header-container > ul > li a {
	color: #ccc;
}

.sub-nav-container .page-header-container > ul > li:active,
.sub-nav-container .page-header-container > ul > li:focus,
.sub-nav-container .page-header-container > ul > li:hover {
	background-color: #454545;
}

.sub-nav-container .page-header-container > ul > li > ul li:not(:last-child) a {
	border-color: #333;
}

.store-page-asset a:active:not(.btn),
.store-page-asset a:focus:not(.btn),
.store-page-asset a:hover:not(.btn) {
	color: #fff;
}


.sub-nav-container .page-header-container {
	/*border-color: #666;*/
}

/*navigation bar end*/


/*asset tile*/
.asset-container .asset {
	background-color: #454545;
	transition-duration: 0.5s;
}

.asset-container .asset:active,
.asset-container .asset:focus,
.asset-container .asset:hover {
	background-color: #505050;
	box-shadow: 0 0 8px #000;
	transition-duration: 0.1s;
}

.asset-container .asset .info h3 a {
	color: #ccc;
}

.asset-container .asset .info h3 a:hover {
	color: #fff;
	transition-duration: 0.1s;
}

.asset-container .asset .info .creator a:hover {
	color: #18c;
	transition-duration: 0.1s;        
}

.asset-container .asset .info .details {
	border-color: #333;
}


.store-page-asset .asset .info ul li.categories ul.categories-full-view {
	background-color: #333;
}

/*asset tile end*/


/*tiles view*/

.sort-filter-section .current-sort:active,
.sort-filter-section .current-sort:focus,
.sort-filter-section .current-sort:hover,
.sort-filter-section .filter-icon:active,
.sort-filter-section .filter-icon:focus,
.sort-filter-section .filter-icon:hover {
	background-color: #454545;
	color: #fff;
}

.sort-filter-section .open .current-sort,
.sort-filter-section .open .filter-icon {
	background-color: #454545;
	color: #fff;
}

.sort-filter-section .filter-dropdown,
.sort-filter-section .sort-options {
	background-color: #454545;
	box-shadow: 3px 5px 7px 0 #0006;
}

.sort-filter-section .filter-dropdown .price-range .custom-range input {
	border-color: #666;
}

.sort-filter-section .filter-dropdown .price-range .custom-range input:focus {
	border-color: #1bf;
}


.sort-filter-section .filter-dropdown .show-all-versions,
.sort-filter-section .filter-dropdown li {
	background-color: #666;
}

.sort-filter-section .filter-dropdown .price-range .custom-range span:first-child {
	color: #333;
	background-color: #1bf;
}

.sort-filter-section .filter-dropdown .show-all-versions:active,
.sort-filter-section .filter-dropdown .show-all-versions:focus,
.sort-filter-section .filter-dropdown .show-all-versions:hover,
.sort-filter-section .filter-dropdown li:active,
.sort-filter-section .filter-dropdown li:focus,
.sort-filter-section .filter-dropdown li:hover {
	color: #333;
	background-color: #1bf;
}

/*tiles view end*/



/*Asset page*/

.store-page-asset h1,
.store-page-asset h2,
.store-page-asset h3,
.store-page-asset h4,
.store-page-asset h5,
.store-page-asset h6 {
	color: #808080;
}


.asset-acquire-callout {
	background-color: #454545;
}

.asset-acquire-callout .asset-acquire-right {
	border-color: #333;
}

.asset-acquire-callout .asset-acquire-right .row .label {
	color: #999;
}


.asset-comments,
.asset-description-wrapper {
	border-color: #808080;
}

/*tags*/
.asset-desc.tags a {
	background-color: transparent;
	border: 1px solid #666;
	border-radius: 14px;
	padding: 0 10px;
}
.asset-desc.tags a:hover {
	background-color: #454545;
}

/*search*/
.asset-search ul.autocomplete {
	background-color: #393939;
	border-color: #808080;
}

.asset-search ul.autocomplete li:hover {
background-color: #454545;
}
/*search end*/
    
/*comments*/

.comments {
	color: #ccc;
}


.asset-details-container .asset-content-container .asset-comments .comments .comment .comment-info-box .comment-body div.textile-container-view {
	background-color: #454545;
}

.asset-details-container .asset-content-container .asset-comments .comments .comment .comment-info-box .comment-body div.textile-container-view::before {
	border-top-color: #454545;
}


.asset-details-container .asset-content-container .asset-comments .add-comment textarea,
.asset-details-container .asset-content-container .asset-comments .comment-add-reply textarea {
	color: #ccc;
	background-color: transparent;
	border-color: #666;
}

.textile-container ul.textile-head li,
.textile-container-view ul.textile-head li {
	background-color: #666;
	border: none;
}

.textile-container ul.textile-head li:hover,
.textile-container-view ul.textile-head li:hover {
	background-color: #1bf3;;
}

.textile-container ul.textile-head li.textile-selected,
.textile-container-view ul.textile-head li.textile-selected {
	background-color: #0aaff1;
}

.asset-details-container .asset-content-container .asset-comments .pagination a,
.asset-details-container .asset-content-container .asset-comments .pagination span {
	color: #ccc;
}

.store-page-asset .pagination a,
.store-page-asset .pagination span {
	border: none;
	background-color: #666;
}

.store-page-asset .pagination span {
	color: #ccc;
}


/*comments end*/

/*asset page end*/

/*checkout*/
#purchase-app .location-bar-container .location-bar-inner .location-bar .navigation-element {
	color: #999;
}  
/*checkout end*/
    
}
