css:
@-moz-document domain("ent.insa-toulouse.fr") {
.btn,
.portletContainer {
	box-shadow: 2px 2px 4px #cfcfcf;
	border-radius: 4px;
}
.btn:hover {
	box-shadow: 3px 2px 8px #666;
}
#tabs ul li,
#tabs ul li div.onglet,
#tabs ul#chanList li div.onglet,
#tabs ul li div.tab-edit,
#tabs ul li div.editable-tab {
	background-image: none;
	background-color: #4F4D50;
	box-shadow: 2px 2px 4px #888;
	border-radius: 4px;
}
#tabs ul#chanList li {
	background-image: none;
	background-color: #E42719;
	box-shadow: 2px 2px 4px #888;
}
#tabs ul li.espace,
#tabs ul li.espace div.onglet {
	background-image: none;
	background-color: #F0A44F;
}
#tabs ul li.espace:hover div.onglet,
#tabs ul li:hover div.onglet,
#tabs ul li.espace:hover,
#tabs ul li:hover,
#tabs ul#chanList li div.onglet:hover,
#tabs ul li.editable-tab div.onglet,
#tabs ul li.editable-tab {
	background-image: none;
	background-color: #E42719;
}
#tabs ul li div.onglet a {
	color: #fff;
}
#tabs ul li.editable-tab div.onglet a,
#tabs ul li:hover div.onglet a,
#tabs ul li div.tab-edit a,
.editable-tab a {
	color: #fff;
}
#tabs li a:hover,
#tabs ul#chanList li a:hover {
	text-decoration: none;
}
.portlet-toolbar h2 {
	background-image: none;
	color: #fff;
	text-shadow: 1px 1px 2px #555;
	border-radius: 4px;
}
.portlet-toolbar {
	box-shadow: 2px 2px 4px #cfcfcf;
	background-image: none;
	background-color: #E42719;
	border-radius: 4px;
}
.bienvenue {
	color: #E42719;
	font-size: 1em;
}
#tabs ul li a {
	text-shadow: 1px 1px 2px #555;
	font-weight: bold;
	padding-top: 6px;
	padding-bottom: 6px;
	padding-left: 20px;
	padding-right: 20px;
	line-height: 27px;
}
#tabs ul li div.onglet {
	text-transform: uppercase;
	margin-left: 0px;
	padding-left: 0px;
	padding-right: 0px;
}
.ent {
	color: #E42719;
	text-shadow: 1px 1px 2px #ccc;
}
.preference-items a {
	text-shadow: 1px 1px 2px #ccc;
}
.preference-items a:hover {
	color: #E42719;
	text-shadow: 1px 1px 4px #888;
}
}
