css:
@-moz-document url-prefix("http://baike.baidu.com"), url-prefix("https://baike.baidu.com") {
body {
	background: #f2f0e8 !important;
}
#J-declare,
#J-declare-wrap,
.declare-wrap,
.navbar-wrapper,
.side-content,
.after-content,
#side-share,
#suggestion {
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	background: none;
}
.header,
.layout,
.gt-searchbar {
	height: 40px;
}
.search {
	margin: 0 !important;
	padding: 0 !important;
	-webkit-box-sizing: border-box !important;
	box-sizing: border-box !important;
	position: inherit !important;
	height: 48px;
}
.wgt-searchbar {
	width: auto;
	float: left;
	margin-left: 0;
}
.header-wrapper {
	background: #3498db !important;
	height: 60px !important;
	margin-top: 0 !important;
	position: fixed;
	width: 100% !important;
	top: 0;
	overflow: hidden;
}
.header-wrapper .header {
	margin-top: 6px;
}
.header-wrapper .header .layout {
	width: 1280px !important;
}
.header-wrapper .topbar {
	position: fixed;
	top: 0;
	right: 50px;
	z-index: 1;
	border: none;
}
.header-wrapper .topbar .wiki-common-headTabBar,
.header-wrapper .topbar .separator {
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
.header-wrapper .topbar .wgt-userbar {
	height: 40px;
	margin-top: 8px !important;
	overflow: hidden;
}
.header-wrapper .topbar .wgt-userbar li {
	padding: 4px;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	line-height: 40px;
	float: left;
	margin-left: 20px !important;
}
.header-wrapper .topbar .wgt-userbar a {
	color: #fff !important;
	font-size: 14px;
	text-decoration: none;
}
.header-wrapper .topbar .wgt-userbar li:first-child,
.header-wrapper .topbar .wgt-userbar .userbar_mall {
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
.header-wrapper .topbar .wgt-userbar .userbar_user {
	height: inherit;
}
.header-wrapper .logo-container {
	margin: 0 !important;
	padding: 0 !important;
	-webkit-box-sizing: border-box !important;
	box-sizing: border-box !important;
	position: inherit !important;
	width: 48px;
	height: 48px;
	background: #fff;
	border-radius: 8px 0 0 8px;
	box-sizing: border-box;
}
.header-wrapper .logo-container .cmn-icons_logo-bai,
.header-wrapper .logo-container .cmn-icons_logo-baike {
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
.header-wrapper .logo-container .cmn-icons_logo-du {
	margin: 0;
	padding: 0;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	position: inherit;
	width: 48px;
	height: 40px;
	font-size: 30px !important;
	line-height: 48px;
	text-align: center;
	margin-left: 8px;
	padding-right: 8px;
	border-right: 1px solid blue;
}
.header-wrapper #searchForm {
	margin: 0;
	padding: 0;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	position: inherit;
	height: 60px;
}
.header-wrapper #searchForm input,
.header-wrapper #searchForm button {
	height: 48px;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	border: none;
	outline: none;
	margin-top: 0;
}
.header-wrapper #searchForm input {
	width: 500px;
}
.header-wrapper #searchForm input:focus {
	border: none;
}
.header-wrapper #searchForm button {
	height: 48px;
	margin: 0;
}
.header-wrapper #searchForm #search {
	width: 48px;
	overflow: hidden;
	border-radius: 0 8px 8px 0 !important;
	position: relative;
	z-index: 1;
	margin-left: -4px;
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}
.header-wrapper #searchForm #search:hover::after {
	-webkit-box-shadow: none;
	box-shadow: none;
	background: #ecf0f1;
	color: #2980b9;
	-webkit-transition: background .4s;
	transition: background .4s;
}
.header-wrapper #searchForm #search::after {
	content: "GO";
	position: absolute;
	z-index: 2;
	top: 0;
	left: 0;
	bottom: 0;
	right: 0;
	background: #2980b9;
	color: #ecf0f1;
	line-height: 48px;
	-webkit-transition: background .35s;
	transition: background .35s;
}
.header-wrapper #searchForm #searchLemma {
	border-radius: 0 8px 8px 0;
	margin-left: -4px;
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
.header-wrapper #searchForm a {
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
.body-wrapper {
	position: fixed;
	top: 60px;
	bottom: 0;
	overflow-y: scroll;
	overflow-x: hidden;
	height: 92%;
	width: 100%;
}
.poster,
.poster * {
	border: none !important;
}
.body-wrapper .content {
	/* 	background: none !important; */

	width: 1280px !important;
	border: none !important;
}
.body-wrapper .content::-moz-selection {
	background: #34495e;
	color: #eaeded;
}
.body-wrapper .content::selection {
	background: #34495e;
	color: #eaeded;
}
.body-wrapper #errFormSub,
.body-wrapper #fixBox {
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
.body-wrapper .main-content {
	width: 1280px !important;
	border: none !important;
	-webkit-column-count: 2;
	column-count: 2;
	-webkit-column-gap: 60px;
	column-gap: 60px;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	padding: 12px !important;
	border-radius: 8px !important;
	/* 	background: none !important; */

	position: none !important;
	word-wrap: break-word;
	word-break: break-all;
}
.body-wrapper .main-content .basic-info {
	background: none;
}
.body-wrapper .page-background {
	/* 	background-color: transparent !important; */
	/* 	background: auto; */
}
.body-wrapper .main-content #hotspotmining_s .active {
	color: #000 !important;
	background: none;
	border-left: 12px solid #3498db;
	height: 24px;
	line-height: 24px;
	font-size: 22px;
}
.body-wrapper .main-content #hotspotmining_s #hotspotminingContent-226031 {
	padding-left: 0;
	padding-right: 0;
}
.body-wrapper .main-content #hotspotmining_s .hotspotminingContent_cnt .hotspotminingContent_cnt_tit {
	overflow: auto !important;
	margin: 0;
	padding: 0;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	position: inherit;
	height: 30px;
}
#hotspotmining_s .hotspotminingContent .hotspotminingContent_cnt .hotspotminingContent_cnt_cnt {
	overflow: auto;
	width: 100% !important;
	height: auto !important;
}
.body-wrapper .main-content .para-title,
.custom-title {
	margin-left: 0 !important;
	border-left-color: #3498db;
	background: none !important;
}
.body-wrapper .main-content .para {
	text-align: justify;
	text-indent: 0;
	overflow: auto;
	margin-bottom: 1.2em;
	overflow: auto;
	width: 100% !important;
}
.body-wrapper .main-content .lemma-album {
	display: block;
	overflow: hidden;
	margin: 0;
	padding: 0;
	-webkit-box-sizing: border-box;
	box-sizing: border-box !important;
	position: inherit;
	margin: 0 6px;
}
.body-wrapper .main-content .lemma-picture,
.body-wrapper .main-content .hotspotminingContent_pic,
.body-wrapper .main-content .album-wr {
	max-width: 100%;
	/* 	padding: 4px; */

	background-color: #fff !important;
	border: 1px solid #ddd;
	border-radius: 4px;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
	/* 	margin: 1em; */
}
.body-wrapper .main-content .lemma-picture .description,
.body-wrapper .main-content .hotspotminingContent_pic .description,
.body-wrapper .main-content .album-wrap .description {
	text-align: center;
}
.body-wrapper .hotspotminingNavbar,
.body-wrapper .title-text,
.body-wrapper .edit-icon {
	background: none !important;
}
.body-wrapper p,
.body-wrapper div,
.body-wrapper font,
.body-wrapper span {
	font: 300 17px/1.8 PingFang SC, Lantinghei SC, Microsoft Yahei, Hiragino Sans GB, Microsoft Sans Serif, WenQuanYi Micro Hei, sans-serif !important;
	background-color: transparent !important;
}
.body-wrapper .top-tool {
	position: relative;
	top: -10px;
	left: 0;
	width: 365px;
	height: 40px;
	line-height6: 40px !important;
	font-size: 16px !important;
}
.body-wrapper .top-tool .cmn-icon {
	line-height: 40px;
}
.body-wrapper .top-tool > * {
	border: none !important;
}
.body-wrapper .top-tool .top-share-list {
	border: none !important;
	font-size: 16px !important;
}
.body-wrapper .top-tool #top-share {
	position: absolute;
	z-index: 100000;
	padding-top: 0;
	top: -2px;
	left: 47px;
	display: block !important;
	border: none;
}
.wgt-footer-main {
	background: none;
	display: none;
	padding: none;
	margin: none;
	-webkit-box-sizing: border-box;
	box-sizing: border-box;
}
#passport-login-pop {
	position: fixed;
	background: #f2f0e8;
	width: 100%;
	height: 100%;
	left: 0 !important;
	top: 0 !important;
	transition: background .4s;
}
#passport-login-pop .tang-background,
#passport-login-pop .tang-foreground {
	width: 578px;
	height: 450px;
	position: absolute;
	top: 50%;
	left: 50%;
	margin: -225px auto auto -289px;
	border-radius: 4px !important;
}
#passport-login-pop .tang-foreground {
	box-shadow: 0 0 8px 4px rgba(0, 0, 0, .2);
	padding: 0;
	height: 378px;
	border-radius: 10px !important;
	box-sizing: content-box;
	overflow: hidden;
}
#passport-login-pop .tang-title {
	position: relative;
	z-index: 2;
	background: #fff;
}
#passport-login-pop .tang-title span {
	text-align: center;
	font-size: 20px;
	margin-top: 20px;
	margin-bottom: -20px;
	font-weight: bold !important;
	font-family: PingFang SC, Lantinghei SC, Microsoft Yahei, Hiragino Sans GB, Microsoft Sans Serif, WenQuanYi Micro Hei, sans-serif !important;
	background: url(https://passport.baidu.com/passApi/img/baidu_logo_24.png) 205px 5px no-repeat !important;
}
#passport-login-pop .Qrcode-status-con {
	text-align: center;
	margin: -12px auto 20px 11px;
	background: none !important;
}
#passport-login-pop .Qrcode-status-con img {
	border: 1px solid #ddd;
	padding: 16px;
	border-radius: 4px;
}
#passport-login-pop .pass-text-input {
	border-radius: 4px !important;
}
#passport-login-pop .pass-login-pop-content {
	position: relative;
	z-index: 1;
}
#pass-phoenix-login {
	margin: -68px auto auto auto;
	position: relative;
	z-index: 20;
	border-top: none !important;
}
/* 清除广告 */

.right-ad,
.polysemant-list {
	display: none;
}
/* 目录效果 */

.lemmaWgt-lemmaCatalog {
	overflow: auto;
	width: 100% !important;
}
.lemmaWgt-lemmaCatalog::-webkit-scrollbar {
	width: 10px;
	height: 0px;
	background-color: rgba(0, 0, 0, 0);
}
.lemmaWgt-lemmaCatalog:hover::-webkit-scrollbar {
	width: 10px;
	height: 4px;
	background-color: rgba(0, 0, 0, 0);
}
.lemmaWgt-lemmaCatalog::-webkit-scrollbar-thumb {
	border-radius: 8px;
	background-color: rgba(0, 0, 0, 0.2);
	transition: all 0.4s ease;
	-moz-transition: all 0.4s ease;
	-webkit-transition: all 0.4s ease;
	-o-transition: all 0.4s ease;
}
.lemmaWgt-lemmaCatalog::-webkit-scrollbar-thumb:hover {
	border-radius: 3px;
	background-color: rgba(0, 0, 0, 0.6);
	transition: all 0.4s ease;
	-moz-transition: all 0.4s ease;
	-webkit-transition: all 0.4s ease;
	-o-transition: all 0.4s ease;
}
.lemmaWgt-lemmaCatalog::-webkit-scrollbar-track {
	background-color: rgba(0, 0, 0, 0.1);
}
/* 滚动条样式 */

::-webkit-scrollbar {
	width: 4px;
	height: 10px;
	background-color: rgba(0, 0, 0, 0);
}
::-webkit-scrollbar-thumb {
	border-radius: 3px;
	background-color: rgba(0, 0, 0, 0.2);
	transition: all 0.4s ease;
	-moz-transition: all 0.4s ease;
	-webkit-transition: all 0.4s ease;
	-o-transition: all 0.4s ease;
}
::-webkit-scrollbar-thumb:hover {
	border-radius: 3px;
	background-color: rgba(0, 0, 0, 0.6);
	transition: all 0.4s ease;
	-moz-transition: all 0.4s ease;
	-webkit-transition: all 0.4s ease;
	-o-transition: all 0.4s ease;
}
::-webkit-scrollbar-track {
	background-color: rgba(0, 0, 0, 0.1);
}
::-moz-selection {
	background: #34495e;
	color: #eaeded;
}
::selection {
	background: #34495e;
	color: #eaeded;
}
/* 表格样式 */

table,
tr,
td,
th,
tbody,
thead,
tfoot,
.cell-module {
	border: 0 !important;
	background: transparent !important;
}
.iframe-module {
	overflow: scroll;
	width: 100%;
}
}
