@namespace url(http://www.w3.org/1999/xhtml);

/* i really want this to be global */

/* EOL Tweaks 3.3.5 */

/* PORTADA - PUBLICIDAD */

/* Quita la publicidad de la portada */

    div.text-center.visible-lg-block, div.news-ad.text-center, div.anno, .dfp-queue {
        display: none !important;
    }

/* PORTADA - GENERAL */

/* Ajusta el ancho de portada, eliminando los huecos laterales (Para cuando meten publicidad intrusiva) */

    body {
        padding-right: 0px !important;
        padding-left: 0px !important;
    }

/* Tipografía de las noticias */

    .new .wrap .body {
        font-size: 17px !important
    }

/* Elimina el borde de la imagen de tu avatar de la parte superior */

    #u-avatar {
        box-shadow: none !important
    }
        
/* Hace tu avatar mas grande */

    #u-avatar img {
        max-height: 50px !important;
        max-width: 50px !important;
	border-radius: 10px !important;
        border: 2px solid #008d0f !important
    }

/* Alinea el nick de tu avatar */

    #u-nick.logged  {
        margin-top: 12px !important;
        margin-left: 12px !important
    }
     
/* Elimina la caja de búsqueda lateral redundante ya que puedes usar la de la parte superior */
     
    form#n-search {
        display: none !important;
    }

/* Quitar Slider de noticias */
     
    #slider {
        display: none !important;
    }

/* Quita el fondo verde punteado de la cabecera y lo reemplaza por un color sólido */
     
    #header {
        background: none !important;
        background-color: #395a2f !important;
    }
     
/* Estiliza las noticias, tanto fijas como dinamicas, también las individuales */
     
    .slide.notext, .new.left, .new.right, #news-single {
        border-style: dashed !important;
        border-color: rgba(255,153,51,0.85) !important;
        border-width: 3px !important;
        background-repeat: no-repeat !important;
        background-color: black !important;
    }

/* Estiliza la caja de las noticias breves */

	#shortnews {
		background-color: #ff7f00 !important;
		border-style: dashed !important;
		border-color: rgba(0,128,0,0.85) !important;
		border-width: 3px !important
	}

/* Caja de usuario */

	.h-popup-user#h-popup {
		background-color: #395a2f !important;
	}

	a#ucp-cp, a#ucp-pm, a#ucp-sub, a#ucp-fav, a#ucp-posts, a#ucp-threads, a#ucp-part, a#ucp-push, a#ucp-mentions, a#ucp-logout, a.open.active {
		color: #c2c2c2 !important;
	}

/* FORO - GENERAL */

/* Añade un color a la barra de menú */
     
    #menu {
        background-color: #008000 !important;
    }
    
/* Tamaño global de la tipografía */

    body {
        font-size: 17px !important
    }
    
/* Tamaño de la tipografía en el campo de escritura del post */

    .form-control {
        font-size: 17.5px !important
    }

/* Tamaño de la tipografía en las citas */

	blockquote {
		font-size: 14px !important
	}
     
/* Tipografía */

   /*  body {
        font-family: "Noto sans", "Noto serif" !important
    }  */

/* Redondea el avatar */

    .section .post .profile .avatar img {
    	border-radius: 15px !important;
    	border: 2px solid #008000 !important;	 
    }

	.avatar.online {
		border-bottom: 3px dashed #ff7f00 !important;
	}

/* Elimina los posts de los ignorados */

    .ignored {
        display: none !important;
    }

/* Pinta de verde los formularios */

	.form-control {
		background-color: #008000 !important;
		color: #c2c2c2 !important
	}

/* Suscripciones Push (https://www.elotrolado.net/webpush.php) */

	#subscription-list {
		background-color: #395a2f !important
	}

/* Cambia el color en las citas */

/*	blockquote.uncited {
		background-color: #417531 !important
	}

	blockquote {
		background-color: #395a2f !important
	}*/

    
