css:
@-moz-document domain("boras.pingpong.se") {
body {
 background-color: #414141; 
    color: #999999;
}

#login-wrapper {
    border: 3px solid #424242;
    background-color: #252525;
}

.action-section, .table-tag-wrapper, .box, #content-frame-wrapper, .entry, #blog .comments-list, .back-action, .dialog, .accordion_toggle, .dialog .actionprogram-template-preview .box {
    background: #303030;
    margin-bottom: 1em;
    border-radius: 6px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.05);
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
}

#header {
    background: #303030 url('https://boras.pingpong.se/images/toppbanner.png') no-repeat top left;
}

#header-shortcuts ul li a {
    color: #999999;
    text-decoration: none;
}

.action-section a, tr.link a, #contentNodeTree a, .rte-nav a, .rte-nav-bottom a, .selection-list-box a, #EntranceMessage a, #ContactList a, #ProjectGroups a, .podcast-start a, .back-action a {
    color: #999999;
    text-decoration: none;
}

#bread-crumbs {
    font-size: 0.9em;
    background: #303030;
    padding: 0.5em 2em;
    border-radius: 0 0 6px 6px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.05);
}

#nav {
    background: #47a2b4;
    background: -moz-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #7dd6e9), color-stop(100%, #136f80));
    background: -webkit-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: -o-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: -ms-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: linear-gradient(to bottom, #393939 0%, #303030 100%);
}

#nav .nav-sub, #nav .nav-sub-sub {
    color: #999999;
    background: #424242;
    display: none;
    position: absolute;
    left: 0;
    width: 14em;
    margin: 0;
    padding: 0.5em;
    list-style: none;
    z-index: 10000;
    box-shadow: 0 2px 6px rgba(0,0,0,0.15);
}

#nav .nav-sub-item-link {
    color: #999999;
    text-decoration: none;
    display: block;
    padding: 0.3em 0.5em;
    overflow-x: hidden;
    text-overflow: ellipsis;
}

.a-block li a, .discuss-overview-list ul li a, .a-block dt a, .discuss-overview-list ul dt a {
    color: #999999;
    text-decoration: none;
    display: block;
    padding: 0.25em;
    overflow-x: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

.box-header-inner, thead.box-header {
    background: #47a2b4;
    background: -moz-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #7dd6e9), color-stop(100%, #136f80));
    background: -webkit-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: -o-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: -ms-linear-gradient(top, #7dd6e9 0%, #136f80 100%);
    background: linear-gradient(to bottom, #393939 0%, #303030 100%);
}

.even {
    background-color: #353535;
}

#pimInboxTable {
    background: #353535;
}

.default-table td a {
    color: #999999;
}

input[type=text], input[type=password], input[type=number], input[type=url], .character-input, .textarea, textarea {
    background: #353535;
    margin: 0.5em 0;
    padding: 0.6em;
    border: 1px solid #b6b6b6;
    border-radius: 3px;
    max-width: 100%;
}

input[type=text]:hover, input[type=text]:focus, input[type=text]:active, input[type=password]:hover, input[type=password]:focus, input[type=password]:active, input[type=number]:hover, input[type=number]:focus, input[type=number]:active, input[type=url]:hover, input[type=url]:focus, input[type=url]:active, .character-input:hover, .character-input:focus, .character-input:active, .textarea:hover, .textarea:focus, .textarea:active, textarea:hover, textarea:focus, textarea:active {
    color: #FFF;
    background: #353535;
    border: 1px solid #959595;
}

#nav #main-navigation .nav-top-item:hover, #nav #main-navigation .nav-top-item:focus, #nav #main-navigation .nav-top-item:active, #nav #main-navigation .nav-top-item.active {
    background: #303030;
}

input[type="submit"], input[type="button"], button {
    cursor: pointer;
    text-shadow: 0 1px 0 #000000;
    background: #ffffff url('https://boras.pingpong.se/images/button.png') repeat-x scroll left top;
    background-image: -moz-linear-gradient(top, #fafafa 0%, #ebebeb 100%);
    background-image: -o-linear-gradient(top, #fafafa 0%, #ebebeb 100%);
    background-image: -webkit-linear-gradient(top, #8e5555 0%, #040000 100%);
    background-image: linear-gradient(to bottom, #393939 0%, #303030 100%);
    padding: 0.8em 1em;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    width: 100%;
}

.default-table {
    background: #353535;
    width: 100%;
    margin: 0 0 1em;
    padding: 0;
    border-collapse: collapse;
    border: 1px solid #dfdfdf;
}

.default-table th {
    color: #4b4b4b;
    background: #f2f2f2;
    background: -moz-linear-gradient(top, #f9f9f9 0%, #f2f2f2 100%);
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%, #f9f9f9), color-stop(100%, #f2f2f2));
    background: -webkit-linear-gradient(top, #f9f9f9 0%, #f2f2f2 100%);
    background: -o-linear-gradient(top, #f9f9f9 0%, #f2f2f2 100%);
    background: -ms-linear-gradient(top, #f9f9f9 0%, #f2f2f2 100%);
    background: linear-gradient(to bottom, #393939 0%, #303030 100%);
    padding: 1em 0.5em;
}

input[type=button], input[type=submit], input[type=reset], .button, .link-button {
    color: #4b4b4b;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    cursor: pointer;
    text-shadow: 0 1px 0 #000;
    text-decoration: none;
    display: inline-block;
    margin: 0.5em 0;
    padding: 0.6em 1em;
    border: 1px solid #b6b6b6;
    background-color: #f0f0f0;
    border-radius: 3px;
    -moz-user-select: none;
    -webkit-user-select: none;
    -ms-user-select: none;
}

#chatScrollArea, #scrollArea {
    height: 30em;
    overflow: auto;
    background: #303030;
    padding: 0 1em;
    border: 1px solid #dbdbdb;
    border-radius: 6px;
    box-shadow: 0 2px 6px rgba(0,0,0,0.05);
}
}
