css:
@-moz-document domain("wordpress.com"), domain("insanitycave.poetry.blog") {
* {background-color: #2a2a2a; color: #ccc;}
div {background-color: #2a2a2a !important; color: #ccc !important;}
body {background: none !important;background-color: #0d0d0d !important; color: #ccc}

.hentry, .page-header, .page-content {background-color: #2a2a2a; color: #ccc;}
.site-main .paging-navigation, .site-main .post-navigation {background-color: #2a2a2a; color: #ccc;}

#content-wrapper {padding: 0px;}
    
a {color: #C9C4C4;}
a:hover {color: #C4B88A;}
span {color: #C9C4C4 !important;}
site-navigation {background: none !important;}
site-navigation {background-color: #131313 !important;}
comments {background-color: #241d1d !important; color: #afafaf;}
#secondary .widget {background-color: #2a2a2a !important;}
.commentlist > li.comment {background-color: #303030 !important; color: #afafaf;}
.commentlist .children li.comment {background-color: #303030 !important; color: #afafaf;}
.sp-wrap{background: none; background-color: #3c3c3c; border-color: #C9C4C4;}
.sp-body {background: none !important;background-color: #0d0d0d !important;}
.singular .entry-title {color: #bbb !important;}
.stacked-list {background: none !important;background-color: #0d0d0d !important;}
.stacked-list-item {background: none !important;background-color: #0d0d0d !important;}
.stacked-list-item:hover {background: none !important;background-color: #C4B88A !important;}
.stacked-list-item.collapsible {background: none !important;background-color: #282828 !important;}
.stacked-list-item .collapsible-header{background-color: #0d0d0d !important;}
#content h1, #content h2, #content h3, #content h4, #content h5, #content h6 {color: #999;}
}

@-moz-document domain("yukinotane.wordpress.com") {
.site-header {width: 10%;}
}

@-moz-document domain("omochikaeri.wordpress.com") {
#container {border-top: solid #2a2a2a;}
#subcontent{box-shadow: 0 0 0 15px #2a2a2a inset;}
#container form {background-color: #2a2a2a !important;}
#container form input {background-color: #bbb !important; color: #222 !important;}

#subcontent table td a {background-color: #2a2a2a !important; color: #595959 !important;}

.entry .info {background-color: #2a2a2a !important; color: #ccc !important;}
#comments .header {background-color: #edf3de !important; color: #000 !important;}
#comments .header strong {background-color: transparent !important; color: #000 !important;}
#comments .header span {background-color: transparent !important; color: #000 !important;}
#comments .header .url {background-color: transparent !important; color: #000 !important;}

}
