css:
@-moz-document domain("musicpleer.audio") {
body.ui-mobile-viewport.ui-overlay-a {
    background-color: #1d1d1d;
}
.ui-content {
    border-width: 0;
    overflow: visible;
    overflow-x: hidden;
    padding: 1em;
   background-color: #1d1d1d;
}
.ui-collapsible-inset .ui-collapsible-heading .ui-btn {
    border-right-width: 1px;
    border-left-width: 1px;
    background-color: #ff9a00;
    color: #1b1b1b;
}
.ui-collapsible-content>.ui-listview:not(.ui-listview-inset)>li.ui-first-child, .ui-collapsible-content>.ui-listview:not(.ui-listview-inset)>li.ui-first-child>a.ui-btn {

    background-color: #684613;
    color: white;
}
a.ui-btn.ui-btn-icon-right.ui-icon-carat-r {
     background-color: #684613;
    color: white;
}
.ui-listview>li.ui-last-child, .ui-listview>li.ui-last-child>a.ui-btn {
  
    background-color: #7c5110;
    color: white;
}
a.ui-btn {
    background-color: #7c5110!important;
}
a.ui-btn h3 {
    color: #ffffff;
}
.ui-listview>li p {
    
    color: white;
}
a.ui-btn.ui-shadow.ui-btn-corner-all.ui-btn-up-c.icon-download {
    color: #ffffff;
}
.mp3info .ui-block-a {
   
    color: white;
}
.ui-overlay-a, .ui-page-theme-a, .ui-page-theme-a .ui-panel-wrapper {
   
    text-shadow: 0 1px 0 #000000;
}
.ui-page-theme-a .ui-btn, html .ui-bar-a .ui-btn, html .ui-body-a .ui-btn, html body .ui-group-theme-a .ui-btn, html head + body .ui-btn.ui-btn-a, .ui-page-theme-a .ui-btn:visited, html .ui-bar-a .ui-btn:visited, html .ui-body-a .ui-btn:visited, html body .ui-group-theme-a .ui-btn:visited, html head + body .ui-btn.ui-btn-a:visited {
   
    text-shadow: 0 1px 0 #000000;
}
a.ui-collapsible-heading-toggle.ui-btn.ui-icon-minus.ui-btn-icon-left.ui-btn-inherit.ui-mini {
    color: #ffffff;
}


#player, #search {
    
    background-color: #1d1d1d;
}
.myfooter a {
    color: #dddddd!important;
}
.mp3info .ui-block-b {

    color: #ffffff;
}
.ui-collapsible-inset.ui-collapsible-themed-content .ui-collapsible-content {

    color: white;
background-color: #1d1d1d;
}
.ui-body-a, .ui-page-theme-a .ui-body-inherit, html .ui-bar-a .ui-body-inherit, html .ui-body-a .ui-body-inherit, html body .ui-group-theme-a .ui-body-inherit, html .ui-panel-page-container-a {
    background-color: #714400;

    color: #ffffff;
    text-shadow: 0 1px 0 #1d1d1d;
}
a.ui-btn.ui-btn-icon-right.ui-icon-arrow-r {
    color: #ffffff;
}
.ajaxSubmit {
 
    background: #7c5110;
}

}
