css:
@-moz-document domain("pccasegear.com") {
:root {
	--bg0: #090909;
	--bg1: #111;
	--bg2: #151515;
	--bg3: #1c1c1c;
	--bg4: #222;
	--bg5: #252525;
	--bg6: #2c2c2c;
	--bg7: #333;

	--colourF: #fff;
	--colourD: #ddd;
	--colourB: #bbb;

	--blue: #2196F3;
	--blueHover: #1976D2;
}
  
  
body, html, #main_container, .mob_fix_header, footer {
  background: var(--bg0);
}

.main_container, .top-page-link-bg, .filter-group-wrapper {
  background: var(--bg1)
}

b, strong, a, h1.page-heading {
  color: var(--blue);
}
b, strong:hover, a:hover {
  color: var(--blueHover);
}



.navigation, .left_cat_menu, .left_cat_menu > li, .cat_sub_menu {
  background: var(--bg3);
  border-color: var(--bg4)
}
.menu_head strong {
  color: var(--colorF) !important;
}
.left_cat_menu > li > a:focus, .left_cat_menu > li > a:hover, .cat_sub_menu::before, .navigation .navbar-nav > li > a:hover, .faq-group, #contact-us .contact-form-details .write-to-us {
  background: var(--bg4) !important;
}
.table-border {
  border: 1px solid var(--bg3);
  background: var(--bg3);
}
.table > tbody > tr > td {
  border-top-color: var(--bg4) !important;
}
.nav > li > a:focus, .nav > li > a:hover {
  background: var(--bg3)
}
.dash-tab-bg, .dash-tab.dash-tab {
  border-color: var(--bg3)
}
.dash-tab-on, .dash-tab-on:hover {
  background: var(--bg4) !important;
}
.cat_des, .white_box_shipping, .white_box, option {
  background: var(--bg3);
  border-color: var(--bg3)
}
.form-control {
  background: var(--bg4);
  border-color: var(--bg4)
}
.feed_form, .s_cart_table tr, .my_order_tbl.d_table > ul > li, .panel-default > .panel-heading, .brand-group {
  border-color: var(--bg3)
}
.form-control:focus {
  box-shadow: inset 0 1px 1px rgb(33,150,243),0 0 8px rgb(33,150,243);
  border-color: rgb(33,150,243);
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control, .login-bg, .panel, .faq-groups {
  background: var(--bg2);
  border-color: var(--bg2)
}
.modal-content {
 background: var(--bg2);
}
.modal-footer {
	border-color: var(--bg5);
}



.menu_head, .login-section .sprite_bg, .product-free-shipping {
  background: transparent !important;
}
.subscribe {
  display: none !important;
}
  
  
  
  
  
/*  COLOUR F  */
.table_head strong, .blue_btn strong, .table > tbody > tr > td, .h2, h2, .product-price, .cat_des h1, .wish_list_dropdown > li > a strong, .f-20, .chkout_btn button strong, .wishlist_privacy, .add-to-cart-btn-bg strong, .f-14, .shipping-estimates td, .green_btn_b strong, .green_btn_b:focus strong, .checkbox label, .radio label, .my_order_box .panel-heading strong, .form-horizontal .control-label, .faq-groups label, #contact-us .blurb, .brand-name a, .green_btn strong, #sli_autocomplete.rich .sli_ac_title, .glyphicon, .sli_ac_sugg, .default_product_name, .system_build_cat_name, #contact-us .contact-form-details .write-to-us label {
	color: var(--colourF) !important;
}
  
  
/*  COLOUR D  */
.f-12, .table-border span, .breadcrumb > li, .filter-heading, .prdct_grid_des p, .cat_des, .prd_des, .m-b-40, .form-control, .add_section span, .f-30, .m-b-20, .m-b-30, .d_table > ul > li, #sli_autocomplete.rich .sli_ac_excerpt, .t-small, .pageactive, .system-manager h5, .short_text, .faq-response {
	color: var(--colourD) !important;
}


#sli_autocomplete.rich {
	background: var(--bg5);
	border-color: var(--bg5);
	box-shadow: 0 5px 10px rgba(0,0,0,1);
}
#sli_autocomplete.rich .sli_ac_odd {
	border-color: var(--b6);
	background: var(--bg6);
}
#sli_autocomplete.rich .sli_ac_even {
	border-color: var(--b5);
	background: var(--bg5);
}
.sli_ac_active {
	border-color: var(--bg7) !important;
	background: var(--bg7) !important;
}
.sli_ac_active > .sli_ac_title {
	background: transparent !important;
}
#sli_pagination_footer {
	background: var(--bg1);
}
.per-page-bg, #sli_loadingDiv {
	background: var(--bg2);
}
#sli_facet_container, #sli_facets, #sli_menu_button.selected, #sli_facet_button.selected, #sli_menu_button, #sli_facet_button {
	background: var(--bg3);
	border-color: var(--bg4);
	color: var(--colourD) !important;
}
.sli_facet_heading {
	background: transparent;
}
ul.sli_facets {
	border-top: 1px solid var(--bg4);
	border-bottom: 1px solid var(--bg4);
}
#ship img, .ship_img img {
	   filter: invert(93%) hue-rotate(180deg);
}
.add-to-cart-btn-bg, .blue_btn {
	box-shadow: none;
	border-radius: 0;
	border: none;
}
.add-to-cart-btn-bg strong, .blue_btn strong {
	text-shadow: none;
}
#___ratingbadge_0 {
	opacity: 0.01;
	transition: 0.2s ease;
}
#___ratingbadge_0:hover {
	opacity: 1;
}
.faq-group, #contact-us .contact-form-details .write-to-us {
	border-color: var(--bg4);
}
#contact-us .contact-form-details .write-to-us input, #contact-us .contact-form-details .write-to-us textarea {
	background: var(--bg6);
	border-color: var(--bg6) !important;
	box-shadow: none;
	color: #fff;
}

































}
