css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("app.kanjialive.com") {
  
  body {
    background-color: #282828;
    color: #aaa;
  }
  
  img {
    filter: invert(86.5%);
  }
  
  .container {
    background-color: #222;
  }
  
  /**** Main Page ****/
  
  .ka-welcome-text {
    color: #aaa;
  }
  
  code {
    background-color: #282828;
  }
  
  /* Top-right image */
  img[src="https://media.kanjialive.com/img/fan_logo_104.jpg"] {
/*     opacity: 0.2; */
    display: none;
  }
  
  /* Search box */
  .search-bar {
    background-color: #282828;
    color: #aaa;
  }
  
  .search-form {
    background-color: #1C1C1C;
  }
  
  /* Separators */
  .border-inner {
    background-color: #333;
  }
  
  /*** 'Info' button -> Pop-up Pane ***/
  
  .modal-content {
    background-color: #222;
  }
  
  /* Header */
  .modal-header {
    border-bottom-color: #333;
  }
  
  .close, .close:focus, .close:hover {
    color: #aaa;
  }
  
  /* Tables */
  .table-bordered {
    border-color: #333;
  }
  
  .table-bordered > tbody > tr > td, 
  .table-bordered > tbody > tr > th, 
  .table-bordered > tfoot > tr > td, 
  .table-bordered > tfoot > tr > th, 
  .table-bordered > thead > tr > td, 
  .table-bordered > thead > tr > th {
    border-color: #666;
  }
  
  /* Code */
  kbd {
    background-color: #282828;
    color: #d82d59; /* from default #c7254e; */
  }
  
  /* Footer */
  .modal-footer {
    border-top-color: #333;
  }
  
  /**** Kanji Search Results ****/
  
  /* List options */
  a.ka, a.ka:visited {
    color: #aaa;
  }
  
  /**** Individual Kanji Page e.g. app.kanjialive.com/è¦ª ****/
  
  /* Stroke Video Player */
  
  .icon-step-left, .icon-play, .icon-pause, .icon-step-right, .icon-pause-down, .icon-step-right-down, .icon-step-left-down, .icon-play-down {
    color: #666;
  }
  
  a.gray-out:hover {
    color: #aaa !important;
  }
  
  video {
    filter: invert(86.5%);
    opacity: 0.4;
  }
  
  /* Stroke Progression Images */
  img.ka-breakdown {
    opacity: 0.4;
  }
  
  /* 'Radical' */
  
  img.radical-character {
    opacity: 0.5;
  }
  
  .ka-rad-anim img.opaque {
    opacity: 0.5;
  }
  
  /* 'Luminous' */
  .luminous a {
    color: #aaa;
  }
  
}

@-moz-document domain("kanjialive.com") {
  
  body {
    background-color: #282828;
    color: #aaa;
  }
  
  button, input, select, textarea {
    border-color: #333;
  }
  
  .site {
    background-color: #222;
  }
  
  /* Header */
  .site-header h1 a, .site-header h2 {
    color: #666;
  }
  
  /* Buttons */
  .menu-toggle, input[type="submit"], input[type="button"], input[type="reset"], article.post-password-required input[type="submit"], .bypostauthor cite span {
    background-color: #282828;
  }
  
  /** Top Menu **/
  .main-navigation ul.nav-menu, .main-navigation div.nav-menu > ul {
    border-top-color: #666;
    border-bottom-color: #666;
  }
  
  /* Header Menu Dropdowns */
  .main-navigation li a {
    color: #aaa;
  }
  
  .main-navigation li a:hover, .main-navigation li a:focus {
    color: #ccc;
  }
  
  .main-navigation li ul li a {
    background-color: #282828;
    border-bottom-color: #333;
  }
  
  .main-navigation li ul li a:hover, .main-navigation li ul li a:focus {
    background-color: #282828;
    color: #ccc;
  }
  
  /* Images */
  .entry-content img, .comment-content img, .widget img, img.header-image, .author-avatar img, img.wp-post-image {
    filter: invert(86.5%);
  }
  
  /* Enlarged Image on click */
  
  #outerImageContainer {
    background-color: #222;
  }
  
  #imageContainer {
    filter: invert(86.5%);
  }
  
  #imageDataContainer {
    background-color: #222;
    color: #aaa;
  }
  
  #jqlb_closelabel {
    filter: invert(86.5%);
  }
  
  /* Code */
  code {
    background-color: #282828;
    border-color: #333;
  }
  
  /* Tables e.g. 'Values for Textbooks' */
  .tablepress thead th, .tablepress tfoot th {
    background-color: #282828;
  }
  
  .tablepress thead th {
    border-bottom-color: #666;
  }
  
  .tablepress tbody td, .tablepress tfoot th {
    border-top-color: #333;
  }
  
  .tablepress .row-hover tr:hover td {
    background-color: #333;
  }
  
  .entry-content table, .comment-content table {
    color: #aaa;
    border-bottom-color: #666;
  }
  
  /* Comments */
  .site-content article {
    border-bottom-color: #333;
  }
  
  /* Footer */
  footer[role="contentinfo"] {
    border-top-color: #666;
  }
  
}
