css:
@-moz-document domain(hooktube.com) {
/* HookTube 98
 * Description: Windows 98 Hooktube theme
 * By: Glyphs (github.com/glyphs)
 * Version: 0.1
 */

body {
    background-color: #007f7f;
    color: #010101;
}
a, a:hover {
	color: blue;   
}

h4 {
	font-size: 1em;
    padding: 0;
}
.alert {
	font-size: .75em;
}
#search-form input.form-control {
	padding: .2em .3em;
    color: #010101;
    border-radius: 0px;
}
#search-form button.btn {
    padding: .2em .3em;  
    border-radius: 0px;
}
.alert-warning {
	background-color: rgba(180,132,132,.1);
    border-color: rgba(255,238,186,.1);
    color: rgba(255,238,186,.8);
}
.alert-error {
    background-color: rgba(245,57,57,.1);
    border-color: rgba(245,57,57,.3);
    color: rgba(255,179,179,.6);
}
#category-list {
    padding: .5em;
    margin: 0px;
    background-color: #c0c0c0;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa;
}

#generic-title {
    color:010101;
    padding: .5em;
    margin-top: 10px;
    background-color: #c0c0c0;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa
}

.row:nth-of-type(2){padding:0px;}

.col-md-8 {
    padding: .5em;
    margin: 0px;
    background-color: #c0c0c0;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa;

}

.col-sm-12.col-md-4.col-lg-4 {margin: 0px;}

#video-list {
    padding: .5em;
    margin: 0px;
    background-color: #c0c0c0;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa;
}
button {
	background: #555;
    border: 1px solid #111!important;
    font-size: .9em!important;
    box-shadow: 1px 1px 1px #111;
    color: #dadada;
    text-shadow: 2px 2px 2px #111;
}
.btn-secondary {
	background-color: rgba(0,0,0,.8);
    border: none!important;
}
.btn-secondary:hover, .show>.btn-secondary.dropdown-toggle {
    background-color: rgba(0,0,0,1);
    box-shadow: none;
}
h1.card-title {
	font-size: 1.3em;   
}
.list-channel-title {
    color: #dadada;
}

.modal-content {
    background-color: #c0c0c0!important;
    padding: .5em;
    margin: 0px;
    color: #010101;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa;
    border-radius: 0px;
}

.row {
    padding: .5em;
    margin: 0px;
    color: #010101;
}

/*homepage intro*/
.jumbotron {
	background-color: #c0c0c0!important;
    padding: .5em;
    margin: 0px;
    color: #010101;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa;
    border-radius: 0px;
}
.jumbotron p {
    font-size: .9em!important;
}

/*homepage categories*/
#category-list {
	text-align: left!important;
    line-height: 1em;
}

/*video page*/
p#video-channel-name {
	font-size: .75em;
}
#video-description .card-body {
    background-color: #c0c0c0;
    font-size: .75em;
    padding: .5em;
}
#video-description .card {
 	border: none;   
}
#video-keywords .badge {
    background-color: rgba(0,0,0,.5);
}
#comments .row {
    border-bottom: 1px solid #222;
    padding: .5em 0;
}

/*catalog list*/
#video-list img {
	border: 1px solid #000;   
}

.video-description {background-color: #c0c0c0;}

/*popups*/
.modal-content {
	background-color: rgba(39,39,39,1);   
    border-color: rgba(0,0,0,1);
}
.modal-content .modal-header {  
	border-bottom-color: rgba(0,0,0,.3);
}

/*search bar*/
input[type="text"] {
    background: #c0c0c0!important;
    color: #010101!important;
    font-size: .8em;
    border-right: 1px solid #010101;
    border-bottom: 1px solid #010101;
    border-top: 1px solid #fcfcfa;
    border-left: 1px solid #fcfcfa;
}
input[type="text"]::placeholder {
    color: #010101!important;
}
#search-form button.btn {
	background: #c0c0c0;
    border-right: 1px solid #010101;
    border-bottom: 1px solid #010101;
    border-top: 1px solid #fcfcfa;
    border-left: 1px solid #fcfcfa;
    font-size: .8em;
}

/*footer*/
#main-footer {
    color: #010101;
	border-top: 0;
    background: #c0c0c0;
    border-right: 4px solid #010101;
    border-bottom: 4px solid #010101;
    border-top: 4px solid #fcfcfa;
    border-left: 4px solid #fcfcfa;
    margin-bottom: 1em;
    padding: .5em;
    margin-top: 10px;
}
#main-footer p {
	margin-bottom: .25em;   
}
#main-footer a{color: blue;}

/*navbar*/
nav.navbar {
	background-color: rgb(192,192,192);
    border-bottom: 1px solid #010101;
}
nav.navbar a.navbar-brand img {
	width: 75%;
}
nav.navbar ul li a.nav-link {
    padding-left: 10px;
	color: black !important;
    font-size: 12px;
    padding-top: .55em;
    border-right: 1px solid #010101;
    border-bottom: 1px solid #010101;
    border-top: 1px solid #fcfcfa;
    border-left: 1px solid #fcfcfa;
}

nav.navbar ul li a.nav-link:hover {
	color: black !important;
    font-size: 12px;
    background: #fcfcfa;
    padding-top: .55em;
    border-top: 1px solid #010101;
    border-left: 1px solid #010101;
    border-bottom: 1px solid #fcfcfa;
    border-right: 1px solid #fcfcfa;
}

nav.navbar a.navbar-brand {
    width: 130px;
    height: 32px;
    margin: 0;
    padding: 0;
}
}
