    resize: none;
    display: block;
    padding: 10px;
    font-size: 12px;
    margin: 0px auto 10px auto;
}
  .messaging-conversation-author-pseudo {
    font-weight: bold;
    margin-top: 3px;
    margin-bottom: 1px;
    font-size: 115%;
