    padding: 6px;
    word-wrap: break-word;
}
.pun .author_bottom {
    background-color: rgba(14, 14, 14, 1);
    float: left;
    height: 41px;
    margin-top: -1px;
    padding-top: 1px;
    width: 170px;
}
body {
    vertical-align: baseline;
    font-family: "trebuchet ms", arial, serif;
    font-size: 14px;
    line-height: 1em;
    color: rgb(222, 104, 0);
    min-height: 100%;
}
form#post {
    background-color: rgba(0, 0, 0, .75);
}
