css:
@-moz-document domain(google.com) {
  html {
      overflow-x: hidden;
  }
  .sfbgg {
      background: linear-gradient(-45deg, #EE7752, #E73C7E, #23A6D5, #23D5AB);
      background-size: 400% 400%;
      animation: Gradient 15s ease infinite;
  }
  
  body.hp {
      background-image: url("https://picsum.photos/1920/1080?random");
      background-size: cover;
      background-position: center!important;
      background-repeat: no-repeat;
  }

  #gs_st0, #gbqfbb, #footer, #hplogo, .sbsb_b, ._XIi, #hplogo > a img, ._bGb, .szppmdbYutt__middle-slot-promo span {
      display: none;
  }

  #gb #hptl a, #gb .gb_P  {
      font-weight: bold;
      text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff;

  }
  #gb #hptl a:hover, #gb .gb_P:hover{
      animation: invtxt 600s infinite;
      text-shadow: -1px -1px 0 #000, 1px -1px 0 #000, -1px 1px 0 #000, 1px 1px 0 #000;
  }

  #lga {
      background: url("https://cdn.betakit.com/wp-content/uploads/2013/12/doodle.png");
      background-size: 400px;
      background-position: center;
      background-repeat: no-repeat;
  }

  .tsf-p * {
      background: none;
  }
  div.sfbgg {
      height: 67.5px !important;
  }
  #sb_ifc0{
      height: 42px;
      border-right: 1px solid rgba(0,0,0,.5) !important;
  }

  #sb_ifc0, .sbico-c{
      box-shadow: none;
      background: rgba(255,255,255,0.5);
      color: rgba(0,0,0,.75);
      border: none;
      border-radius: 2.5px;
  }
  #sb_ifc0:hover, .sbico-c:hover{
      background: rgba(255,255,255,0.75);
      box-shadow: 2.5px 2.5px 2.5px rgba(0,0,0,.5);
      border: none;
  }
  #lst-ib {
      font-family: Arial;
      letter-spacing: .25px;
  }
  #lst-ib:focus {
      outline: none;
  }

  .jsb input {
      animation: a 600s infinite;
      box-shadow: 4px 4px 4px rgba(0,0,0,.5);
  }
  .jsb input:hover {
      animation: b 600s infinite;
  }

  @keyframes a {
      0%, 100% {
          background: rgba(255,255,255,0.5);
          color: rgba(0,0,0,.75);
          border: none;
      }
  }
  @keyframes b {
      0%, 100% {
          background: rgba(255,255,255,0.75);
          box-shadow: 2.5px 2.5px 2.5px rgba(0,0,0,.5);
          border: none;
      }
  }
  @keyframes Gradient {
      0% {
          background-position: 0% 50%
      }
      50% {
          background-position: 100% 50%
      }
      100% {
          background-position: 0% 50%
      }
  }
  .jsb input {
      animation: a 600s infinite;
      box-shadow: 4px 4px 4px rgba(0,0,0,.5);
  }
  .jsb input:hover {
      animation: b 600s infinite;
  }



  @keyframes invtxt{
      0%{color: #fff;}
      100%{color: #fff;}
  }

  @keyframes a {
      0%, 100% {
          background: rgba(255,255,255,0.5);
          color: rgba(0,0,0,.75);
          border: none;
      }
  }
  @keyframes b {
      0%, 100% {
          background: rgba(255,255,255,0.75);
          box-shadow: 2.5px 2.5px 2.5px rgba(0,0,0,.5);
          border: none;
      }
  }
  @keyframes Gradient {
      0% {
          background-position: 0% 50%
      }
      50% {
          background-position: 100% 50%
      }
      100% {
          background-position: 0% 50%
      }
  }
}
