css:
@-moz-document regexp("^https?:\/\/issues\.unrealengine\.com\/.*") {


/*===========================  Issues start  ================================*/ 
    
    body {
        background-color: #333;
        color: #ccc;
    }
    
    hr {
        border-color: #666;
    }
    
    
    
    .navbar-default {
        background-color: #393939;
    }
    
    .navbar-default .navbar-brand {
        color: #ccc;
    }
    
    .navbar-default .navbar-nav > li > a {
        color: #ccc;
    }
    
    .form-control {
        background-color: transparent;
        border-color: #666;
        color: #ccc;
    }
    
    
    
    .page-header {
        border-color: #666;
        margin-top: 0px;
    }
    
    .container::before,
    .container::after {
        content: none;
    }
    
    .page-header h1.hidden-xs img {
        filter: brightness(80%) invert(100%);
    }
    
    
    
    .bs-callout {
        border-color: #454545 #454545 #454545 #5bc0de;
    }
    
    
    .btn {
        box-shadow: 1px 3px 5px 0 #0003;
    }   
    
    .btn:hover {
        box-shadow: 2px 4px 8px 0 #0006;
    }
    
    .btn-default,
    .btn-default:focus {
        background-image: none;
    }
    
    .btn-default {
        background-color: #666;
        color: #fff;
        border-color: #666;
    }
    
    .btn-default:hover {
        background-color: #1bf;
        color: #fff;
        border-color: #1bf;
    }
    
    .btn-info {
        background-color: #1bf6;
        border-color: #1bf;
    }
    
    .btn-info:hover {
        background-color: #1bf;
        border-color: #1bf;
    }
    
    
    
    .panel {
        background-color: #393939;    
    }
    
    .panel-default {
        border-color: #454545;
    }
    
    .panel-danger {
        border-color: #633;
    }
    
    .panel-success {
        border-color: #350;
    }
    
    .panel-default > .panel-heading {
        color: #fff;
        background-color: #454545;
        border-color: #454545;
    }
    
    .panel-danger > .panel-heading {
        color: #fbb;
        background-color: #633;
        border-color: #633;
    }
    
    .panel-success > .panel-heading {
        color: #9f9;
        background-color: #350;
        border-color: #350;
    }
    
    
    
    
    .list-group-item {
        background-color: #393939;
        border-color: #454545;
    }
    
    a.list-group-item,
    button.list-group-item {
        color: #808080;
    }
    
    a.list-group-item .list-group-item-heading,
    button.list-group-item .list-group-item-heading {
        color: #ccc;
    }
    
    a.list-group-item .list-group-item-heading,
    button.list-group-item .list-group-item-heading {
        word-wrap: break-word;
    }
    
    a.list-group-item:hover,
    a.list-group-item:focus,
    button.list-group-item:hover,
    button.list-group-item:focus {
        color: #808080;
        background-color: #454545;
    }
    
    
    
    .result-stats-fixed {
        background-color: #682;
    }
    
    .result-stats-unresolved {
        background-color: #722;
    }
    .result-stats-duplicate {
        background-color: #666;
    }
    
    .result-stats-wont_do {
        background-color: #222;
    }
    
    
    .panel > .table th {
        background-color: #454545 !important;
    }
    
    .table > thead > tr > th,
    .table > thead > tr > td,
    .table > tbody > tr > th,
    .table > tbody > tr > td,
    .table > tfoot > tr > th,
    table > tfoot > tr > td {
        border-color: #454545;
    }    
    
    .result-stats-by_design {
         background-color: #690;     
    }
    
    
    
    pre {
        color: #c3aa88;
        background-color: #333;
        border-color: #454545;
        white-space: pre-wrap;
    }
    
    .panel-default .panel-body {
        max-height: inherit !important;
    }
    
    
    
    footer {
        background-color: #393939;
    }
    

    
/*============================  Issues end  =================================*/ 
    
}
