css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.nintendo.fr") {
  
  /* Arriere plan corps */
  body {
    background-color: #2d2d2d!important;
    background-image: none;
  }

  /* Champ de recherche */
  .search-form-top .form-control {
    background-color: #2d2d2d;
  }
  /* Texte champ de recherche */
  .form-control {
    color: #d5d5d5;
  }

  /* Bloc Dernières News */
  .panel {
    background-color: #4a4a4a;
  }
  /* Texte news */
  .news-list-info p {
    color: #c5c5c5;
  }
  /* Titre news */
  div.latest-news-margin p.news-list-title {
    color: #d5d5d5;
  }
  /* Date et plateforme */
  .news-list-info p.date {
    color: #c5c5c5;
  }
  
  /* Menu déroulant */
  .dropdown-menu {
    background-color: #4a4a4a;
  }
  #sidebar-wrapper.newSideWrapper .sidebar-nav li.open .container-sub-nav {
    background-color: #4a4a4a;
  }
  /* Liens */
  #sidebar-wrapper.newSideWrapper .sidebar-nav ul.dropdown-menu li a {
    color: #d5d5d5;
  }
  /* Hover sur bloc des liens */
  #sidebar-wrapper ul.sidebar-nav li:hover ul.dropdown-menu li a {
    color: #d5d5d5;
  }
  /* Hover sur titre catégorie */
  #sidebar-wrapper.newSideWrapper .container-sub-nav ul.dropdown-menu li:first-child:hover a, #sidebar-wrapper.newSideWrapper ul.sidebar-nav li ul.dropdown-menu li:first-child:hover a, #sidebar-wrapper.newSideWrapper .container-sub-nav ul.dropdown-menu li.nav-main-title:hover a, #sidebar-wrapper.newSideWrapper ul.sidebar-nav li ul.dropdown-menu li.nav-main-title a.nav-selected, #sidebar-wrapper.newSideWrapper #mainNav_wiiu ul.dropdown-menu li.nav-main-title:hover a, #sidebar-wrapper.newSideWrapper #mainNav_nintendo3ds ul.dropdown-menu li.nav-main-title:hover a, #sidebar-wrapper.newSideWrapper #mainNav_amiibo ul.dropdown-menu li.nav-main-title:hover a, #sidebar-wrapper.newSideWrapper #mainNav_store ul.dropdown-menu li.nav-main-title:hover a, #sidebar-wrapper.newSideWrapper #mainNav_games ul.dropdown-menu li.nav-main-title:hover a, #sidebar-wrapper.newSideWrapper #mainNav_support ul.dropdown-menu li.nav-main-title:hover a {
    color: #df0011 !important;
  }
  /* Bouton "..." du menu gauche > Titre "Divers" */
  .container-sub-nav ul.dropdown-menu .nav-main-title {
    color: #d5d5d5;
  }
  
  /* PAGE NEWS */
  /* Arriere plan header */
  .pageheader {
    background-color: #2d2d2d;
  }
  /* Texte header */
  .pageheader h1 {
    color: #d5d5d5;
  }
  /* Arriere plan news */
  .featured-content {
    background-color: #4a4a4a;
  }
  /* Titre news */
  .overview-news-list .overview-news-element p.news-list-title {
    color: #d5d5d5;
  }
  /*Bloc Derniere news */
  .overview-news-list .overview-news-elements {
    background-color: #4a4a4a;
  }
  /* Arriere plan "Plus de news" */
  .latest-news-more-news, .latest-news-more-news-link {
    background-color: #4a4a4a;
  }
  
  /* PAGE SOCIAL */
  /* Arriere plan */
  .modal-content {
    background-color: #4a4a4a;
  }
  /* Texte */
  .overlay_headline {
    color: #d5d5d5;
  }
  /* Noms réseaux sociaux */
  body {
    color: #d5d5d5;
  }
  
  
  /* PAGE RECHERCHE */
  /* Header */
  .navbar-default.navbar-static-top {
    background-color: #2d2d2d;
  }
  /* Menu */
  nav.navbar.navbar-static-top .cont-main-nav {
    background-color: #2d2d2d;
  }
  /* Texte menu */
  .navbar-static-top .navbar-nav > li > a {
    color: #d5d5d5;
  }
  /* Champ de recherche */
  #search .form-control {
    background-color: #4a4a4a;
  }
  /* Texte "Resultats de la recherche" */
  .ng-binding {
    color: #d5d5d5;
  }
  /* Elements trouvés */
  .list-group-item {
    background-color: #4a4a4a;
    border: 1px solid #000;
  }
  /* Footer */
  footer {
    background-color: #4a4a4a;
  }
  
  
  /* PAGE JEU ACHAT */
  /* Bloc prix */
  .price-box-item {
    background-color: #4a4a4a;
  }
  /* Arriere plan */
  [data-price-box="wrapper"] > .game_info_container {
    background-color: #2d2d2d!important;
  }
  
  /* Prix */
  [data-price-box="price"] [data-price-box-price="current"] span:first-of-type {
    color: #d5d5d5;
  }
  /* Texte description 1 */
  [data-price-box="price"] ul li > span {
    color: #d5d5d5;
  }
  /* Texte description 2 */
  [data-price-box="benefits"] ul li > span {
    color: #d5d5d5;
  }
  /* Liens */
  .link-group-item {
    color: #d5d5d5;
  }
  /* Texte Informations */
  [data-price-box="info"] {
    color: #d5d5d5;
  }
  /* Corps Arriere plan */
  .rd8 .tab-pane {
    background-color: #4a4a4a;
  }
  /* Corps Texte */
  .rd8 .tab-pane {
    color: #d5d5d5;
  }
  /* Corps Titres */
  h2, .content h2, .content h3 {
    color: #d5d5d5;
  }
  /* Bloc "Ce que vous devez savoir" */
  .well {
    background-color: #2d2d2d;
  }
  /* Bloc caractéristiques : Version téléchargeable et Carte de jeu */
  .rd8 .game_info_text {
    color: black;
  }
  /* Fonctionnement */
  .sidebox .row.row-content {
    background: #4a4a4a;
  }
  
  /* NOUVEAU ----------------> REVOIR LA PARTIE "PAGE RECHERCHE" */
  /* RECHERCHE */
  .modal-fullscreen {
    background: rgba(62, 62, 62, 0.9);
  }
  .srch-global-input {
    color: #d5d5d5;
  }
  /* Champ de recherche */
  .queryBox input {
    background: #3e3e3e;
  }
  /* Onglets "Rubrique, Jeux..." */
  .group-first-level .btn {
    background-color: #333;
    color: #d5d5d5;
  }
  /* Onglet actif */
  .group-first-level .btn.active {
    color: #e60012;
  }
  /* Titres des éléments trouvés */
  .page-list-group-item p.page-title {
    color: #d5d5d5;
  }
  /* Descriptions des éléments trouvés */
  .page-list-group-item p {
    color: #c5c5c5;
  }
  /* Pris si c'est un jeu */
  .page-list-group-item p.page-data-purchase {
    color: #d5d5d5;
  }
  /* Titres catégories */
  .result-group .result-group-headline {
    color: #d5d5d5;
  }
  
  
  /* PAGE AMIIBOS */
  /* Vue d'ensemble */
  /* Présentation */
  .rd8 .row.content-container {
    background-color: #4a4a4a;
    color: #d5d5d5;
  }
  
}
