css:
@-moz-document url("https://typing-tube.net/"), url-prefix("https://typing-tube.net/dustbox"), url-prefix("https://typing-tube.net/search"), url-prefix("https://typing-tube.net/?loca"), url-prefix("https://typing-tube.net/comments"), url-prefix("https://typing-tube.net/playlist"), url-prefix("https://typing-tube.net/?c=-30&loca"), url-prefix("https://typing-tube.net/requests"), url-prefix("https://typing-tube.net/?") {
body {
    background: #212121;
    overflow-x: hidden;
}
#right_ads,
#center_ads,
#bottom_ads,
.adsbygoogle,
footer ul,
.createdate img[src*="bn02.gif"],
.contents-top{
    display: none !important;
}
.comments a{
        color: #E2E2D6;

}
.createdate p{
    color:#d3d3d3;
}
.sideber section {
    margin: 0 0 0 0;
}
.request .searchbox {
    background:#101010;
    border: solid 0.1vw #000000;
        color:#d0d0ca;
}
.sideber .createdate .searchbox {
    padding: 0.5vw;
    font-size: 1.3em;
    font-family: Arial, sans-serif;
    color: #d0d0ca;
    border: solid 0.1vw #000000;
    margin: 0;
    width: 20vw;
    background:#101010;
}
.search .searchbox {
    background:#101010;
    color:#d0d0ca;
    border: solid 0.1vw #000000;
}
li,h1,a
{
    color: #E2E2D6;
}
.title a,
.more a
{
    color: #E2E2D6!important;
}
.logo {
    float: left;
    margin: 0.8vw 0 0 0;
    filter: invert(90%);
}
::-webkit-scrollbar-corner{
    background: #414141;
}
::-webkit-scrollbar {
    width: 10px;
    background: #212121;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
}

@-moz-document url-prefix("https://typing-tube.net/movie/show/"), url-prefix("https://typing-tube.net/movie/add_comment") {
header:after, ul:after, ol:after, nav:after, .wrapper:after, .taglist:after, aside:after, footer:after {
display:none;
}
.moviepage br,
.movietitle,
.wrapper >.report p,
.wrapper >.report br,
.movietitle h1,
.pull-left,
header,
#player_ad,
#movie_bottom_ads,
#playBotton1,
.google-auto-placed,
footer ul{
    display: none !important;
}
#playBotton3 a{
    color:#C977A5!important;
}
.newslist li a{
    color:#E2E2D6;
}
#movie-tags {
    background:#C2C2C2;
    width: 100%;
    position: static;
}
#movie-tags li {
    font-weight: bold;
    color: #FF5917;
}
.report{
 color:#E2E2D6;   
}
body>.wrapper>.taglist {
    
    margin-bottom: 0px;
    position: absolute;
    bottom: -1900px;
}
body>.wrapper>.taglist {
    background: #9E9E9E;
    font-weight:bold;
}
body>.wrapper>.taglist a span{
color:#01579B!important;
}
.movietitle{
margin-top: 2px;
}
body {
    background: #212121!important;
}
.share {
    padding: 0;
    order: 1;
}
.wrapper{
    display: flex;           /* flexコンテナ化 */
   flex-direction: column;
}
.pull-right {
 color:#b3b3b3;
    position:absolute;
}
#youtube-movie {
    padding: 0;
}
#controlbox {
    background:#101010;
    padding: 0;
}
#status {
    background:#101010;
    color:#b9b9b9;
    font-size: 24.9px;
    margin-top: 3px;
}
#bar_input_base,
#bar_base{
    background:#2d2d2d;
}
#kashi_roma span{
 color:#FFB74D !important;  
}
#kashi_roma{
    font-size: 20px;
    margin-bottom:9px;
}
#bar_input1,
#bar_input0{
 background:#7CB342 !important;  
}
#bar{
    background:#CE93D8 !important;
}
a.submit_score_button {
	background:#101010;
    border-color:#2d2d2d;
    color:#b3b3b3;
}
.playarea,
#lyricsarea{
    background: #101010;
    color:#d2d2d9!important;
}
.report h1 {
    background-color: #212121;
    margin: 0 0 1.6vw 0;
    -webkit-border-radius: 0.5vw;
    -moz-border-radius: 0.5vw;
    border-radius: 0.5vw;
}
#comment{
color:#E2E2D6!important;
}
h1 a {
    color: #71A791;
    font-weight: bold;
}
.wrapper .report li {
    color: #dfdfda;
}
button, input, optgroup, select, textarea {
    margin: 0;
    color: #dfdfda;
    background: #101010;
    border:  groove #444444;
    
}
.ui-widget-content {
    border: 1px solid #dddddd;
    background: #c2c2c2;
    color: #444444;
}
p {
    color:#E2E2D6;
}
.report h1 {

    padding: 0vw;

}
.report h4 a{
    color: #E2E2D6;
    font-weight: bold;
}
#status::-webkit-scrollbar {
    width: 10px;
    background: #212121;
    border: solid 0.1px#262626;
}
#status::-webkit-scrollbar-thumb {
    background: #414141;
}
#status::-webkit-scrollbar-corner{
    background: #414141;
}
::-webkit-scrollbar-corner{
    background: #414141;
}
::-webkit-scrollbar {
    width: 10px;
    background: #212121;
}
::-webkit-scrollbar-thumb {
    background: #555;
}
}

@-moz-document url-prefix("https://typing-tube.net/movie/edit") {
.google-auto-placed{
    display: none !important;
}
}

@-moz-document regexp("https://typing-tube.net/movie/show/.*?mode=lyrics") {
.taglist{
    display: none !important;
}

}

@-moz-document url("https://typing-tube.net/comments") {
.main ul li a{
    color:#E2E2D6!important;
}
}
