css:
@-moz-document domain("www.omega-cheats.com") {
body {
    background: #414141;
}

.menu ul {
    padding: 0;
    margin: 0;
    background: #c93c3c;
    box-shadow: 0px 2px 10px -1px #c93c3c;
}

td, th {
    display: table-cell;
    vertical-align: inherit;
}
.menu li a:link, .menu li a:visited, .menu li a:hover {
    -webkit-transition: none;
    -moz-transition: none;
    -ms-transition: none;
    -o-transition: none;
    transition: none;
    color: #fff !important;
    text-shadow: none !important;
}
a:link {
    color: #e65050 !important;
    text-decoration: none;
}
.smalltext {
    font-family: 'Droid Sans',sans-serif;
    font-size: 13px;
    color: #fff !important;
}
#content {
    background: #363636;
    width: auto !important;
    color: #333;
    padding: 10px 10px;
    overflow: hidden;
    border: 1px solid #c93c3c;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    -webkit-box-shadow: 0px 2px 5px -1px #adaeaf;
    -moz-box-shadow: 0px 2px 5px -1px #adaeaf;
    box-shadow: 0px 2px 10px -1px #c93c3c;
    font-family: 'Droid Sans',sans-serif;
    font-size: 13px;
}

.navigation {
    background: #c93c3c;
        background-clip: border-box;
    color: #fff;
    text-shadow: 1px 1px 0px #882929;
    -webkit-box-shadow: rgba(0,0,0,0.3) 0px 1px 4px,inset RGB(32,32,32) 0px 1px 0px;
    -moz-box-shadow: rgba(0,0,0,0.3) 0px 1px 4px,inset RGB(32,32,32) 0px 1px 0px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    height: 30px;
    line-height: 30px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    padding: 0px 10px;
    font-family: 'Droid Sans',sans-serif;
    font-size: 12px;
    font-style: normal;
    box-shadow: 0px 2px 10px -1px #c93c3c;
}

.tborder {
    background: #414141;
    width: 100%;
    margin: auto auto;
    border: 1px solid #e63c3c;
    padding: 5px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    font-family: 'Droid Sans',sans-serif;
    box-shadow: 0px 2px 10px -1px #c93c3c;
}

.trow1 {
    background: #232323;
    color: #fff !important;
    padding: 10px;
    font-family: 'Droid Sans',sans-serif;
}
.trow2 {
    background: #232323;
    color: #fff !important;
    padding: 10px;
    font-family: 'Droid Sans',sans-serif;
}

.footermenu {
    background: #232323;
    border: 1px solid #e63c3c;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    font-family: 'Droid Sans',sans-serif;
    font-size: 13px;
}
a.button6:link, a.button6:visited, a.button6:active {
    color: #6c6c6c;
    display: inline-block;
    padding: 2px 2px;
    margin: 2px;
    text-shadow: #6c6c6c 0px 0px 0px;
    font-family: 'Droid Sans',sans-serif;
    font-size: 13px;
    font-style: normal;
}

.tcat {
    background: #363636;
    color: #999;
    border-bottom: 1px #e7e6e6;
    padding: 10px;
    padding: 6px;
    font-family: 'Droid Sans',sans-serif;
    font-size: 12px;
}

.thread_status.hotlockfolder:before {
    color: #ffffff;
    position: absolute;
    background-position: 0 -200px;
    content: "\f004";
    text-shadow: 0px 0px 0px #fff;
    font-family: FontAwesome;
    font-size: 16px;
}

.thread_status.lockfolder:before {
    color: #ffffff;
    position: absolute;
    background-position: 0 -220px;
    content: "\f023";
    text-shadow: 0px 0px 0px #fff;
    font-family: FontAwesome;
    font-size: 16px;
}

select {
    background: #414141;
    color: #ffffff;
    padding: 3px;
    border: 1px solid #c93c3c;
    outline: 0;
    font-family: 'Droid Sans',sans-serif;
    font-size: 13px;
}

.post .post_author {
    border-bottom: 1px solid #c93c3c;
    border-top: 1px solid #c93c3c;
    background: #333;
    padding: 5px;
    overflow: hidden;
}

.post .post_head {
    padding-bottom: 4px;
    border-bottom: 1px solid #c93c3c;
    margin-bottom: 4px;
    font-size: 12px;
}

.post .post_author div.author_avatar img {
    padding: 5px;
    border: 1px solid #c93c3c;
    background: #333;
}

.post .post_author div.author_statistics {
    color: #fff;
    float: right;
    padding: 3px 10px 3px 5px;
    line-height: 1.3;
    font-size: 13px;
}

.fa-folder-o:before {
    content: "\f114";
}

.post .post_head span.post_date {
    color: #fff;
    font-size: 12px;
}

.post_body {
    font-size: 14px;
    padding: 12px 0;
    color: #fff;
}

.reputation_neutral {
    color: #e0be00;
}

blockquote {
    background: #333;
    color: #fff;
    border: 1px solid #c93c3c;
    margin: 0;
    padding: 15px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    border-left: 4px solid #c93c3c;
}

fieldset {
    background: #333;
    color: #333;
    padding: 12px;
    border: 1px solid #c93c3c;
    text-shadow: 0px 0px #fff;
    -moz-box-shadow: 0 1px 2px 0 #c93c3c;
    -webkit-box-shadow: 0 1px 2px 0 #c93c3c;
    box-shadow: 0 1px 2px 0 #c93c3c;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    margin: 0;
    font-family: 'Droid Sans',sans-serif;
}

.tab-content2 {
    display: none;
    background: #333;
    padding: 15px;
    border: 1px solid #c93c3c;
}

ul.tabs li.current {
    background: #232323;
    color: #fff;
    border: 1px solid #c93c3c;
    border-bottom: none;
}

ul.tabs li {
    color: #fff;
    display: inline-block;
    padding: 10px 15px;
    cursor: pointer;
    background: #333;
    border: 1px solid #c93c3c;
    border-bottom: none;
}
}
