css:
@-moz-document domain(h1z1showcase.com){

/* PRE SITE */

html {
  background: #212121;
}

.inner {
  background-color: #212121;
  border-radius: 0px;
  box-shadow: none;
}

hr.star-light {
  display: none;
}

.img-responsive, .thumbnail>img, .thumbnail a>img, .carousel-inner>.item>img, .carousel-inner>.item>a>img {
  margin-bottom: 25px;
}

.row {
  margin-top: 100px;
}

/* BUTTONS */

.main_home_button {
  border: 4px solid #EC644A;
  border-radius: 0px;
  background-color: #EC644A;
}

.main_home_button:hover {
  background-color: #212121 !important;
}

.header_text_small {
  display: none;
}

h2 {
  margin-bottom: 20px;
}

/* H1Z1 MAIN SITE */

/* <!-- Navbar --> */

.navbar-default {
  background-color: #EC644A;
}

.navbar-brand {
  font-size: 15px;
}

.navbar .navbar-nav {
  margin-right: 22%;
}

.navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus {
  background-color: #212121;
}

.navbar>.container .navbar-brand, .navbar>.container-fluid .navbar-brand {
  margin-top: 2%;
}

.navbar-fixed-top .navbar-brand {
  font-size: 15px;
}

.dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus {
  background-color: #EC644A;
}

.dropdown-menu>li>a {
  color: #212121 !important;
}

.form-control {
  padding: 10px 27px;
  border: 1px solid #EC644A;
  border-radius: 0px;
}

/* <!-- Intro --> */

.row {
  margin-top: 21px;
  margin-bottom: 0px;
}

.intro-text {
  display: none;
}

/* <!-- Section --> */

a, a:hover, a:focus, a:active, a.active {
  text-decoration: none !important;
  color: #fff !important;
}

hr.star-primary {
  display: none;
}

section {
  background-color: #EC644A;
}

section h2 {
  margin-bottom: 50px;
  color: #fff;
}

.skin-item {
  background-color: #EC644A;
}

.thumbnail {
  background-color: #EC644A;
  border-radius: 0px;
}

.btn {
  border-radius: 0px;
}

.btn-default {
  border-color: #fff;
  background-color: #212121;
}

a.btn.btn-default {
  box-shadow: none !important;
}

div {
  background-color: transparent !important;
}

/* <!-- FOOTER --> */

footer .footer-below {
  background-color: #EC644A !important;
}

/* /// ICONS /// */

i.fa.fa-picture-o {
  display: none;
}

i.fa.fa-play-circle {
  display: none;
}

i.fa.fa-video-camera {
  display: none;
}

i.fa.fa-cubes {
  display: none;
}

i.fa.fa-caret-right {
  color: #fff;
}

i.fa.fa-circle {
  color: #fff;
}

}
